INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a32ec9ba-ef1c-11ed-a13e-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', '', '1', '培训与指导', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a32f65fa-ef1c-11ed-8e4f-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a32ec9ba-ef1c-11ed-a13e-8c554aa628fd', '2', '培训', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a32fb41d-ef1c-11ed-86b5-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a32f65fa-ef1c-11ed-8e4f-8c554aa628fd', '3', '对初级养老护理员进行基础知识培训', 1, 1, 1, '。\n培训目标：能为老年人正确使用热水袋保暖。\n\n步骤3 讲解重点难点(30~40分钟)\n讲解与讲述需要一定的讲课方法支撑：如启发性、引导性讲课方法。\n(1)简要知识回顾。老年人使用热水袋的基本知识有哪些？讲述要点：老年人生理机能变化，热水袋的作用，热水袋的类型，热水袋使用方法，热水袋使用要求。\n(2)讲解重点问题。老年人使用热水袋的方法和要求，如温度一般为50℃，位置为离开足底10厘米，时间一般为30分钟。\n(3)讲解观察方法。卧床或失能老年人使用热水袋应注意10~15分钟观察一次，查看热水袋有无漏水现象，有无直接接触老年人皮肤，老年人皮肤有无发红，如有异常立即报告。\n\n步骤4 组织课堂讨论(30分钟)\n提问式讨论；老年人使用热水袋为何容易烫伤？\n案例式讨论：一名89岁老年人，晚上睡觉时使用了热水袋，第二天早晨发现小腿部有水泡，请分析为什么会出现水泡？哪些环节是造成这名老年人烫伤的原因或相关因素呢？\n强化式讨论：热水袋检查，热水测量方法，如何灌装热水袋及热水量控制，如何排出热水袋中的空气，如何包裹并放置热水袋。\n\n步骤5 布置作业及小结(10分钟)\n布置作业的目的是为了促进学员巩固和消化课堂所学的知识，形成学员自己的知识和技能。散师在布置作业时，要符合教学目标，理论联系实际，启发学员思考，要从知识技能的角度加强考感，在布置作业时，应明确作业的类型和形式，布置下次课的预习要点，回顾自已讲课中的优点与问题。\n\n注意事项\n1.开场白应引起学生的关注。\n2.讲解应避免使用随意性的语言。\n3.课堂讨论应尽量避免超时。\n4.课堂讨论应尽量选择思考题形式及案例讨论形式。\n\n\n', '1 工作准备\n(1)任务评估\n\n初级养老护理员基础知识培训任务评估\n评估教学任务			确认培调目标\n确认培训内容			老年人生活照料中的热水袋应用\n确认培训对象			初级养老护理员\n培训目标定位			岗前规范化培训\n培训课时定位			2小时\n培训地点确认			第三教室\n培训形式定位			面授\n培训方式设计			PPT幻灯片演示十案例讨论\n\n(2)教师准备。准备好适宜的教材及教学大纲、教案、演示PPT幻灯片、讲义等资料\n(3)环境准备。敦室的投影设备、黑板等用物。\n(4)学生准备。培训教材或讲义。\n\n步骤2 导入典型案例(5~10分钟)\n开场问好：同学们好。\n导人题目：今天我们要学习如何为老年人使用热水袋。\n典型案例：老年人热水袋烫伤的图片情境。\n焦点叙述：培训内容看似简单，但需要我们掌握规范的操作方法及');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3300249-ef1c-11ed-956d-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a32f65fa-ef1c-11ed-8e4f-8c554aa628fd', '3', '编写初级养老护理员培训教案', 1, 1, 1, '\n1.编写教案避免出现知识性错误。\n2.编写教案避免脱离教材的完整性与系统性。\n3.编写教案避免简单重复教材的内容。\n', '1 工作准备\n(1)知识准备。护理员应熟悉教材及大纲，熟悉教学计划及教案编写关健环节和要求。\n(2)资料准备。收集整理有关标准、教程、规范等相关资料，阅读整理，并为编写教案提供信息，\n(3)任务评估。了解培训任务，例如，对某养老护理机构招聘新入职的护理员进行基础培训，了解培训对象，如护理员的文化水平是初中毕业；了解培训时间，如两周的岗前培训。\n\n步骤2 研读标准及教程\n标准及教程是培训散案编写的重要依据。确定本节及单元的教学目标，一般应包含知识、能力及思想三个内容。\n\n步骤3 设计培训内容\n根据培训目标，结合培训对象的实际情况，确定本单元或本节课内容的重点和难点，作业设计，包括客观题、主观题、思考题等。布置哪些内容，要考虑知识的拓展性和能力性，是否需要提示或解释等。培训课程结束前小结设计的内容主要是重点和难点的回顾，对学生提出具体的要求。\n\n步骤4 选择教学方式\n选择教学方式时，应分析培训对象的特点，是对新入职人员进行培训，还是岗位规范化培训，以协助设计培训方法。根据教材内容，设计授课类型，应采用的教学方法和准备好教学资源，即授课时使用的各种教具和设备，并设计一节课的教学全过程的程序。\n\n步骤5 形成教学方案\n形成教案后，要认真阅读，并不断修改和完善。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a330294d-ef1c-11ed-a177-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a32ec9ba-ef1c-11ed-a13e-8c554aa628fd', '2', '指导', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3307766-ef1c-11ed-9d26-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a330294d-ef1c-11ed-a177-8c554aa628fd', '3', '对初级养老护理员进行实践指导', 1, 1, 1, '\n1.实践指导过程中，应注意示教顺序，示范演示应边讲边做。\n2.实训分组不宜过大。\n', '1 工作准备\n(1)教师准备。服装整洁，仪态仪表符合要求，语言准备，示范关键动作准备。\n(2)用品准备。示教物品准备、训练记录物品准备、观摩考察物品准备。\n(3)环境准备。清洁，整洁。\n(4)学员准备。服装整洁，礼仪规范。\n\n步骤2 回顾与示范\n实践指导前应首先回顾上一节课的培训内容，总体介绍本次实践指导课的安排。如本单元训练整体安排、目标、时间、重点内容等，大约10分钟；然后进行示范演示，包括分节动作与整体动作演示，大约20分钟。\n\n步骤3 训练与分组\n将实训室进行必要的布置，安排好训练场所；每组选择出负责人和记录人，老师进行巡回督导，训练时间大约为40分钟。\n\n步骤4 观摩与讨论\n可采取抽签的方式排列观摩顺序，提出观摩中的要求及每组演示的时间。总体需要约30分钟的时间完成。\n\n步骤5 讲评与小结\n讲评的内容主要结合训练演示中的问题进行讲评，以提示学员能在今后的工作过程加以改进。讲评时间大约需要5分钟。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3309ec0-ef1c-11ed-b9d8-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', '', '1', '基础护理', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a330ecb6-ef1c-11ed-a1d7-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3309ec0-ef1c-11ed-b9d8-8c554aa628fd', '2', '临终关怀', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33113a7-ef1c-11ed-9057-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a330ecb6-ef1c-11ed-a1d7-8c554aa628fd', '3', '为临终老年人及家属提供精神安慰支持', 1, 1, 0, '\n1.要富有爱心，尊重、理解老年人和家属的言行。\n2.要保护老年人和家属的隐私。\n3.要保持自身良好的心态和稳定的情绪。\n\n', '1 准备工作\n(1)护理员衣着整洁，保持良好心态。\n(2)房间安静、整洁，光线柔和，温湿度适宜，床单位整洁。\n(3)老年人衣物整洁，取舒适体位，家属情绪平稳，愿意沟通。\n\n步骤2 沟通\n护理员应与老年人及家属沟通，评估老年人及家属的状况和需求。\n\n步骤3 制订方案\n护理员根据评估的情况制订方案来关怀老年人及其家属，选择不同的场所、适合的方法来安慰老年人及其家属。\n\n步骤4 实施\n聆听诉说，运用语言、眼神、肢体关怀等方法与老年人及家属进行交流，鼓励、指导家属尽可能多地亲自照料老年人生活，营造家庭氛围，使老年人和家属双方都获得精神慰藉。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33161c7-ef1c-11ed-a287-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a330ecb6-ef1c-11ed-a1d7-8c554aa628fd', '3', '运用肢体语言为临终老年人提供慰藉支持', 1, 1, 0, '\n1.保持环境整洁、安静。\n2.护理员要有足够耐心，认真聆听。\n3.鼓励家属共同参与运用肢体语言慰精老年人。\n', '1 工作准备\n(1)护理员衣着整洁，保持良好心态。\n(2)房间安静整洁，光线柔和，温湿度适宜，床单位整洁。\n(3)老年人衣物整洁，取舒适体位。\n\n步骤2 沟通\n护理员应与老年人沟通，关心、询问老年人感觉如何，有何不适，以拉近与老年人的距离。\n\n步骤3 聆听倾诉\n护理员耐心、认真地聆听老年人的倾诉，并做出理解、宽容、积极的回应。\n\n步骤4 安抚\n利用肢体语言安抚老年人，握住老年人的手或是抚摸老年人，使其感到有人陪伴、支持。\n\n步骤5 与家属沟通\n与家属交流同老年人沟通的内容，将老年人的意愿告知家属，以满足老年人的愿望\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33188d7-ef1c-11ed-b6f8-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3309ec0-ef1c-11ed-b9d8-8c554aa628fd', '2', '冷热应用', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a331b147-ef1c-11ed-92fc-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '为老年人测量口温', 1, 1, 0, '\n1.与病情结合，可口温和肛温对照读取。\n2.若口温计破碎造成误吞水银的情况应及时处理。\n3.其他注意事项同测量腋温。', '1 准备工作\n(1)护理员采用六步洗手法洗净双手和戴口罩。评估老年人的身体状况，确定老年人在30分钟内没有影响体温的因素。对老年人做好解释工作，以取得合作。\n(2)物品准备包括：口温计1支（盛放在垫有纱布的容器中，检查口温计有无破损，水银柱是否在35℃以下)、带盖容器（内放配制好的消毒液）、消毒纱布、体温记录单、笔和表。\n(3)老年人在测量体温前避免喝热饮或冷饮、剧烈运动、情绪激动或洗澡，安静休息30分钟以上。\n(4)保持环境安静、整洁，室温适宜，室温最好为22~24℃，光线柔和。\n\n步骤2 测量体温\n护理员将备好的用物带至床前，让老年人张开嘴，将口温计水银端斜放于老年人舌下（舌系带两侧），嘱老年人闭紧口唇，用鼻呼吸，勿用牙咬。记录时间3分钟。\n\n步骤3 读取体温计时时间到后，护理员协助老年人取出口温计并用消毒纱布擦拭干净后读，方法同测量腋温。读取温度数据后，将口温计放人消毒液中，消毒备用。\n\n步骤4 整理并记录\n(1)帮助老年人整理床单位，盖好被子。\n(2)洗手后记录体温在记录单上，如体温异常则应及时报告，并协助给予老年人进行物理降温。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a331fe0a-ef1c-11ed-9266-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '为老年人测量肛温', 1, 1, 0, '\n1.与病情结合，可口温和肛温对照读取。\n2.读数前要将肛温计擦拭干净。\n3.其他注意事项同测量腋温。', '1 准备工作\n(1)护理员采用六步洗手法洗净双手并戴口罩。护理员应评估老年人的身体状况，确定老年人在30分钟内没有影响体温的因素。对老年人做好解释工作以取得合作。\n(2)物品准备包括：肛温计1支（盛放在垫有纱布的容器中，并检查其有无破损，水银柱是否在35℃以下)、带盖容器（内放配制好的消毒液）、石蜡油、棉签及消毒纱布、体温记录单、笔和表。\n(3)老年人在测量体温前应避免喝热饮或冷饮、剧烈运动、情绪激动或洗澡，安静休息30分钟以上。\n(4)保持环境安静整洁，室温适宜，最好在22~24℃，光线柔和。\n\n步骤2 测量体温\n护理员将备好的用物带至床前，协助老年人采用侧卧、屈膝仰卧或俯卧位，露出臀部，先用棉签蘸取石蜡油涂在肛温计水银端用于润滑，再将肛温计的水银端轻轻插人肛门3~4厘米。注意保持测量体位，以防体温计折断，记录时间为3分钟。\n\n步骤3 读取体温\n计时时间到后，取出肛温计，用消毒纱布擦拭干净后读数，读数方法同测量腋温。读取温度数据后，将肛温计放人消毒液内浸泡消毒备用。\n\n步骤4 整理并记录\n帮助老年人整理床单位，盖好被子。洗手后记录体温在体温记录单上，如体温异常应及时报告并协助给予老年人物理降温。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3322518-ef1c-11ed-a1f7-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '为老年人测量腋温', 1, 1, 0, '\n1.甩体温计时，应注意勿触及他物，以防破碎。\n2.测量过程中应告知老年人如果发生体温计滑落或脱位应保持原体位不动，及时通知护理员，护理员应耐心寻找，避免体温计破碎误伤老年人。\n3.一旦发现体温计破碎，水银外流，护理员应立即采取安全的方法处理。\n4.体温计用后应按要求及时消毒。', '1 准备工作\n(1)护理人员衣着整齐，用六步洗手法洗净双手、戴口罩。护理员应评估老年人的身体状况，确定老年人在30分钟内没有影响实际体温的因素。向老年人解释操作的目的，以取得老年人的合作。\n(2)物品准备，包括：腋温计1支（检查腋温计无破损、水银柱在35℃以下)、体温记录单。\n(3)老年人在测量体温前避免喝热饮或冷饮、剧烈运动、情绪激动或洗澡，安静休息30分钟以上。\n(4)环境准备：环境安静整洁，温湿度适宜。\n\n步骤2 测量体温\n护理员协助老年人解开胸前衣扣，用老年人自己的干毛巾帮助擦干腋下汗液。将体温计水银端放在老年人腋窝深处并贴紧皮肤，屈臂过胸，用上臂将体温计夹紧，必要时托扶老年人手臂，以免体温计脱位或掉落。测量时间为10分钟。\n\n步骤3 读取体温\n计时到点后，护理员协助老年人取出体温计，读取体温值：一手横拿体温计尾部，即远离水银柱的一端，手不可触碰水银端，使眼与体温计刻度保持同一水平，然后慢慢地转动体温计，从正面看到很粗的水银柱时就可读出相应的体温值。\n\n步骤4 整理并记录\n(1)护理员帮助老年人系好衣扣，整理床单位。\n(2)洗手后及时记录，如老年人体温异常应及时报告，并协助给予物理降温。按要求对体温计进行消毒。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3324d82-ef1c-11ed-b8f0-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '使用温水擦浴为老年人进行物理降温', 1, 1, 0, '\n1.温水擦浴过程中应注意保暖。\n2.温水擦浴过程中注意保护老年人的隐私，避免暴露过多。\n3.温水擦浴过程中注意保护老年人的安全，避免坠床的发生。\n\n', '1 工作准备\n(1)护理员向老年人解释操作的目的，取得老年人的合作。同时详细评估老年人的身体状况是否适合进行操作。\n(2)准备物品，包括：32~34℃温水1盆，内浸纱布或小毛巾2块，大毛巾、冰袋、热水袋、布套或小巾2块，屏风，必要时可备干净衣裤1套，体温计、体温记录单、笔。\n(3)环境安静整洁；温湿度适宜，最好在22~24℃；关闭门窗，用屏风遮挡老年人身体。\n\n步骤2 实施擦浴\n(1)护理员打开老年人盖被。将准备好的冰袋、热水袋用布袋或小巾包裹，给老年人头部放冰袋，脚下置热水袋。\n(2)协助老年人露出擦拭部位，下垫大毛巾，拧干浸湿的小毛巾缠在手上成手套式，以离心方向边擦边按摩，其顺序如下：\n1)露出一侧上肢，自颈部沿上臂外衡藤至手背，自侧胸部经肢窝内侧至手心，同法擦拭另一上肢；2) 使老年人侧卧，露出背部，自颈向下擦拭全背部，擦干后穿好上衣；3)露出一侧下肢，自髋部沿腿的外侧擦至足背，自腹股沟的内侧擦至踝部，自股下经胭窝擦至足跟；同法擦村另一下肢。\n(3)擦干后穿好裤子移去热水袋和冰袋，协助老年人盖好被子。\n\n步骤3 复测体温\n温水擦浴30分钟后护理员协助老年人测量体温，如体温降至38.5℃，则可取下头部冰袋。\n\n步骤4 整理记录\n护理员协助老年人躺卧舒适，按要求整理好热水袋和冰袋洗手，并记录体温变化。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3329a46-ef1c-11ed-838f-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '用冰袋为老年人进行物理降温', 1, 1, 0, '\n1.护理员每10分钟观察用冷疗法部位皮肤状况，若有苍白、青紫、灰白、颤抖、疼痛或麻木感须立即停止使用。\n2.化学冰袋使用前应检查有无破损，防止破损后化学物质渗漏，造成皮肤损伤。\n3.应密切观察老年人病情及体温变化，一般体温降温后不宜低于36℃，如有异常及时报告。\n\n', '1 准备工作\n根据实际情况准备自制冰袋或化学冰袋数个并检查化学冰袋是否完好，布套或小巾，体温计，体温记录单，笔。\n\n步骤2 评估沟通\n护理员向老年人解释操作的目的，取得老年人的合作。详细评估老年人的身体状况，以确认是否可进行物理降温操作。\n\n步骤3 放置冰袋\n(1)护理员用布套或小巾将冰袋包裹，置于老年人前额、头顶和体表大血管处，如腹股沟、腋下，禁止直接接触皮肤。\n(2)使用冰袋期间，护理员要经常询问老年人的感受，观察冰袋的情况及局部皮肤的颜色，有无冻伤等。冰块融化后应及时更换。\n\n步骤4 复测体温 \n物理降温后30分钟应给予复测体温，观察降温效果，若采用腋下测温注意要在未放置冰袋侧腋窝处测量体温。\n\n步骤5 整理用物\n(1)体温下降后取出冰袋，整理床单位，安置好老年人，使其体位舒适。\n(2)将冰袋中冰水倒空，倒挂冰袋晾干，吹人空气后夹紧袋口（以防两层橡胶粘连)，放于通风阴凉处；袋套清洗，晾干备用。若使用一次性化学冰袋，用完后按医疗垃圾分类处置。\n\n步骤6 记录\n洗手后，记录老年人使用冰袋前后的体温变化。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a332c162-ef1c-11ed-b2f7-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '为老年人进行湿热敷', 0, 1, 0, '\n1.严密观察湿热敷部位皮肤状况，防止烫伤。\n2.瘫痪、糖尿病、肾炎等血液循环障得或感知觉异常的老年人不可使用湿热敷，以免发生意外。\n', '1 工作准备\n(1)环境准备。关闭门窗，温湿度适宜。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。老年人取坐位或卧位。\n(4)物品准备。水盆（内盛50~60℃热水）、暖瓶1只、毛巾2块、橡胶单1块、浴巾1块、润肤油1瓶。\n\n步骤2 沟通\n了解老年人身体疾病状况。向老年人告知给予一般性湿热敷可以缓解关节疼痛。告知湿热敷的过程，取得老年人的配合。\n\n步骤3 进行湿热敷\n(1)备齐物品携至老年人床旁。露出老年人需要湿敷的部位（如关节），铺好橡胶单及浴巾。\n(2)护理员将毛巾浸在水盆中湿透，拧至半干，以不滴水为宜。抖开，在自己的手腕掌侧测试敷布温度，感觉温热适宜，放于老年人关节部位上，将干毛巾覆盖在上面，以防散热过快。\n(3)观察并询问老年人有无不适，局部皮肤有无发红、起水泡等烫伤情况。如果老年人感觉过热时可揭开干毛巾一角放出热气。每3~5分钟更换一次敷布，水盆内随时添加热水，湿敷20一30分钟（或按医嘱）。\n\n步骤4 整理用物\n湿热敷完毕，用毛巾擦干局部皮肤，撤去用物。在湿热敷部位涂润肤油。整理好衣裤，盖好盖被。清理用物。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3330f81-ef1c-11ed-9580-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '使用热水袋为老年人保暖', 0, 1, 0, '\n1.在老年人使用热水袋过程中，护理员要每15分钟巡视一次。如发生烫伤，应立即停止使用，进行局部降温并及时报告。\n2.老年人应避免长时间用热，时间以30~60分钟为宜。\n3.老年人使用热水袋，水温应调节至50℃，热水袋装人布套内或包裹毛巾，避免与皮肤直接接触，防止烫伤。\n', '1 工作准备\n(1)环境准备。环境清洁，室温适宜。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。老年人取平卧，盖好盖被。\n(4)物品准备。同本学习单元技能要求1。\n\n步骤2 沟通\n评估老年人身体状况，有无感觉、运动功能障碍，痛觉、温觉是否减退或消失，有无皮肤破损情况。向老年人解释将使用热水袋为其保暖，以取得配合。\n\n步骤3 放置热水袋\n携热水袋至老年人床旁，掀开盖被放置于距离足部或身体10厘米处。告知老年人热水袋放置的位置。提醒老年人变换体位时避免肢体触及，感觉不适，按呼叫器通知护理员。热水袋放置期间，护理员应15分钟巡视一次。\n\n步骤4 取出热水袋\n用热30一60分钟后，取出热水袋。\n(1)检查热水袋温度，询问老年人是否继续使用（需要更换热水）。\n(2)观察老年人靠近热水袋处的肢体是否温暖，皮肤有无发红、水泡等低温烫伤的迹象。\n(3)协助老年人取舒适卧位，将被子盖严，整理床铺。\n\n步骤5 整理用物\n护理员将热水袋内的水倒空，倒挂晾干后吹入空气，旋紧塞子，放在阴凉干燥处备用。\n\n步骤6 记录\n护理员洗净双手，记录热水袋使用情况。记录内容包括：热水袋放置时间、取出时间、老年人用热后全身及局部情况。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3333695-ef1c-11ed-8d53-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '使用热水袋为老年人睡前暖被褥', 0, 1, 0, '\n1.灌入热水后要仔细检查热水袋是否旋紧塞子，避免漏水打湿床褥。\n2.老年人入睡前，勿忘取出热水袋。', '1 工作准备\n(1)环境准备。环境清洁，室温适宜。\n(2)护理员准备。洗净双手，服装整洁\n(3)物品准备\n1)准备用物包括热水袋、热水袋套、水壶（内盛有50℃左右的温水）、水温计、毛巾、纱布等。\n2)灌装热水袋方法\n①水温计插入水壶中测量水温，水温调节至50℃。\n②检查热水袋外观，应完好无破损。\n③灌装热水。一手持热水袋袋口边缘，另一手持水壶缓慢灌入热水至热水袋的1/2~2/3满\n④排气，旋紧盖子，热水袋口端逐新放平，见热水法到袋口即排尽袋内空气，此时旋紧螺旋塞。\n⑤检查。使热水袋袋口朝下，双手进行挤压，检查热水袋有无漏水。\n⑥装入热水袋套。用毛巾擦干热水袋袋口及外壁水痕，全部装入热水袋套内，系紧袋口。\n\n步骤2 沟通\n告知老年人即将把热水袋放人铺好的被子里，提醒老年人稍后上床休息。\n\n步骤3 放置热水袋\n携热水袋至老年人床旁，再次检查热水袋有无漏水。依老年人喜好将热水袋放置在铺好的被子里的适宜位置，如床铺中间或床尾的位置等。\n\n步骤4 取出热水袋\n老年人睡前，护理员从被子中取出热水袋并感受被内温度及有无漏水潮湿。协助老年人就寝，盖好被子。\n\n步骤5 整理用物\n将热水袋内的水排空，倒挂晾干，之后吹人空气旋紧塞子，放在阴凉干燥处备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33384b1-ef1c-11ed-86b1-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33188d7-ef1c-11ed-b6f8-8c554aa628fd', '3', '老年人皮肤异常观察与记录', 0, 1, 0, '\n1.注意皮肤情况要前后对比。\n2.观察顺序从头到脚，勿遗漏。\n3.记录完整，报告及时。\n', '1 工作准备\n(1)环境准备。环境清洁，温湿度适宜，光线充足。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。老年人平卧床上。\n(4)物品准备。手电、护理记录单、笔。\n\n步骤2 沟通\n护理员查房，询问老年人有无不适。告知老年人要检查一下全身皮肤情况，以便取得配合。\n\n步骤3 观察皮肤\n掀开盖被，逐步暴露躯体，仔细观察老年人全身皮肤有无异常。如光线不足可用手电照明。如有皮损、破遗、颜色异常，仔细询问老年人有无感、知觉变化。认真听取老年人主诉。观察完毕，协助老年人取舒适卧位，盖好盖被。\n\n步骤4 记录\n护理员洗手，记录。将老年人皮肤情况详细记录在记录单上，主要记录内容包括：皮损部位、面积、颜色、性质等。新发皮肤问题，立即报告主管领导。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a333abc6-ef1c-11ed-bc0f-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3309ec0-ef1c-11ed-b9d8-8c554aa628fd', '2', '应急救护', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a333d2d5-ef1c-11ed-816b-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '为心脏骤停的老年人进行现场心肺复苏', 1, 1, 1, '为心脏骤停的老年人进行现场心肺复苏\n\n步骤1 工作准备\n(1)护理人员准备。护理员保持应急状态，随时准备抢救。\n(2)物品准备。按压板1块，简易呼吸器1个，治疗盘1个，手电筒1个，血压计1个，听诊器1个，脚凳1个。\n(3)环境准备。嘱家属全部离开房间。\n(4)安置老年人。\n1)将老年人仰卧于床上，头部与心脏在同一水平，以保证脑血流量。\n2)如有可能应拾高下肢，以增加回心血量。\n\n步骤2 判断\n(1)双手拍打老年人双肩并呼叫老人（喂！您怎么啦），观察有无反应。\n(2)老年人无反应，立即呼叫医生并开始抢救计时（报告×时×分开始抢救）。\n(3)去床头挡。\n(4)撤去枕头，使老年人平卧。\n(5)掀开棉被。\n(6)胸部下垫按压板。\n(7)解开老年人衣扣。\n(8)判断颈动脉搏动，右手食指、中指并拢，沿老年人的气管纵向滑行至喉结处，在旁开2~3厘米处停顿触摸搏动，计时小于10秒。\n\n步骤3 胸外心脏按压\n(1)报告：颈动脉搏动消失。\n(2)垫脚凳。\n(3)立即行胸外心脏按压30次。\n\n步骤4 开放气道\n常用仰面举颌法开放气道。\n\n步骤5 人工呼吸\n(1)护理员用口包裹老年人口，双手捏住老年人双侧鼻孔，用力吹气。\n(2)人工呼吸2次，报告气道有无梗阻\n(3)无梗阻连续做5个循环，按压与人工呼吸比为30：2。\n\n步骤6 评估\n(1)用右手食指、中指触横颈动脉，数10秒（数出声音）\n(2)如自主呼吸恢复（即眼观老年人胸部有起伏或有正常呼吸）、颈动脉博动恢复，立即报告“X时入分自主呼吸恢复，颈动脉博动可触及”\n(3)测量血压（收缩压室60毫米汞柱以上即8千帕以上）。报告数据，并整理血压计\n(4)将手电简对准自己打开\n(5)左手拇指拨开老年人上眼脸\n(6)灯光从老年人腹部绕到老年人远侧眼尾侧。\n(7)缓慢移至瞳孔正上方。\n(8)停顿数秒，观察瞳孔大小及缩小情况。\n(9)同样方法检查老年人近侧瞳孔。\n(10)瞳孔较前缩小，表示大脑有足够的氧和血液供应。报告观察情况，如“瞳孔缩小，对光反射存在”。\n(11)观察并报告老年人颜面、口唇、牙床紫绀减轻，末梢循环改善等情况。\n(12)报告“复苏成功，继续给予高级生命支持”。\n\n步骤7 整理用物\n(1)复苏成功后撤按压板。\n(2)为老年人整理衣服。\n(3)头部垫枕\n(4)盖好棉被\n(5)安装床头挡\n(6)洗手\n(7)记录抢救过程\n', '1 工作准备\n(1)护理人员准备。护理员保持应急状态，随时准备抢救。\n(2)物品准备。按压板1块，简易呼吸器1个，治疗盘1个，手电筒1个，血压计1个，听诊器1个，脚凳1个。\n(3)环境准备。嘱家属全部离开房间。\n(4)安置老年人。\n1)将老年人仰卧于床上，头部与心脏在同一水平，以保证脑血流量。\n2)如有可能应拾高下肢，以增加回心血量。\n\n步骤2 判断\n(1)双手拍打老年人双肩并呼叫老人（喂！您怎么啦），观察有无反应。\n(2)老年人无反应，立即呼叫医生并开始抢救计时（报告×时×分开始抢救）。\n(3)去床头挡。\n(4)撤去枕头，使老年人平卧。\n(5)掀开棉被。\n(6)胸部下垫按压板。\n(7)解开老年人衣扣。\n(8)判断颈动脉搏动，右手食指、中指并拢，沿老年人的气管纵向滑行至喉结处，在旁开2~3厘米处停顿触摸搏动，计时小于10秒。\n\n步骤3 胸外心脏按压\n(1)报告：颈动脉搏动消失。\n(2)垫脚凳。\n(3)立即行胸外心脏按压30次。\n\n步骤4 开放气道\n常用仰面举颌法开放气道。\n\n步骤5 人工呼吸\n(1)护理员用口包裹老年人口，双手捏住老年人双侧鼻孔，用力吹气。\n(2)人工呼吸2次，报告气道有无梗阻\n(3)无梗阻连续做5个循环，按压与人工呼吸比为30：2。\n\n步骤6 评估\n(1)用右手食指、中指触横颈动脉，数10秒（数出声音）\n(2)如自主呼吸恢复（即眼观老年人胸部有起伏或有正常呼吸）、颈动脉博动恢复，立即报告“X时入分自主呼吸恢复，颈动脉博动可触及”\n(3)测量血压（收缩压室60毫米汞柱以上即8千帕以上）。报告数据，并整理血压计\n(4)将手电简对准自己打开\n(5)左手拇指拨开老年人上眼脸\n(6)灯光从老年人腹部绕到老年人远侧眼尾侧。\n(7)缓慢移至瞳孔正上方。\n(8)停顿数秒，观察瞳孔大小及缩小情况。\n(9)同样方法检查老年人近侧瞳孔。\n(10)瞳孔较前缩小，表示大脑有足够的氧和血液供应。报告观察情况，如“瞳孔缩小，对光反射存在”。\n(11)观察并报告老年人颜面、口唇、牙床紫绀减轻，末梢循环改善等情况。\n(12)报告“复苏成功，继续给予高级生命支持”。\n\n步骤7 整理用物\n(1)复苏成功后撤按压板。\n(2)为老年人整理衣服。\n(3)头部垫枕\n(4)盖好棉被\n(5)安装床头挡\n(6)洗手\n(7)记录抢救过程');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33420f5-ef1c-11ed-9f59-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '初步应对老年人烫伤', 1, 1, 1, '\n1.若穿着衣服或鞋袜的部位被烫伤，千万不要急忙脱去被烫部位的鞋袜或衣裤，以免造成表皮随同鞋林、衣裤一起脱落。应先用冷水或食醋（食醋有收敛、散疼、消肿、杀菌、止痛作用)隔着衣裤或鞋袜浇到伤处及周围，然后再脱去鞋袜或衣裤，进行“冷却治疗”。\n2.“冷却治疗”期间，要为老年人保暖，以免着凉。\n\n', '1 工作准备\n(1)迅速脱离热源\n1)迅速协助老年人脱离热源\n2)有衣物者，切勿脱去衣物。\n(2)物品准备。准备冰块数个，手帕1条，鸡蛋清或烫伤膏，记录本1个，笔1支。\n\n步骤2 冷却治疗\n用冷水冲洗或浸泡，或用手帕包裹冰块冰敷伤处\n\n步骤3 涂皮肤保护剂\n在红肿处涂鸡蛋清或烫伤膏等。\n\n步骤4 报告\n报告医护人员或家属，或拨打急救电话。\n\n步骤5 记录\n记录烫伤的原因，伤处的面积、程度及处理过程。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3344801-ef1c-11ed-83df-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '协助为老年人进行氧气吸入操作', 1, 1, 1, '\n1.在给老年人戴吸氧管前应先调节好氧流量，以免损伤老年人鼻黏膜；在停止氧气吸入时，要先为老年人摘下吸氧管，后关闭氧流量调节阀，以免影响老年人的呼吸。\n2.清洁老年人一侧鼻孔时可使用多个棉签，以清洁干净为淮，但不可同一棉签清洁双侧鼻孔。\n3.给老年人进行氧气吸人前应确保氧气管道的通畅，并随时观察老年人氧气吸入的效果。\n\n\n', '1 工作准备\n(1)物品准备。准备氧气筒氧气吸入装置1套，流量表及湿化瓶（内装灭菌蒸馏水或冷开水，液量为湿化瓶容量的1/2)各1个，吸氧管（双管头）1根，弯盘1个，小药杯1个（内装20毫升灭菌蒸馏水），棉签1包。\n(2)放置氧气筒。\n1)将氧气筒置于架上或平放于地面上。\n2)用扳手夹紧总开关旋钮逆时针旋转，将总开关打开，使小量氧气从气门冲出\n3)随即顺时针旋转关好总开关，以达清洁该处的目的，避免灰尘吹人氧气表内。\n(3)装氧气压力表。将氧气压力表的螺帽与氧气筒的螺丝接头衔接，用手向下旋繁，将表稍向后倾，再用扳手进一步向下旋紧。\n(4)检查通气。旋开总开关，再向上旋转打开流量调节阀，检查氧气流出是否通畅，全套装置有无漏气，最后向下旋转关闭流量调节阀。\n(5)推至床旁待用。\n(6)向老年人解释，取得老年人配合。\n(7)协助老年人取舒适体位。\n\n步骤2 清洁鼻孔\n(1)检查老年人鼻孔。\n1)用食指分别轻压老年人双侧鼻孔，检查是否柔软，有无硬结。\n2)询问老年人“通不通”“痛不痛”“有没有鼻部疾病或有无做过鼻部的手术”等，并用眼看老年人鼻孔内是否有血痂等。\n(2)清洁老年人鼻孔。\n1)取一根棉签蘸蒸馏水或冷开水伸人一侧鼻孔约2厘米，紧贴鼻腔黏膜轻轻旋转，清洁鼻腔。\n2)同样方法清洁另一侧鼻孔。\n\n步骤3 连接吸氧管\n(1)协助医护人员取吸氧管与氧气流量表出口接头相连，将吸氧管单头插至流量表出口接头的底部\n(2)检查氧气管路是否通畅。打开流量调节阀后，左手将吸氧管头贴近操作者面部，感觉是否有气流吹出。将吸氧管头放人装有灭菌蒸馏水或冷水的杯中，观察是否有气泡冒出。\n\n步骤4 给氧固定\n(1)遵医嘱旋转流量旋钮，调节氧流量，使流量表内浮球所指的刻度与医嘱规定的氧流量数字一致。\n(2)将双头插入老年人双侧鼻孔。\n(3)导管绕过老人双耳至下颌锁住，或至头顶锁住。\n(4)用别针将导管固定在床上。\n\n特别提示 固定一次性吸氧面罩时，将吸氧面里扣住老年人口鼻，窄头朝鼻侧宽头朝下领侧，将面罩两侧松紧带绕到头后侧固定面罩\n\n步骤5 观察记录\n(1)观察老年人氧气吸人的效果。\n1)紫绀。观察口唇、口腔黏膜、牙床、類部、鼻尖、耳廓、甲床等部位紫绀是否减轻，即这些部位的颜色是否转红润。\n2)意识。烦躁不安的老年人是否安静，意识障碍的老年人是否呼能应，能否准确回答询问的问题。\n3)主诉。询问老年人头晕、头痛、心慌等症状是否缓解\n(2)记录氧气吸入的过程。记录氧气吸入开始的时间，氧流量，氧气吸人后老年人的表现，即老年人氧气吸人的效果，以及氧气吸人结束的时间。\n\n步骤6 停止吸氧\n在老年人缺氧症状改善后，应遵医嘱停止氧气吸入。\n(1)松开氧气管的锁圈，摘下吸氧管（或轻轻摘下面罩的松紧带，顺势取下面罩）\n(2)关闭流量调节阀。\n(3)取下吸氧管，缠绕成圈，放入袋内避污保存。\n(4)用扳手关闭氧气筒总开关。\n(5)打开流量表调节阀，放尽余气。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3349618-ef1c-11ed-82b7-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '协助为骨折老年人进行初步固定', 1, 1, 1, '\n1.夹板的长度与宽度要与骨折的肢体相适应，其长度必须超过骨折的上、下两个关节。固定时除骨折部位上、下两端外，还要固定上、下两个关节。\n2.固定应松紧适度，以免影响血液循环。\n3.固定中避免不必要的搬动，不可强制老年人进行各种活动。\n4.若老年人为腰部骨折或下肢骨折，切勿随意移动老年人，应先立即拨打就医电话并报告，待医护人员到场后再配合进行下一步处理。', '1 工作准备\n(1)安置老年人。\n1)协助老年人原地坐起，不可随意移动。\n2)嘱张奶奶勿随意活动右侧上肢，保持屈曲位。\n(2)物品准备。准备绷带数卷（或三角巾数条），胶布数卷，剪刀，夹板（或木板、木棍等)数个。\n(3)评估。评估老人情绪状态，并安慰老人\n\n步骤2 报告\n立即报告医护人员，或拨打就医电话。\n\n步骤3 配合固定\n(1)医护人员到场后，配合医护人员对伤口包扎\n(2)用绷带将夹板固定骨折处。\n\n步骤4 观察\n随时观察并询问老年人有何不适。\n\n步骤5 记录\n记录老年人受伤过程及时间、部位，包扎过程及时间。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a334bea2-ef1c-11ed-95cc-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '协助老年人进行外伤止血', 1, 1, 1, '\n1.毛细血管出血或伤口处出血量少，伤口可先用流动的清水冲洗。\n2.若为大血管出血，出血量大、速度快，应先止血，并立即就医处理。\n3.止血过程中，一定要随时观察老年人伤口远端皮肤颜色及温度，紫绀或是皮肤温度下降，立即将止血带松开，以免发生组织坏死。\n', '1 工作准备\n(1)护理人员准备。护理员洗手并用干净毛巾擦干，戴口罩。\n(2)安置老年人。立即协助老年人离开危险现场，取舒适体位，若为摔倒则保持原位，不能移动。\n(3)物品准备。立即取用消毒纱布（或干净的手帕、毛巾、衣物）数块，绷带(橡皮带、三角巾、布条、线绳等)1条，胶布1卷（或创可贴）。\n\n步骤2 评估\n(1)评估出血属于何种类型（若为大静脉或动脉出血，可直接加压包扎）。\n(2)评估老年人情绪状态，并安慰老年人。\n\n步骤3 覆盖伤口\n用消毒纱布或清洁手帕置于出血处，覆盖伤口。\n\n步骤4 加压包扎\n用绷带加压包扎（若出血量少，则可用创可贴缠绕固定），必要时使用止血带。\n\n步骤5 报告\n报告家属或医护人员，或拨打就医电话。\n\n步骤6 观察记录\n观察老年人皮肤颜色及伤口处有无继续出血的情况，并记录出血原因、出血类型、伤口情况、加压包扎的开始时间（使用止血带时需记录扎、松止血带时间）。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a334e441-ef1c-11ed-bec7-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '对老年人摔伤进行初步处理', 1, 1, 1, '\n1.包扎伤口时，先简单清创并盖上消毒纱布，然后再用绷带等。操作时要小心、谨慎，不要触及伤口，以免加重疼痛或导致伤口出血及污染。\n2.包扎时松紧要适宜，过紧会影响局部血液循环，过松易致敷料脱落或移动。\n3.绷带固定时的结应放在肢体的外侧面，忌在伤口上、骨隆突处或易受压的部位打结。\n4.包扎四肢应将指（趾）端外露，以便于观察血液循环，如有异常，及时报告。\n', '1 工作准备\n(1)护理人员准备。护理员洗手并用干净毛巾擦于。\n(2)安置老年人。\n1)评估制动。因无法判断老年人有无骨折，故先嘱老年人勿活动。\n2)将老年人移至床上或座椅上，取舒适体位。\n(3)物品准备。准备绷带数卷（或三角巾数条），胶布数卷，剪刀1把，消毒剂1瓶，棉签数包。\n\n步骤2 报告\n立即报告医务人员或家属，或拨打急救电话。\n\n步骤3 覆盖伤口\n用无菌纱布（或干净手帕）放在伤口正上方，覆盖伤口。\n\n步骤4 包扎伤口\n\n步骤5 冷敷伤口\n用毛巾包裹冰块或用塑料袋装冷水浸泡的毛巾敷于伤口处。\n\n步骤6 观察记录\n记录老年人受伤时间、受伤部位、出血情况、处理情况等，观察老年人有无其他不适。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a335329e-ef1c-11ed-85af-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '对老年人跌倒进行急救处理', 1, 1, 1, '对老年人跌倒进行急救处理\n\n步骤1 工作准备\n(1)护理员立即到老年人身边。\n(2)暂时勿移动老年人。\n\n步骤2 判断意识\n判断老年人意识是否清楚。\n\n步骤3 询问\n询问老年人有何不适。\n\n步骤4 处理外伤\n如有外伤出血等，先处理外伤。\n\n步骤5 协助移动或就医\n无不适者可协助起立，并坐、卧休息。严重者不可搬动，立即就医。\n\n步骤6 观察记录\n观察老年人有何异常及不适，并记录老年人跌倒原因、过程及处理流程。\n\n', '1 工作准备\n(1)护理员立即到老年人身边。\n(2)暂时勿移动老年人。\n\n步骤2 判断意识\n判断老年人意识是否清楚。\n\n步骤3 询问\n询问老年人有何不适。\n\n步骤4 处理外伤\n如有外伤出血等，先处理外伤。\n\n步骤5 协助移动或就医\n无不适者可协助起立，并坐、卧休息。严重者不可搬动，立即就医。\n\n步骤6 观察记录\n观察老年人有何异常及不适，并记录老年人跌倒原因、过程及处理流程。\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3355969-ef1c-11ed-b37f-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '应对老年人异物卡喉', 1, 1, 1, '\n1.在操作时掌握好力度。海姆利克操作技术虽卓有成效，但也可产生合并症，如肋骨骨折、腹部或胸腔内脏的破裂或撕裂。\n2.使用此操作方法成功抢数老年人后应询问老年人有无不适，检查老年人有无并发症的发生', '1 工作准备\n评估老年人身体情况，有无意识不清，能否站立或坐起。\n步骤2 摆体位\n依据老年人身体情况为老年人摆好坐位或站位，嘱其低头口朝地面，或呈仰卧位。\n\n步骤3 反复适力挤压\n护理员按老年人体位采用相应手法，按要求以适当力量反复挤压老年人腹部。\n\n步骤4 取出异物\n嘱老年人张口吐出异物或看到异物排出及时用手帮老年人取出。\n\n步骤5 观察就医\n无法缓解或有其他异常情况应立即就医。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a335a788-ef1c-11ed-bd45-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a333abc6-ef1c-11ed-bc0f-8c554aa628fd', '3', '配合医护人员搬移骨折老年人', 1, 1, 1, '\n1.三位搬运者由老年人头部按身高顺序排列，高者在老年人头侧，使老年人头部处于高位,以减轻不适\n2.按口令同时用力,保持平稳,减少意外伤害的发生\n\n', '1 工作准备\n(1)安置老年人\n1)将老年人平卧于原地，切勿移动老年人\n2)嘱附老年人勿随意活动肢体。\n(2)物品准备。绷带数卷（或三角巾数条），胶布数卷，剪刀，夹板（或木板、木棍等)数个。\n(3)评估。\n1)评估老年人受伤部位、程度、老年人的主诉及情绪状态。\n2)安慰老年人。\n\n步骤2 报告\n立即拨打就医电话，并报告医护人员或家属。\n\n步骤3 配合包扎与固定\n(1)医护人员到场后，配合医护人员对伤处包扎。\n(2)取绷带将夹板固定骨折处。\n\n步骤4 配合抬担架\n(1)在医护人员指导下，三人同时站在老年人同一侧。\n(2)甲托住老年人的头、颈、肩及胸部。\n(3)乙托住老年人的背、腰、臀部。\n(4)丙托住老年人的膝及脚。\n(5)三人同时拾起老年人，使老年人身体稍向护理员倾斜。\n(6)移步将老年人放于担架\n(7)配合医护人员抬担架至指定位置。\n\n步骤5 观察\n观察老年人有无不适。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a335ce99-ef1c-11ed-8605-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3309ec0-ef1c-11ed-b9d8-8c554aa628fd', '2', '消毒防护', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a335f5a9-ef1c-11ed-9ee1-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a335ce99-ef1c-11ed-8605-8c554aa628fd', '3', '实施老年人房间消毒', 1, 1, 1, '实施老年人房间消毒\n\n步骤1 工作准备\n(1)护理人员准备。护理员着装整洁，洗手，戴口罩，必要时戴手套。\n(2)物品准备。\n1)用桶作为容器配制消毒液备用。\n2)准备脸盆1个、抹布1个、拖布1把等。\n(3)安置老年人。\n1)能活动的老年人需在护理员的陪伴下离开房间（搀扶或用轮椅推出），安置在一个安全、温暖的地方，并有人看护，防止走失或摔倒。\n2)活动不便的，为其戴口罩，并嘱其闭上眼睛或用眼罩。\n(4)环境准备，环境清洁宽散、干燥平组，停止清扫工作，减少走动，避免生埃飞扬。\n(5)携用物至老年人床旁，关闭门窗。\n\n步骤2 浸泡物品\n(1)向脸盆内倒人适量配制好的消毒液。\n(2)将需浸泡消毒的物品，如餐（茶）具、老年人使用的物品（金属、有色针织禁用)等放人消毒液中。\n\n步骤3 擦拭物品\n用抹布蘸取桶内消毒液对家具、墙面、窗台进行擦拭。\n\n步骤4 消毒地面\n用拖布蘸取桶内消毒液拖地。\n\n步骤5 整理用物\n将浸泡的物品取出，用清水刷洗干净后晾干，将剩余消毒液到入水池。\n\n步骤6 开窗通风\n(1)开窗通风30分钟。\n(2)搀扶老年人回房间。\n\n', '1 工作准备\n(1)护理人员准备。护理员着装整洁，洗手，戴口罩，必要时戴手套。\n(2)物品准备。\n1)用桶作为容器配制消毒液备用。\n2)准备脸盆1个、抹布1个、拖布1把等。\n(3)安置老年人。\n1)能活动的老年人需在护理员的陪伴下离开房间（搀扶或用轮椅推出），安置在一个安全、温暖的地方，并有人看护，防止走失或摔倒。\n2)活动不便的，为其戴口罩，并嘱其闭上眼睛或用眼罩。\n(4)环境准备，环境清洁宽散、干燥平组，停止清扫工作，减少走动，避免生埃飞扬。\n(5)携用物至老年人床旁，关闭门窗。\n\n步骤2 浸泡物品\n(1)向脸盆内倒人适量配制好的消毒液。\n(2)将需浸泡消毒的物品，如餐（茶）具、老年人使用的物品（金属、有色针织禁用)等放人消毒液中。\n\n步骤3 擦拭物品\n用抹布蘸取桶内消毒液对家具、墙面、窗台进行擦拭。\n\n步骤4 消毒地面\n用拖布蘸取桶内消毒液拖地。\n\n步骤5 整理用物\n将浸泡的物品取出，用清水刷洗干净后晾干，将剩余消毒液到入水池。\n\n步骤6 开窗通风\n(1)开窗通风30分钟。\n(2)搀扶老年人回房间。\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33643e2-ef1c-11ed-b36e-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a335ce99-ef1c-11ed-8605-8c554aa628fd', '3', '对老年人的居室进行紫外线消毒', 1, 1, 1, '，做好告知工作\n(2)协助能活动的老年人离开房间（搀扶或用轮椅推出），到一个安全、温暖的地方，并有人看护，防止走失或摔倒。\n(3)为卧床老年人给予屏风挡护，并以大单盖护身体及皮肤，头部要用支架，支架外覆盖稍厚的棉布遮挡头面部，保证老年人正常呼吸，并告诉老年人闭上眼睛或用眼罩，对于有躁动或不能进行有效沟通交流的老年人必要时可使用约束带约束肢体。\n\n步骤2 携用物至床旁\n(1)将紫外线车携至床旁，距床至少2米，远离头部。\n(2)打开灯管保护门的铁扣\n(3)轻轻将灯管拍平，松开即可，灯管可自动卡住保持不动。\n(4)将房间内的杯子餐盒盖好盖子。\n\n步骤3 连接电源\n将电源插头插向插座底部。\n\n步骤4 打开开关消毒\n(1)顺时针旋转时间控制旋钮，调节消毒时间。\n(2)向“开”字方向按下开关，对房间进行消毒（见图2一9）。\n(3)关闭日光灯。\n(4)紫外线灯打开的过程中，要定时巡视房间情况，确保卧床老年人的安全。\n\n步骤5 整理用物\n(1)照射时间完成后，紫外线灯会自动熄灭，向“关”字方向按下开关关闭紫外线灯\n(2)打开日光灯。\n(3)拔掉电源插头，断开电源。\n(4)向下轻按灯管，将灯管放回保护门内，并扣好铁扣。\n(5)拉开窗帘，打开门窗。（卧床老年人：拿去保护老年人所用的大单或毡子。能活动的老年人：查看老年人情况，开窗通风30分钟后请室外老年人回房间。)\n(6)将紫外线车移走，放回原处，用清洁的棉布擦拭。\n\n步骤6 记录登记\n在紫外线登记本上登记并签字。\n\n注意事项\n1.在使用紫外线车或灯前，应观察紫外线照射时间及累计照射时间，是否需要更换灯管。\n2.紫外线对有机细胞有杀伤力，避免直接对皮肤及眼睛的照射。\n3.用紫外线灯消毒室内空气时，房间内应保持清洁干燥，减少尘埃和水雾，温度低于20℃或高于40℃，相对湿度大于60%时应适当延长照射时间。\n4.灯管清洁度会影响消毒效果，故按使用要求进行灯管清洁十分必要。\n5.若老年人在消毒过程中，出现恶心、呕吐、心悸、气促、面色苍白、抽搐等症状，应及时停止消毒，并报告医护人员。\n6.如老年人情绪躁动，暂不进行紫外线消毒。若必须消毒，则应注意安全，适当约束，专人看护。\n7.开窗通风时，注意室内老年人的保暖，切勿着凉。\n', '1 工作准备\n(1)护理员向老年人讲解紫外线消毒的方法和');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3366ad7-ef1c-11ed-b4bc-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a335ce99-ef1c-11ed-8605-8c554aa628fd', '3', '监测老年人居室消毒结果', 1, 1, 1, '监测老年人居室消毒结果\n\n步骤1 工作准备\n(1)护理人员准备。护理员着装整洁，洗手，戴口罩，必要时戴手套。\n(2)物品准备。\n1)配制好消毒液备用。\n2)准备适量试纸，脸盆1个、抹布1个、拖布1把（或紫外线灯及紫外线强度计)，培养皿数个。\n(3)安置老年人。\n1)协助能走动的老年人离开房间。\n2)卧床老年人戴口罩、闭眼，必要时遮挡面部并戴眼罩。\n(4)环境准备。环境清洁宽敞、干燥平坦，停止清扫工作，减少走动，避免尘埃飞扬。\n\n步骤2 监测消毒用具\n用试纸监测消毒液的浓度是否符合标准（或用紫外线强度计监测紫外线灯是否能够进行有效消毒)。\n\n步骤3 实施消毒\n按要求对老年人房间进行消毒。\n\n步骤4 空气采样\n(1)检查培养皿是否在有效期内。\n(2)将培养皿按要求放在固定位置。\n(3)期间任何人不能走动。\n(4)5分钟后将培养皿盖盖上。\n(5)用无菌巾盖好。\n\n步骤5 物品表面采样\n消毒处理后4小时对物品表面进行采样。\n\n步骤6 及时送检\n将培养皿及物品表面采样及时送到检验部门进行检验。\n\n步骤7 记录保存结果\n将检测效果记录在固定本上或贴在本上，检测结果存留2年。\n\n', '1 工作准备\n(1)护理人员准备。护理员着装整洁，洗手，戴口罩，必要时戴手套。\n(2)物品准备。\n1)配制好消毒液备用。\n2)准备适量试纸，脸盆1个、抹布1个、拖布1把（或紫外线灯及紫外线强度计)，培养皿数个。\n(3)安置老年人。\n1)协助能走动的老年人离开房间。\n2)卧床老年人戴口罩、闭眼，必要时遮挡面部并戴眼罩。\n(4)环境准备。环境清洁宽敞、干燥平坦，停止清扫工作，减少走动，避免尘埃飞扬。\n\n步骤2 监测消毒用具\n用试纸监测消毒液的浓度是否符合标准（或用紫外线强度计监测紫外线灯是否能够进行有效消毒)。\n\n步骤3 实施消毒\n按要求对老年人房间进行消毒。\n\n步骤4 空气采样\n(1)检查培养皿是否在有效期内。\n(2)将培养皿按要求放在固定位置。\n(3)期间任何人不能走动。\n(4)5分钟后将培养皿盖盖上。\n(5)用无菌巾盖好。\n\n步骤5 物品表面采样\n消毒处理后4小时对物品表面进行采样。\n\n步骤6 及时送检\n将培养皿及物品表面采样及时送到检验部门进行检验。\n\n步骤7 记录保存结果\n将检测效果记录在固定本上或贴在本上，检测结果存留2年。\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a336b904-ef1c-11ed-bbc2-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a335ce99-ef1c-11ed-8605-8c554aa628fd', '3', '配制常用消毒液', 1, 1, 1, '配制常用消毒液\n\n步骤1 工作准备\n(1)护理人员准备。护理人员着装整洁，洗手，戴口罩，必要时戴手套。\n(2)物品准备。准备清水、消毒剂（或消毒液原液）适量及塑料桶1个。\n(3)安置老年人。安置老年人离开房间或远离消毒液，坐好或躺好。\n(4)环境准备。环境清洁宽敞、干燥平坦，停止清扫工作，减少走动，避免尘埃飞扬。\n(5)检查物品。\n1)检查水桶有无裂痕、破损，大小是否合适\n2)检查消毒片（剂）是否在有效期内。\n\n步骤2 量取清水\n按所配制浓度要求用量杯量取所需量的清水并倒人桶内。\n\n步骤3 放入消毒片（剂）\n按所配制浓度要求将所需消毒片（剂）放入桶内\n\n步骤4 搅拌混匀搅拌液体，将消毒片完全溶解于清水中，并混匀。\n\n步骤5 标注\n在桶盖上注明消毒液名称、浓度及配制时间。\n\n步骤6 盖好桶盖\n盖上桶盖备用。\n', '1 工作准备\n(1)护理人员准备。护理人员着装整洁，洗手，戴口罩，必要时戴手套。\n(2)物品准备。准备清水、消毒剂（或消毒液原液）适量及塑料桶1个。\n(3)安置老年人。安置老年人离开房间或远离消毒液，坐好或躺好。\n(4)环境准备。环境清洁宽敞、干燥平坦，停止清扫工作，减少走动，避免尘埃飞扬。\n(5)检查物品。\n1)检查水桶有无裂痕、破损，大小是否合适\n2)检查消毒片（剂）是否在有效期内。\n\n步骤2 量取清水\n按所配制浓度要求用量杯量取所需量的清水并倒人桶内。\n\n步骤3 放入消毒片（剂）\n按所配制浓度要求将所需消毒片（剂）放入桶内\n\n步骤4 搅拌混匀搅拌液体，将消毒片完全溶解于清水中，并混匀。\n\n步骤5 标注\n在桶盖上注明消毒液名称、浓度及配制时间。\n\n步骤6 盖好桶盖\n盖上桶盖备用。');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a336e009-ef1c-11ed-a0ab-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3309ec0-ef1c-11ed-b9d8-8c554aa628fd', '2', '用药照料', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3372e3b-ef1c-11ed-b50d-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '为I度压疮的老年人提供处理措施', 1, 1, 0, '\n1.防止局部长期受压。对有头发遮挡的枕骨粗隆，耳郭背面，吸氧面罩、胃管部分压迫的不易观察到的部位的皮肤要特别注意，应拨开老年人的头发认真检查。\n2.护理过程中防止手表、指甲划伤老年人的皮肤。对老年人的手脚指（趾）甲应常修剪，以防自伤。便器等护理用具完好，避免刮伤、蹭伤皮肤。\n3.鼓励老年人尽量做力所能及的活动，如下床、关节自主运动等，以促进静脉回流，起到预防压疮的作用。\n\n', '1 准备工作\n(1)护理员衣帽整洁、洗净双手，掌握老年人的病情和压疮发生的原因。\n(2)环境清洁，温湿度适宜，必要时关闭门窗，注意遮挡。\n(3)用品准备包括：温水、毛巾、脸盆、体位垫、干净被服、衣裤、翻身记录。\n(4)老年人准备：护理员与老年人沟通，讲明操作的目的和意义并取得老年人的合作。老年人取适当的卧位，暴露压疮部位。\n\n步骤2 评估原因\n护理员松开盖被，观察压疮部位，询问老年人的不适感觉，明确引起I度压疮的原因。\n\n步骤3 采取措施\n护理员根据评估的结果选用不同的措施，观察和询问老年人是否舒适及体位保持情况。\n\n步骤4 整理用物\n护理员整理好床单位，协助老年人穿好衣裤，避免皱褶，发现潮湿时应及时更换，洗净双手。\n\n步骤5 记录\n护理员应随时记录压疮部位的大小、皮肤颜色及变化的情况。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3375538-ef1c-11ed-bbd8-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '为老年人上眼药膏', 1, 1, 0, '\n白天宜用滴眼剂，临睡前可用眼药膏涂敷，这样不影响生活且药物附着眼壁时间长，可维持有效浓度。其他注意事项同滴眼药水。', '1 准备工作\n(1)护理员衣帽整洁并洗净双手。\n(2)物品准备包括：给药单、眼药膏、消毒棉球、棉签、干净纸、污物杯。\n\n步骤2 评估沟通\n(1)护理员向老年人解释操作的目的和配合的方法。\n(2)护理员应评估老年人的身体状况以确认是否可以进行上眼药膏的操作。\n\n步骤3 核对\n携用物至老年人床旁，检查核对老年人姓名。核对药品名称、给药途径、用法、给药时间、药品质量和有效期。确认是左、右眼还是双眼上眼药膏。\n\n步骤4 清洁眼部\n协助老人取仰卧位或坐位，先用棉签拭净眼部分泌物，嘱老人头略后仰，眼往上看，拔开瓶塞，应将瓶塞侧面或瓶塞口向上，最好将其置放于一张干净纸或器皿上。\n\n步骤5 上眼药膏\n护理员左手（或用干净棉签）向下轻轻拉下老年人的眼脸并固定，将眼药膏瓶口垂直向下，并用手轻轻地挤出些许眼药膏，挤出的眼药膏呈一条细直线状。从外眼角方向顺眼裂水平挤在下脸结膜与眼球结膜交界处（即下穹窿)。先使下脸恢复原位，再轻提上眼脸，使结膜囊内充盈眼药膏。\n\n步骤6 闭眼吸收\n(1)滴药完毕，嘱老人闭上眼晴，轻轻转动眼球，用消毒棉签为老年人拭去眼部外溢眼药膏，将用过的消毒棉签放人污物杯内。\n(2)询问、观察老年人有无不适。\n\n步骤7 整理用物\n(1)护理员协助老年人取舒适卧位\n(2)整理用物，处理污物，洗手，记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a337a35a-ef1c-11ed-9f27-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '为老年人应用滴耳剂', 1, 1, 0, '\n1.老年人耳聋、耳道不通或耳膜穿孔时，不应使用滴耳剂。\n2.滴药后嘱老年人保持原体位1~2分钟，以利于药物吸收。\n\n', '1 准备工作 \n(1)护理员衣帽整洁，洗净双手。\n(2)物品准备包括滴耳药液、消毒棉球、棉签、污物杯。\n\n步骤2 评估沟通\n(1)护理员向老年人解释操作的目的和配合的方法，以取得老年人的配合。\n(2)护理员应评估老年人的身体状况，以确认是否可以进行滴耳的操作。\n\n步骤3 核对\n携用物至老年人床旁。检查核对姓名，核对药品名称、给药途径、用法、给药时间、药品质量和有效期。确认是左、右耳还是双耳道滴药。\n\n步骤4 清洁耳道\n护理员协助老年人取坐位或半卧位，头偏向一侧，使患侧耳在上，健侧耳在下。用棉签将耳道内分泌物反复清洗至干净，用干棉签拭干。\n\n步骤5 滴入药液\n护理员用左手将老人耳廓向后上方轻轻牵拉，使耳道变直，用右手持药液瓶，将掌跟轻置于耳旁。将药液沿耳道后壁滴人耳道内5~10滴（或遵医嘱)。\n\n步骤6 轻揉耳郭\n(1)护理员协助老年人轻轻压住耳屏，使得药液充分进入中耳，或用消毒棉球塞入外耳道口，以避免药液流出。\n(2)询问并观察老年人有无不适。\n\n步骤7 整理用物\n协助老人取舒适位。清理用物，处理污物，洗手，记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a337cbc9-ef1c-11ed-99e6-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '为老年人应用滴鼻剂', 1, 1, 0, '\n1.如果老年人鼻腔内有干痂，应先用温盐水清洗浸泡，待干痂变软取出后再滴药。\n2.滴药后保持仰卧位1~2分钟，以利于药物吸收。\n3.如果药液流人口腔，可叮嘱老年人将其吐出。\n', '1 准备工作\n(1)护理员衣帽整洁并洗净双手。\n(2)物品准备。 包括：给药单、滴鼻剂、消毒棉球、棉签、污物杯。\n\n步骤2 评估沟通\n(1)护理员向老年人解释操作的目的和配合的方法，以取得老年人的配合。\n(2)护理员应评估老年人的身体状况，以确认是否可进行滴鼻操作。\n\n步骤3 核对\n携用物至老年人床旁。检查核对姓名，核对药品名称、给药途径、用法、给药时间、药品质量和有效期。确认是左、右鼻腔还是双鼻腔滴药。\n\n步骤4 清洁鼻腔\n滴药前，护理员先协助老年人将鼻涕等分泌物排出，并擦拭干净。\n\n步骤5 滴入药液\n护理员协助老年人平卧位，头尽量向后仰。嘱附老年人先吸气，然后滴人药液2~3滴。注意滴鼻剂瓶口不要碰到鼻黏膜，\n\n步骤6 轻揉鼻翼\n滴药完毕，护理员以手轻轻地揉按鼻翼两侧，使药液能均匀地渗到鼻黏膜上并询问并观察老年人有无不适。\n\n步骤7 整理用物\n护理员协助老人取舒适卧位。清理用物，处理污物，洗手，记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3381893-ef1c-11ed-9c85-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '为老年人滴眼药水', 1, 1, 0, '\n1.护理员使用滴眼液前应先混匀药液。\n2.滴眼药水时动作应轻柔，避免损伤黏膜。\n3.滴眼液的保存应参照相关说明执行，需要时放入冰箱保存。', '1 准备工作\n(1)护理员衣帽整洁，修剪指甲、洗手、戴口罩。\n(2)用物包括：给药单、眼药水、干净纸、棉签、污物杯。\n\n步骤2 评估沟通平\n(1)护理员向老年人解释操作的目的和配合的方法，以取得老年人的合作。\n(2)护理员应评估老年人的身体状况，以确认是否可进行滴眼药水的操作。\n\n步骤3 核对\n护理员带着用物至老年人床旁。\n(1)检查核对老年人姓名，核对药瓶上的姓名、药品名称、给药途径、用法、给药时间、药品质量和有效期。\n(2)确认是滴左、右眼还是双眼滴药。\n\n步骤4 清洁眼部\n(1)护理员协助老年人取仰卧位或坐位，先用棉签拭净眼部分泌物，嘱老人头略后仰，眼往上看\n(2)拔开瓶塞，应将瓶塞侧面或瓶塞口向上，最好将其置放于一张干净纸或器皿上。\n\n步骤5 悬滴药液\n(1)护理员左手（或用干净棉签）向下轻轻拉下老年人的眼脸并固定。\n(2)右手持眼药水瓶、摇匀，距眼约2~3厘米将眼药水滴人下结膜内1~2滴。\n(3)轻提上眼脸，使结膜囊内充盈药液。\n\n步骤6 闭眼吸收\n护理员嘱咐老年人轻轻闭上眼，轻轻转动眼球，用消毒棉签为老年人拭去眼部外溢药液，作为医疗垃圾放入污物杯中。\n\n步骤7 整理用物\n(1)滴药完毕，整理用物，协助老人取舒适卧位。\n(2)观察、询问老年人无不适后离开。\n(3)清理用物，处理污物，洗手，记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3383fa0-ef1c-11ed-9316-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '为老年人进行氧气雾化吸入', 1, 1, 0, '\n1.严格执行查对制度。\n2.雾化吸人器应专人专用。\n3.用氧过程中注意安全，严禁接触烟火及易燃品。\n4.雾化吸入过程中注意检查各连接有无松动、脱落等异常情况。\n5.若老年人感觉疲劳，可休息片刻再进行吸入。', '1 准备工作\n(1)护理员衣帽整洁，并洗净双手。\n(2)物品准备，包括：雾化吸入器1套、氧气桶或管道氧、药液、注射器、毛巾。\n(3)环境整洁，光线充足，温湿度适宜，无烟火及易燃品。\n\n步骤2 评估沟通\n(1)护理员向老年人详细介绍操作的目的和配合的方法。\n(2)护理员应评估老年人的年龄、病情、意识状态，有无呼吸困难、咳嗽等情况，以确定是否可进行雾化吸入。\n\n步骤3 核对加药\n(1)护理员核对治疗单上的姓名、药品的用法、给药途径、给药时间，并检查药品质量。\n(2)按医嘱抽药液，用蒸馏水稀释或溶解药物在5毫升以内，注人雾化器。\n\n步骤4 连接氧源\n(1)连接雾化吸入器至氧源，注意各部位连接紧密，勿漏气。\n(2)调节氧流量为6~8升/分钟，注意氧气湿化瓶内不放水。\n\n步骤5 实施雾化\n(1)护理员协助老年人取坐位或半坐卧位。指导老年人自己手持雾化器，将吸气管放入口中，紧闭口唇，深吸气，吸气后再屏气1一2秒：用鼻子呼气，如此反复直到药液吸完为止。\n(2)吸毕，取下雾化器，关闭氧源，嘱老年人漱口。\n(3)在氧气雾化吸人过程中，及时协助老年人翻身、叩背、咳痰。随时注意观察老年人的呼吸情况，有无呼吸困难、能否耐受雾化吸人等，若发现异常应立即停止雾化吸入，并及时报告医护人员。\n\n步骤6 整理用物\n协助老年人躺卧舒适，清理用物，洗手，做好记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3388db8-ef1c-11ed-8236-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '为老年人进行超声波雾化吸入', 1, 1, 0, '\n1.严格执行查对制度。\n2.口含嘴、面罩、螺旋管路应专人专用。\n3.因为电晶片和透声膜易损坏，使用时动作要轻柔。\n4.正确控制水温，水槽和雾化罐内切忌加温水或热水。水槽内无水时，不可开机，以免损坏机器。水槽内须保持有足够的冷蒸馏水，如发现水温超过50℃或水量不足，应关机，更换或加入冷蒸馏水。\n5.连续使用超声波雾化吸入器时，两次之间应间隔30分钟。', '1 准备工作\n(1)护理人员衣帽整洁，洗净双手。\n(2)物品准备：超声波雾化吸入器1套，检查超声波雾化吸人香性能，连接各部件，关闭所有开关，冷蒸馏水（凉开水）250~300毫升、药液量杯、注射器、毛巾；必要时备接线板。\n\n步骤2 评估沟通\n(1)护理员向老年人详细介绍操作的目的和配合的方法。\n(2)护理员应评估老年人的年龄、病情、意识状态，有无呼吸困难、咳嗽等情况，以确定是否可以进行雾化吸入。\n\n步骤3 核对加药\n(1)护理员向雾化器水槽加冷蒸馏水至浸没雾化罐底部的透声膜。\n(2)遵医嘱配好药液，放入雾化罐内，将罐盖旋紧，把雾化罐放人水槽中，将水槽盖盖紧。\n\n步骤4 实施雾化\n(1)护理员携用物至老年人床边，核对姓名。\n(2)协助老年人取坐位或半坐卧位，毛巾围于颌下。\n(3)接通电源，先开电源开关，红色指示灯亮，预热3分钟。\n(4)调整定时开关至所需时间，一般为15~20分钟；将面罩覆盖于老年人口鼻部，或将口含嘴放入老年人口中。打开雾化开关，调节雾量：高挡3毫升/分钟、中挡2毫升/分钟、低挡1毫升/分钟，一般采用中挡或根据老年人的耐受情况适当调节。嘱老年人深吸气，呼气时拿开面罩，如此反复，至药液全部喷完。\n(5)雾化过程中护理员应密切观察老年人有无病情变化，如呼吸困难、口唇甲床紫绀等。如老年人感到疲劳，可关闭雾化器，休息片刻再进行雾化吸入。有痰时应协助老年人排出。\n\n特别提示 在使用雾化吸入器的过程中，护理员应注意各连接部件有无松动、脱落。雾化罐上开口应完全罩住。\n\n(6)吸入完毕，取下口含嘴或面罩，先关雾化开关，再关电源开关。协助老年人漱口，用毛巾擦干老年人面部。\n\n步骤5 整理用物\n(1)协助老年人取舒适卧位，整理床单位。\n(2)清理用物，倒掉水槽内水，擦干水槽。将口含嘴、面罩、螺旋管路放人消毒液中浸泡30分钟，然后再清洁、晾干、物归原处。\n(3)洗手，做好记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a338dbd9-ef1c-11ed-8331-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a336e009-ef1c-11ed-a0ab-8c554aa628fd', '3', '查对并帮助老年人服药', 0, 1, 0, '\n1.遵照医嘱协助老年人服药，不得私自加、减药物或停药。\n2.老年人对药品有疑问时，需要再次核对无误方能给药，并要向老年人解释说\n3.用药后发现异常，应及时报告医护人员或协助就诊。\n4.对于吞咽困难的老年人，护理员要咨询医护人员或根据药物的说明书，决定是否可以将药物切割成小块或研碎服用。\n5.协助精神疾患老年人服药，要要求其张口，检查药物是否全部咽下。\n\n', '1 工作准备\n(1)环境淮备。环境整洁，温湿度适宜，安静，光线明亮。\n(2)护理员准备。护理员衣着整齐，洗净双手。\n(3)老年人准备。老年人取舒适体位。\n(4)物品准备。药杯内盛装药物、温开水、服药单。根据需要情况准备量杯、汤匙、滴管等。\n\n步骤2 协助服药\n(1)核对\n1)核对老年人的姓名和服药单是否相符。核对药物与服药单是否相符。\n2)根据药量为老年人倒好温水，按照2~4片（粒）药物备100毫升计算。药物片（粒）数较多时，按照2~4片（粒）/次，分次服下。\n\n(2)协助不同身体状况老年人服药\n1)自理老年人。护理员将药杯递给老年人，告诉老年人先饮一小口水润滑咽喉，再看着老年人将药物服下。\n2)不能自理老年人。护理员告知老年人准备服药，协助老年人取半坐位，即摇高床头或在老年人后背垫靠棉被或靠垫支撑身体。用汤匙或吸管先喂一小口水，将药物放入老年人口中，再用汤匙或吸管协助老人饮水将药物服下。保持体位30分钟，再协助老年人取舒适卧位。\n\n步骤3 整理用物\n护理员将水杯放回原处，整理床单位。药杯收回，浸泡消毒，清洗晾干备用。\n\n步骤4 观察记录\n护理员根据已知老年人服用的药物的作用及不良反应，观察并询问老年人服药后情况，记录服药后的表现。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33902e6-ef1c-11ed-b19c-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3309ec0-ef1c-11ed-b9d8-8c554aa628fd', '2', '遗体照料', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3395115-ef1c-11ed-8db0-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33902e6-ef1c-11ed-b19c-8c554aa628fd', '3', '整理遗物', 0, 1, 0, '\n1.老年人遗物需两人同时在场清点。贵重物品，先行记录并由主管领导保管。\n2.遗物清单至少留存一年。\n', '1 工作准备\n(1)环境准备。光线充足。\n(2)护理员准备。护理员2名，服装整洁，洗净双手，戴手套、口罩。\n(3)物品准备。记录单1份，笔1支。\n\n步骤2 遗物分类\n护理员两人以上将老年人遗物分类放置并清点。\n\n步骤3 记录\n一人整理遗物并读出物品名称。一人洗净双手记录。护理员双人签全名。\n\n步骤4 遗物交接\n护理员将遗物与家属核对并交接，家属在记录单上签全名。记录单保存一年。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3397819-ef1c-11ed-ad7a-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33902e6-ef1c-11ed-b19c-8c554aa628fd', '3', '遗体料理', 0, 1, 0, '\n1.老年人死亡后，遵照家属意愿进行或协助进行尸体清洁工作。\n2.填塞七窍时，避免填塞物外露。\n\n', '1 工作准备\n(1)环境准备。环境清洁，安静。\n(2)护理员准备。服装整洁，戴口罩、手套。\n(3)物品准备。毛巾1条、水盆1个（盛装温水，传染性疾病尸体使用500毫克/升含氯消毒液)、大单1条、屏风1个。\n\n步骤2 与家属沟通\n携用物至老年人床旁。向家属解释，劝其离开房间，用屏风遮挡。\n\n步骤3 擦拭遗体\n为死者撤除各种治疗用物，闭合双眼，用温水毛巾清洁遗体，填塞七窍，更换清洁衣裤，梳理头发。\n\n步骤4 覆盖大单\n将大单盖于遗体上，露出头部。\n\n步骤5 整理用物 \n护理员倾倒水盆，右手捏住左手手套外面脱下，左手伸进右手手套内侧面翻转手套脱下右手手套，双手捏住口罩带子摘下口罩，装入医用黄色垃圾袋内。请家属向遗体告别。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a339c638-ef1c-11ed-ba54-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', '', '1', '康复护理', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a339ed49-ef1c-11ed-a4f2-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a339c638-ef1c-11ed-ba54-8c554aa628fd', '2', '功能锻炼', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33a1459-ef1c-11ed-a111-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a339ed49-ef1c-11ed-a4f2-8c554aa628fd', '3', '帮助老年人进行穿脱衣服训练', 1, 1, 0, '\n1.在进行训练时护理员可将复杂的动作分解成若千单一动作，循序渐进，持之以恒。\n2.依据老年人每日训练的实际情况适当给予协助，但不可催促，不能替代。', '1 准备工作\n(1)环境整洁，温度为18~22℃，湿度为50%~60%为宜，同时应根据季节变化调整。\n(2)老年人有意愿且身体状况许可。\n(3)护理员清洗双手，保持双手温暖。\n(4)物品准备。包括：干净、合适的衣裤鞋袜。必要时备保暖用的小毛毯和披肩。\n\n步骤2 评估\n(1)护理员与老年人及家属充分沟通，了解老年人是否卧床不起，能否坐起，肢体活动情况，肢体偏瘫的程度等。\n(2)护理员应全面评估老年人的身体状况，自理程度，生活习惯等。\n\n步骤3 确立训练目标\n与老年人沟通，确定可以达到的训练目标。根据老年人的肢体功能和个人喜好选择适合老年人的衣裤鞋袜。\n\n步骤4 示范\n护理员耐心向老年人解释每一步操作的要点，并给老年人示范。\n\n步骤5 进行训练\n护理员根据选择衣服的类型按以上方法训练老年人。\n(1)脱衣服时，护理员站在老年人的健侧，先帮老年人脱健侧衣袖；训练老年人自己脱患侧的衣袖，必要时给予协助，并注意随时用毛毯为老年人保暖。\n(2)穿衣服时护理员应站在老年人的患侧，先帮老年人穿患侧衣袖，训练老年人自己穿健侧的衣袖，必要时给予协助。\n\n特别提示 帮助老年人穿脱衣服训练时动作应轻柔，避免暴力对老年人造成损伤。\n\n步骤6 观察\n训练过程中护理员要随时询问老年人的感受，经常鼓励老年人，及时发现老年人的每一点进步。观察老年人有不适时应立即停止训练。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33a627f-ef1c-11ed-b222-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a339ed49-ef1c-11ed-a4f2-8c554aa628fd', '3', '帮助老年人进行站立、行走活动', 1, 1, 0, '\n1.要依据老年人的情况制定行走计划，循序渐进，持之以恒。\n2.老年人在完成站立行走训练时要分步进行，不可操之过急，应做到：醒来后要在床上躺半分钟，起来后要在床上坐半分钟，两条腿下垂后坐在床边等半分钟后再行走。\n3.护理员在协助老年人进行站立、行走活动时应根据老年人的身体状况选择适宜的转移方法，避免对自己的腰椎、颈椎造成损伤。\n\n', '1 准备工作\n(1)老年人有意愿且身体状况许可。\n(2)护理员和蔼耐心，尊重老年人意愿，并掌握老年人的活动能力。护理员应全面评估老年人的身体状况、疾病的程度等。与老年人及家属充分沟通，了解老年人的生活习惯。\n(3)环境适宜，保证安全。地面平整、无障碍物，温湿度适宜。\n(4)准备物品，包括毛巾、座椅、坐垫等。根据老年人情况准备拐杖、步行车、轮椅等。\n\n步骤2 沟通\n护理员应先向老年人解释说明要进行的动作，明确动作的步骤，以取得老年人配合。\n\n步骤3 协助坐起\n协助老年人在床边坐好（具体操作参见本章第1节）。\n\n步骤4 协助站立\n护理员根据老年人的身体情况选择适宜的站起方法，确认老年人已经站稳后注意保护老年人，避免跌倒。\n\n步骤5 协助行走\n(1)护理员根据老年人的身体状况协助老年人行走。\n(2)若老年人体重较重或配合能力较差，也可以用自制保护性腰带或购买协助行走的保护性腰带以保护老年人行走。\n(3)行走过程中护理员应根据老人的情绪状态与其闲谈，使其心情舒畅。经常询问老年人有何不适，观察到有气喘、心慌等异常状况时应立即休息。\n\n步骤6 反馈\n每次活动后护理员要积极主动地与老年人沟通活动感受，观察老年人的食欲、睡眠情况是否改善等，根据情况安排下一次锻炼。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33ab0a2-ef1c-11ed-a681-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a339ed49-ef1c-11ed-a4f2-8c554aa628fd', '3', '帮助压力性尿失禁老年人进行功能训练', 1, 1, 1, '\n1.依据老年人的情况选择适合的康复训练方法。\n2.护理员要尊重、理解尿失禁的老年人。\n3.护理尿失禁的老年人应注意保持皮肤的清洁、干燥，预防压疮的发生。\n4.教育、鼓励老年人正确对待尿失禁，主动参与、积极配合尿失禁的训练。\n\n', '1 工作准备\n(1)全面评估老年人的身体状况及意愿。\n(2)了解老年人排尿的实际间隔时间，每天的饮水量等情况\n(3)与老年人及家属充分沟通。\n(4)准备纸尿裤、集尿器、干净衣服等。\n\n步骤2 制定训练方案\n根据老年人的情况和医院医生的康复计划制定适合老年人的康复训练方案，内容包括要达到的目标、每天的训练内容、每天的饮水量等。\n\n步骤3 进行康复训练\n(1)按时给老年人饮水。\n(2)定时协助老年人排尿。\n(3)一旦老年人发生尿失禁的情况，立即协助老年人更换干净的衣服、床单，并准确记录排尿时间和尿量。\n\n步骤4 观察记录\n(1)在训练过程中随时观察老年人的反应。\n(2)发现异常立即停止。\n(3)与老年人沟通功能训练的效果。\n(4)发现问题及时改进。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33ad7a7-ef1c-11ed-88d4-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a339ed49-ef1c-11ed-a4f2-8c554aa628fd', '3', '帮助肢体障碍老年人进行功能训练', 1, 1, 1, '\n1.依据老年人的肢体障碍情况选择不同的康复训练项目。\n2.肢体障碍老年人的康复训练要有计划性、规律性，并持之以恒。\n3.护理员要尊重、理解肢体障碍的老年人，鼓励老年人和家属主动参与、积极配合训练。\n\n', '1 工作准备\n(1)护理员全面评估老年人的身体状况，疾病的程度等。\n(2)与老年人及家属充分沟通，了解老年人的个性特点等。\n\n步骤2 制定训练方案\n根据老年人的情况和医院医生的康复计划制定适合老年人的康复训练方案，内容包括要达到的目标、每天的训练内容、训练时间等。\n\n步骤3 进行康复训练\n(1)以缓慢温和的语速告诉老年人每一项操作的步骤。\n(2)把每一步的具体动作加以分解。\n(3)反复示范指导老年人练习。\n(4)得到老年人的反馈后再开始下一步动作。\n\n步骤4 观察记录\n(1)康复训练过程中护理员要随时观察老年人的反应。\n(2)发现异常立即停止。\n(3)老年人的表现有进步时要及时给予鼓励，并适当奖励。\n(4)与老年人沟通康复训练的感受。\n(5)及时记录老年人训练的情况。\n(6)发现问题及时改进。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33b2612-ef1c-11ed-99bb-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a339c638-ef1c-11ed-ba54-8c554aa628fd', '2', '康乐活动', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33b4cd7-ef1c-11ed-8baf-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '帮助老年人从仰卧位至侧卧位的体位转换', 1, 1, 0, '帮助老年人从仰卧位至侧卧位的体位转换\n\n步骤1 准备工作\n(1)老年人有意愿且身体状况许可。\n(2)护理员衣着整洁、言语和蔼。全面评估老年人的身体状况、疾病的程度等。与老年人及家属充分沟通，了解老年人的生活习惯。\n(3)环境整洁，关闭门窗，温度适宜。\n(4)用物准备。包括软枕或体位垫、浴巾，需要时备床单、衣服、尿垫等。\n\n步骤2 沟通\n在转换体位前护理员应先向老年人解释说明要进行的动作，明确步骤，取得老年人的配合。\n\n步骤3 协助翻身\n(1)护理员将老年人的头偏向一侧，嘱咐老年人双手交叉放于胸腹部，双下肢伸直或双膝屈曲，双足支撑于床面上。\n(2)护理员站在老年人需转向病床的一侧，嘱附老年人将身体移向远侧，护理员一手扶托老年人的肩部，另一手扶托老年人的膝盖部。将老年人双膝倒向一侧，背部也偏向一侧，呈侧卧位。用软枕或体位垫支撑老年人背部，必要时在膝下、手肘处垫小的软枕。使老年人躺卧舒适后，整理好床单位。\n\n步骤4 观察\n体位转换过程中，护理员应随时询问老年人的感受，注意观察老年人的面色、表情，一旦出现异常应立即停止操作，恢复先前的体位。', '1 准备工作\n(1)老年人有意愿且身体状况许可。\n(2)护理员衣着整洁、言语和蔼。全面评估老年人的身体状况、疾病的程度等。与老年人及家属充分沟通，了解老年人的生活习惯。\n(3)环境整洁，关闭门窗，温度适宜。\n(4)用物准备。包括软枕或体位垫、浴巾，需要时备床单、衣服、尿垫等。\n\n步骤2 沟通\n在转换体位前护理员应先向老年人解释说明要进行的动作，明确步骤，取得老年人的配合。\n\n步骤3 协助翻身\n(1)护理员将老年人的头偏向一侧，嘱咐老年人双手交叉放于胸腹部，双下肢伸直或双膝屈曲，双足支撑于床面上。\n(2)护理员站在老年人需转向病床的一侧，嘱附老年人将身体移向远侧，护理员一手扶托老年人的肩部，另一手扶托老年人的膝盖部。将老年人双膝倒向一侧，背部也偏向一侧，呈侧卧位。用软枕或体位垫支撑老年人背部，必要时在膝下、手肘处垫小的软枕。使老年人躺卧舒适后，整理好床单位。\n\n步骤4 观察\n体位转换过程中，护理员应随时询问老年人的感受，注意观察老年人的面色、表情，一旦出现异常应立即停止操作，恢复先前的体位');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33b9af8-ef1c-11ed-afb0-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '帮助老年人从仰卧位至坐位的体位转换', 1, 1, 0, '帮助老年人从仰卧位至坐位的体位转换\n\n步骤1 准备工作\n(1)老年人有意愿且身体状况许可。\n(2)护理员衣着整洁、言语和蔼。全面评估老年人的身体状况、疾病的程度等。与老年人及家属充分沟通，了解老年人的生活习惯。\n(3)环境整洁，关闭门窗，温度适宜。\n(4)用物准备。包括软枕或体位垫、浴巾，需要时备床单、衣服、尿垫等。\n\n步骤2 沟通\n护理员在转换体位前先向老年人解释说明要进行的动作，明确动作的步骤，取得老年人的配合。\n\n步骤3 协助坐起\n(1)护理员指导老年人双上肢置于身体两侧，双侧肘关节屈曲支撑于床面上，注意保护老年人。\n(2)护理员站在老年人侧前方，以双手扶托老年人双肩并向上牵拉。指导老年人利用双肘的支撑抬起上部躯干后，逐渐改用双手支撑身体而坐起。调整坐姿，使老年人舒适。\n\n步骤4 协助躺下\n(1)老年人取坐位，用双手支撑于床面，逐渐改用双侧肘关节支撑身体，使身体缓慢向后倾倒。\n(2)护理员站与老年人侧前方，以双手扶托老年人双肩以保持老年人向后倾倒速度，缓慢完成从坐位到仰卧位的体位转换，调整卧姿保持老年人的舒适卧位。\n(3)整理床铺，保持床单、衣服无皱褶，为老年人盖好被子。\n\n步骤5 观察\n体位转换过程中护理员应随时询问老年人的感受，是否有头晕、恶心等不适，注意观察老年人的面色、表情，一旦出现异常应立即停止操作，恢复先前的体位。', '1 准备工作\n(1)老年人有意愿且身体状况许可。\n(2)护理员衣着整洁、言语和蔼。全面评估老年人的身体状况、疾病的程度等。与老年人及家属充分沟通，了解老年人的生活习惯。\n(3)环境整洁，关闭门窗，温度适宜。\n(4)用物准备。包括软枕或体位垫、浴巾，需要时备床单、衣服、尿垫等。\n\n步骤2 沟通\n护理员在转换体位前先向老年人解释说明要进行的动作，明确动作的步骤，取得老年人的配合。\n\n步骤3 协助坐起\n(1)护理员指导老年人双上肢置于身体两侧，双侧肘关节屈曲支撑于床面上，注意保护老年人。\n(2)护理员站在老年人侧前方，以双手扶托老年人双肩并向上牵拉。指导老年人利用双肘的支撑抬起上部躯干后，逐渐改用双手支撑身体而坐起。调整坐姿，使老年人舒适。\n\n步骤4 协助躺下\n(1)老年人取坐位，用双手支撑于床面，逐渐改用双侧肘关节支撑身体，使身体缓慢向后倾倒。\n(2)护理员站与老年人侧前方，以双手扶托老年人双肩以保持老年人向后倾倒速度，缓慢完成从坐位到仰卧位的体位转换，调整卧姿保持老年人的舒适卧位。\n(3)整理床铺，保持床单、衣服无皱褶，为老年人盖好被子。\n\n步骤5 观察\n体位转换过程中护理员应随时询问老年人的感受，是否有头晕、恶心等不适，注意观察老年人的面色、表情，一旦出现异常应立即停止操作，恢复先前的体位');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33bc205-ef1c-11ed-b18b-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '帮助老年人从仰卧位至床边坐起的体位转换', 1, 1, 0, '\n1.对留置有输液管路、导尿管等管路的老年人应先将导管妥善安置固定，翻身后要检查导管，确保畅通。\n2.从卧位转换成坐位时，护理员动作一定要缓慢，特别是对长时间卧床的老年人最好分步完成。\n3.体位转换时注意观察老年人受压部位皮肤情况。\n4.使用床挡的老年人体位转换后应及时恢复床挡保护，保证老年人的安全。\n5.为了发挥老年人的潜在能力，不要过多地为其提供帮助，而要善于鼓励并耐心等待，多让老年人自己去做。\n6.对不能配合或体重较大的老年人应同时有两个护理员配合完成体位转换。\n', '1 准备工作\n(1)老年人有意愿且身体状况许可。\n(2)护理员衣着整洁，言语和蔼。全面评估老年人的身体状况、疾病的程度等。与老年人及家属充分沟通，了解老年人生活习惯。\n(3)环境整洁，关闭门窗，温度适宜。\n(4)用物准备包括软枕或体位垫、浴巾，需要时备床单、衣服、尿垫等。\n\n步骤2 沟通\n在转换体位前先向老年人解释说明要进行的动作，明确动作的步骤，以取得老年人配合。\n\n步骤3 协助至侧卧位\n（同“技能要求1”）\n\n步骤4 协助床边坐起\n护理员站在老年人需转向的一侧，协助老年人转向侧卧位（健侧或患侧），两膝屈曲，协助老年人将双下肢垂放到床边，然后用双手托住老年人肩部并向上牵拉，同时嘱咐老年人向上侧屈拾头，用一侧上肢支撑身体，以骨盆为枢纽用力使身体转换成坐位。\n\n步骤5 协助躺下\n(1)从床边坐位转换到仰卧位时，护理员双手托住老年人肩部，缓慢让老年人控制身体向床上倾倒后，协助老年人将双下肢移至床上。\n(2)调整卧姿使老年人舒适，保持舒适卧位。整理床铺，保持床单、衣服无皱褶，为老年人盖好被子。\n\n步骤6 观察\n体位转换过程中护理员应随时询问老年人的感受，是否有头晕、恶心等不适状况，注意观察老年人的面色、表情，一旦出现异常应立即停止操作，恢复先前的体位。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33c1073-ef1c-11ed-aaa5-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '教老年人使用健身器材', 1, 1, 0, '。\n\n步骤3 带领热身\n带领老年人一起做好准备活动，如伸展、弯腰、下蹲等。热身10~15分钟左右。\n\n步骤4 示范辅助\n(1)护理员分步骤为老年人示范健身器材的使用方法，所选用器材的使用注意事项要反复强调。\n(2)护理员协助老年人使用健身器材进行锻炼。锻炼过程中要注意保护老年人安全，随时观察老年人的活动状况，发现异常应立即停止活动。\n(3)协助老年人完成10分钟的整理活动。\n\n步骤5 反馈\n(1)活动结束后护理员应与老年人交流健身器材使用的感受，观察老年人的食欲、睡眠情况是否改善等。\n(2)根据情况安排下一次的锻炼。\n\n注意事项\n1.锻炼时间以30分钟至1小时为宜。\n2.健身前后的热身活动要做到位。\n3.使用健身器材时，护理员应随时观察老年人的情况，确保安全。\n\n', '1 准备工作\n(1)检查活动环境是否安全，地面是否平整、有无积水，气候是否适宜。\n(2)检查健身器材是否完好。\n(3)协助老年人衣着适宜，穿防滑鞋。\n\n步骤2 沟通\n(1)护理员与老年人交谈，全面了解老年人的身体状况、疾病的程度等。了解老年人既往健身器材的使用情况、活动能力、活动时间等信息。\n(2)向老年人讲解健身器材的作用和使用');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33c5e45-ef1c-11ed-8f89-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '为老年人示范娱乐游戏活动', 0, 1, 0, '\n1.游戏活动的设计要充分考虑老年人的能力。\n2.在活动过程中要注意观察老年人的变化。\n3.合理安排时间，避开老年人的休息时间。\n4.在示范过程中随时观察老年人的反应，如果老年人出现厌烦、身体不舒服，应立即停下来休息。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜，场地适宜、安全。\n(2)护理员准备。服装整洁，全面了解参加活动老年人的身体状况、生活习惯、爱好等内容。熟悉即将开展的活动规则。\n(3)老年人准备。身体状况允许，如糖尿病、高血压等疾病的恢复期，自愿参与。\n(4)物品准备。根据活动内容确定。\n\n步骤2 设计\n护理员根据老年人身体状况设计适宜的活动项目，要求简单易学，老年人力所能及，愿意参与。\n\n步骤3 示范\n(1)护理员首先大声、清晰、慢速地讲解游戏规则，确保每个老年人都听清楚、明白。\n(2)护理员认真示范娱乐游戏过程。必要时重复示范。\n(3)看护老年人开始娱乐游戏活动，态度要和蔼真诚，必要时给予帮助。随时观察、询问老年人的感受，如有不适，需搀扶退出活动。\n\n步骤4 总结\n活动结束，护理员对活动进行总结并征求老年人对活动的意见和建议，找出活动中存在的问题，使今后开展的活动更符合老年人的兴趣爱好。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33c8555-ef1c-11ed-b832-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '教老年人手工活动', 0, 1, 0, '\n1.选择活动用具时要符合老年人的特点，保证安全。\n2.在活动过程中要多使用鼓励性语言。\n3.护理员安排手工活动时间时，要避开老年人的休息时间。\n4.老年人在活动中出现厌烦、身体不舒服时，应立即停止，协助老年人休息。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜，光线明亮。\n(2)护理员准备。护理员了解老年人的意愿、生活习惯、爱好等内容。掌握将要进行的活动的要点，指导老年人完成活动。\n(3)老年人准备。老年人身体状况允许，自愿参加。\n(4)物品准备。选择安全经济的用具，如毛线、扣子、开关等。\n\n步骤2 设计活动\n护理员设计老年人手工活动项目，内容新颖、有趣、多样，与日常生活相结合，要让老年人力所能及，愿意参加。如编制中国结、剥豆子、看图搭积木等。\n\n步骤3 示范\n护理员态度和蔼，边示范边指导。合理安排活动时间。根据老年人的能力采取语言鼓励和行为支持等方式。活动中要随时观察老年人的反应。\n\n步骤4 记录\n活动结束，护理员征求老年人对活动的意见和建议并进行客观记录。另外，应记录此次活动锻炼的目的，达到的效果，需要改进的方面等。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33cd376-ef1c-11ed-9ad4-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '带领智力障碍老年人进行康复训练', 1, 1, 1, '\n1.护理员必须尊重、理解智力障碍老年人，实事求是，格尽职守。\n2.康复训练应有规律性和趣味性，依据老年人智力障碍的程度和老年人的兴趣爱好选择不同的训练项目。\n3.为避免智力障碍老年人走失，应提供安全、封闭的康复训练环境。有些老年人可随身携带注明老年人基本情况和联系人、联系方式的材料。\n', '1 工作准备\n(1)护理员准备。\n1)全面评估老年人的身体状况、疾病的程度等。\n2)与老年人及家属充分沟通，了解老年人生活习惯、爱好等：\n(2)准备环境。选择老年人熟悉的、安全的环境，如老年人卧室、客厅等。\n(3)准备器械。准备适合老年人的训练用具，如玩具、认读卡片等。\n\n步骤2 制定训练方案\n根据老年人的情况和医院医生的康复计划制定适合老年人的康复训练方案，内容包括要达到的目标，每天的训练内容、训练时间等。\n\n步骤3 进行康复训练\n(1)护理员以缓慢温和的语速告诉老年人每一项操作的步骤。\n(2)把每一步的具体动作加以分解。\n(3)反复示范指导老年人练习。\n(4)多用表情和手势，得到老年人的反馈后再开始下一步。\n\n步骤4 观察记录\n(1)康复训练过程中护理员要随时观察老年人的反应，发现异常立即停止。\n(2)老年人的表现有进步时要及时给予鼓励，并适当奖励。\n(3)与老年人沟通康复训练的感受。\n(4)及时记录老年人“训练日志”，日志内容包括训练完成情况，老年人的特殊行为和症状发生时间、持续长短及发生时的情景。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33cfa88-ef1c-11ed-9728-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33b2612-ef1c-11ed-99bb-8c554aa628fd', '3', '辅导老年人完成健身康复操', 1, 1, 1, '\n1.护理员应在与家属或医生确认老年人身体状况允许的前提下开展健身康复操锻炼。\n2.老年人在练习健身康复操时要掌握好运动时间，避免运动过量。\n\n', '1 工作准备\n(1)准备物品。准备毛巾、椅子，必要时备球类等。\n(2)老年人准备。老年人应穿着宽松舒适的衣服，防滑的鞋子，并确保有意愿且身体状况允许。\n(3)护理员准备。护理员衣着整洁，了解老年人情况，掌握健身康复操。向老年人充分解释健身康复操的方法，得到老年人的配合。\n(4)准备环境。\n1)环境整洁，地面无积水，活动场地无障碍物。\n2)室外锻炼应选择向阳、避风的地方。\n3)室内锻炼要注意通风，保持空气新鲜。\n\n步骤2 带领热身\n带领老年人一起做好准备活动，热身10~15分钟。\n\n步骤3 辅导健身活动\n(1)护理员先为老年人示范健身康复操的做法。\n(2)带领老年人一起做健身康复操。\n(3)在旁边辅导老年人进行锻炼，必要时给予协助。\n(4)锻炼时间以30分钟至1小时为宜。\n(5)做10分钟的整理活动。\n\n步骤4 观察反馈\n(1)锻炼过程中护理员应注意保护老年人安全。\n(2)观察老年人活动状况，发现异常，如呼吸急促、心慌、面色苍白、出冷汗等，应立即停止活动。\n(3)随时与老年人交流健身康复操的掌握情况，根据情况安排下一次的锻炼。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33d21df-ef1c-11ed-a234-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a339c638-ef1c-11ed-ba54-8c554aa628fd', '2', '活动保护', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33d6fb5-ef1c-11ed-a398-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33d21df-ef1c-11ed-a234-8c554aa628fd', '3', '使用平车转运老年人', 0, 1, 0, '\n1.使用平车转运老年人时最好由两人共同操作。搬运时注意节力，搬运动作要轻稳，协调一致，确保老年人的安全、舒适。\n2.对于不配合的老年人应采用适当的约束。', '1 工作准备\n(1)环境准备。环境宽敞，无障碍物。\n(2)护理员准备。着装整洁，态度和蔼，了解老年人的身体状况、各种引流管的位置。掌握平车的操作。\n(3)老年人准备。身体状况允许，愿意配合，穿好衣裤。\n(4)器具准备。平车的担架完好，轮胎充气，刹车可以制动，护栏完好。必要时准备毛毯或盖被、尿垫等。搬运骨折病人需准备木板。\n\n步骤2 评估和沟通\n护理员全面评估老年人身高、体重、年龄、疾病诊断、病情程度和进展情况，以及有无输液或其他引流管路。耐心向老年人解释即将开始的转运过程，取得老年人的配合。\n\n步骤3 固定平车\n护理员将平车推至老年人床旁，制动车轮，固定平车，放下护栏。\n\n步骤4 转运\n协助老年人穿好衣服。根据老年人的评估情况选用合适的搬运方法，尽量让老年人的身体靠近护理员。护理员将老年人轻放于平车上，头部位于大轮端，可减轻颠簸引起的不适。\n\n步骤5 行进\n老年人躺卧舒适，盖好盖被。妥善安置好老年人的引流管道，将平车护栏拉起并固定。松开车轮制动，推车时护理员站在老年人的头侧平稳行进。遇到拐弯或者地面不平整、有门或门帘的情况时，护理员可请求他人帮助以保证安全通过。行进过程中，护理员要随时询问老年人的感受，观察病情，出现异常及时停下来检查。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33d96c6-ef1c-11ed-bf6f-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33d21df-ef1c-11ed-a234-8c554aa628fd', '3', '使用轮椅转运老年人', 0, 1, 0, '。掌握轮椅的操作。\n(3)老年人准备。身体状况允许，愿意配合，着装合体，鞋子防滑。\n(4)物品准备。选择适合老年人的轮椅。轮椅的轮胎气压充足，刹车制动良好，轮椅完好备用，必要时备毛毯。\n\n步骤2 固定轮椅\n护理员打开轮椅，固定轮椅刹车。协助老年人穿好衣服。\n\n步骤3 坐入轮椅\n(1)护理员向老年人解释即将开始的转移过程，取得老年人的配合。\n(2)搀扶或抱起老年人坐在轮椅上双手扶稳扶手，为老年人系好安全带，将双脚放于脚踏板上，松开刹车平稳行进。\n\n步骤4 转运\n遇到障碍物或拐弯时，护理员要态度和蔼地提示老年人。下坡时采用倒车推行方法，上台阶、电梯，要先翘起前轮，再抬起后轮。在轮椅转运过程中，如观察到老年人身体不适，应就近休息，通知医护人员。\n\n步骤5 反馈\n转运结束，护理员向老年人询问坐轮椅的感受，有无不适，以便改进操作方法。\n\n注意事顶\n1.轮椅上架腿布的使用要得当，以下两种情况下不需要使用，一是当护理员帮助老年人转移时，因护理员的腿要踏人轮椅的空隙处，架腿布而显得碍事；二是能坐轮椅自由移动的老年人，为了使用轮椅的安全，需要撤掉架腿布。\n2.老年人每次乘坐轮椅的时间不可过长，轮椅的坐垫要舒适。每隔30分钟，护理员要协助老年人站立或适当变换体位，避免臀部长期受压造成压疮。\n3.天气寒冷时可用毛毯盖在老年人腿上保暖。\n', '1 工作准备\n(1)环境准备。环境整洁宽敞，无障碍物。\n(2)护理员准备。着装整洁，了解老年人的身体状况和轮椅使用的情况，老年人的活动能力、活动时间及');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33dbe20-ef1c-11ed-b10d-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33d21df-ef1c-11ed-a234-8c554aa628fd', '3', '教老年人使用拐杖', 0, 1, 0, '\n1.老年人使用拐杖行走前，护理员要告知老年人使用拐杖的注意事项。\n2.护理员应严格遵从医生和康复师对拐杖的选择和步行的指导要求指导老年人。\n3.拐杖应放置在老年人随手可及的固定位置。\n4.行走中避免拉、拽老年人路膊，以免造成老年人跌倒和骨折。\n', '1 工作准备\n(1)环境准备。环境宽敞，地面平坦，无积水。\n(2)护理员准备。着装整洁，全面了解老年人身高、体重、年龄、疾病诊断、病情及进展情况。与家属充分沟通，了解老年人已往的拐杖使用情况、活动能力、活动时间等。护理员掌握拐杖的操作。\n(3)老年人准备。有行走的意愿，身体状况允许，着装合体，鞋子防滑。\n(4)物品准备。拐杖完好，适合老人使用。\n\n步骤2 检查拐杖\n在使用拐杖前，护理员先教会老年人检查拐杖是否完好，内容包括把手有无松动，拐杖与地面接触的橡胶垫是否完好，调节高度的按钮是否锁紧等。\n\n步骤3 保护行走\n(1)护理员指导老年人使用拐杖时手握住把手，拐杖放在脚的前外侧，目视前方，保持身体直立行走。\n(2)看护老年人自己行走，与其保持适当的距离，在必要时给予帮助。\n(3)老年人无偏瘫时护理员应站在道路侧陪同行走，老年人偏瘫时护理员应站在偏瘫肢体侧陪同行走，行走时护理员可以拉住老年人的腰带或特制的保护腰带防止老年人跌倒。\n(4)在行走过程中，护理员要观察有无妨碍行走的障碍物，及时清理。观察老年人有无出汗、呼吸急促、心慌等异常情况，询问老年人的感受，如果老年人感到疲劳应立刻休息。\n\n步骤4 反馈\n行走结束，护理员向老年人了解使用拐杖行走的感受，使用中存在的问题，以便解决问题，给予指导。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33e0bf7-ef1c-11ed-869b-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', '', '1', '心理护理', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33e3306-ef1c-11ed-9a82-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33e0bf7-ef1c-11ed-869b-8c554aa628fd', '2', '心理保健', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33e8127-ef1c-11ed-a02c-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33e3306-ef1c-11ed-9a82-8c554aa628fd', '3', '设计老年人心理健康宣教方案', 1, 1, 1, '\n1.做好与其他专业人员的沟通在制定心理健康宣教方案时，应与社会工作者、护士等专业人员进行沟通，在共同商讨的条件下，共同制定心理健康宣教方案。涉及专业内容时，可请护士协助完成。\n2.宣教内容和方式考虑老年人的需要宣教内容和方式的确定，应建立在对老年人心理健康保健观念、个人需求、语言理解力等方面进行评估的基础上，兼顾老年人的共性问题和个性问题，以确保宣教的效果。\n3.恰当把握宣教的时间和频次在确定心理健康宣教的时间和频次时，应考虑老年人的接受能力，每次的时间和间隔时间不宜太长。\n', '1 工作准备\n(1)护理员准备。在设计心理健康宜教方案时，与社会工作者、护士等专业人员进行沟通，共同制定心理健康宣教方案。\n(2)老年人准备。\n1)自愿接受心理健康宣教。提前告知老年人心理健康宣教的时间、地点和内容，使老年人自愿接受心理健康宣教。\n2)做好宣教前准备。确保老年人身体状况允许、情绪状态稳定，避开吃饭、午睡、其他治疗和护理操作的时间。\n(3)环境准备。心理健康宣教可在活动室或老年人的房间内进行。室内应整洁、空气流通、光线充足，至少有两把高度为40~50厘米的椅子或沙发，两侧应有扶手；调节好房间的温度，最好在22~24℃。\n(4)物品准备。心理健康宣教手册、心理健康宜教光盘、一台电视机或录像机。\n\n步骤2 评估\n(1)通过交谈，评估老年人对心理健康和心理保健的态度，以及接受心理健康宣教的意愿\n(2)设计老年人心理健康宣教需求表格，评估老年人对宣教内容、形式的个性化需求\n(3)评估老年人的视力、听力、语言理解能力，情绪是否稳定等。\n\n步骤3 制订计划\n与社会工作者、护士等专业人员共同制定心理健康宣教方案，并制作心理健康宣教手册或光盘。心理健康宣教方案应包括心理健康宣教的目标、内容、形式、时间和频次、场所等。\n(1)目标。根据对老年人心理健康宣教的评估结果，制定目标。内容包括：①描述老年人心理健康的标准；②列举老年人心理保健的要点；③掌握放松训练的技巧。\n(2)内容。根据老年人的心理状况及对心理健康和心理保健知识的了解程度，确定宣教的内容，包括老年人心理健康的标准、老年人自身进行心理保健的要点、调适情绪的方法、放松训练的技巧等。对于家属的宣教内容，还可以增加子女如何对老年人提供情感支持、如何寻求心理保健资源和心理保健机构等方面的内容。\n(3)形式。针对老年人的意愿及身心状况，选择恰当的宣教形式，例如，以小组形式进行讲座，通过多媒体进行演示、现场示范、组织观看宣传教育光盘、制作和发放图文并茂的宣传小册子、一对一讲解、经验交流茶话会等\n(4)时间和频次。针对老年人的共性问题，可以季度为单位，每季度安排一轮心理健康宣教讲座，每月1次，每次30~60分钟。对于老年人的个性问题，可每周1次，以一对一的形式进行心理健康宣教。\n(5)场所。对于小组形式的心理健康宣教以及经验交流茶话会，可在活动室内进行；对于一对一的讲解，可在老年人自己的房间内进行。\n\n步骤4 实施计划\n按照制订好的心理健康宣教计划，对老年人及其家属实施心理健康宣教方案。\n在进行心理健康宣教时，应采用通俗、易懂，老年人熟悉的语言进行讲解。在每次心理健康宣教过程中，认真观察和记录老年人的参与情况、接受能力、对宣教效果的反馈，及时对计划做出调整。\n在每次心理健康宣教的开始阶段，可用5分钟的时间，带领老年人进行某种放松训练，如呼吸放松训练、肌肉放松训练，以起到热身作用，同时达到指导老年人学会放松训练技巧的目的。\n\n步骤5 效果评价\n设计一份心理健康宣教效果评价表，在每次心理健康宣教之后，评估老年人对本次宣教内容、形式、时间、场所安排的满意程度。根据评价结果，及时调整下一次宣教的计划和重点。\n\n步骤6 记录存档\n记录每次进行心理健康宣教的时间、地点、参加者、内容、宣教过程中出现的异常情况等，并将心理健康宣教的计划、实施记录、需求评估表、效果评价表进行存档。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33ea838-ef1c-11ed-92da-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33e3306-ef1c-11ed-9a82-8c554aa628fd', '3', '设计老年人活动方案', 1, 1, 1, '。\n2)评估设施是否完好。在组织活动前，应评估活动场地及设施是否安全，包括桌椅的稳定性、健身器材的完好性、活动所需物品的安全性等。\n(2)老年人准备。确保老年人身体状况适宜活动，情绪状态相对稳定，有参加活动的意愿，且对活动项目感兴趣。\n(3)环境准备。根据活动项目及老年人的身体状况，可在活动室、阅览室、棋牌室或室外进行。\n1)活动室。活动室应宽敞、整洁，空气流通，光线充足。至少有一张长桌、几张方桌、10~15把椅子，能播放光盘的电视机、录像机、投影设备、音响设备等。附近最好有卫生间。\n2)阅览室。阅览室内应安静、光线充足。至少设有一张矮桌或茶几，5一6把椅子或沙发，2~3组书架，上面放有各种杂志、报纸、图书、画报、健康保健及疾病知识宣传书籍或手册等。\n3)棋牌室。棋牌室内应安静、光线充足。至少有2~3张方桌、每张桌子周围有2~4把椅子，有扑克牌、麻将牌、象棋、围棋、跳棋等各类棋牌。\n(4)物品准备。\n1)听音乐或唱歌。准备好光盘或录像带，根据老年人的爱好，选择好乐曲。\n确认电视机、录像机、投影设备等处于正常功能状态。根据人数，摆放好足够的椅子，并确认桌椅稳固。\n2)棋牌类活动。准备好扑克牌、麻将牌、象棋、围棋、跳棋等。确认桌椅稳固，保证数量，摆放合理\n3)艺术类活动，准备好书法、绘画、临摹、涂色、刺绣、编织等所需材料，确认桌椅稳周，保证数量\n4)阅读书刊。准备好杂志、报纸，图书、画报，健康保健及疾病知识宣传书籍或手册等，确认桌椅稳固，保证数量，照明设备完好。\n5)健身类活动。准备好健身操、广播操等健身话动所需的录音带或录像带，准备好兵乓球台、球拍等\n\n步骤2 评估\n(1)评估老年人对活动项目的喜好，将能提供的休闲娱乐活动项目罗列在一张表格中，评估老年人对各类活动项目的喜好程度，以及老年人参与活动的意愿\n2)评估老年人的身体状况和情绪状态，对要参加活动的老年人进行身体状况和情绪状态评估。对身体状况欠佳、情绪异常的老年人，在活动过程中要作为重点现察对象\n\n步骤3 制订计划\n与职业治疗师、康复师、社会工作者、护士等专业人员共同制定活动方案。活动方案应包括活动项目、形式、时间和频次、场所、实施方法等，可将每周安排的活动项目列在一张表格中，然后根据每位老年人的身体状况、个人喜好等，与老年人一起制订个性化活动计划。\n(1)活动项目。活动项目包括听音乐、唱歌、阅读书刊、书法绘画、编织、刺绣、剪纸、各类棋牌、趣味游戏、健身活动、园艺活动、简单炊事活动、外出观光或游览等\n(2)形式。尽量以小组形式进行活动，这样可以增进老年人之间的交流。参与每项活动的小组人数不宜太多（最好不超过10人），根据老年人的喜好或身体状况，也可单独听音乐、阅读书刊等。\n(3)时间和频次。每天至少有1小时的时间，安排各类休闲娱乐活动。对于每项活动来说，每周2~3次，每次活动20~30分钟。\n(4)场所。尽量集中在活动室、棋牌室、阅览室等处进行活动。也可根据活动项目的特点，老年人的身体状况等，到室外活动，或在老年人自己的房间内进行活动\n(5)实施方法。根据每项活动的特点，制定具体的实施方法，在活动过程中，应有护理员在场进行活动协调、指导或示范。\n\n步骤4 实施计划\n按照制订好的活动计划实施。在活动过程中，认真观察和记录老年人的参与情况及其反馈。\n(1)热身阶段。安排参加活动的老年人就座，介绍活动的内容、时间和活动过程中的注意事项，必要时，先进行示范。对于小组活动，可在活动之前，做5分钟左右的健身操，以融治气氛。\n(2)活动过程中。在活动过程中，由1~2名护理员负责协调和示范，以保证活动安全，促进活动的顺利进行。同时，密切关注活动过程中老年人的身体状况和情绪变化，对出现情绪激动或有攻击行为倾向的老年人，应立即带离活动现场。\n(3)活动结束。做好安全疏导，护送半自理老年人回到自己的房间；清理活动场地，设备归位，并做好活动登记和记录。\n\n步骤5 效果评价\n设计一份活动评价表，在每次活动之后，评估老年人对本次活动项目、形式、时间、场所安排的满意程度。根据评价结果，结合活动过程中对老年人参加活动的积极程度和异常表现的观察与记录，及时调整下一次的活动计划。\n\n步骤6 记录存档\n记录每次活动的时间、地点、参加者、活动项目、活动过程中出现的异常情况等，并将活动计划、实施记录、活动评价表进行存档。\n\n注意事项\n1.做好与其他专业人员的沟通在制定活动方案时，应与职业治疗师、康复师、社会工作者、护士等专业人员进行沟通，在共同商讨的条件下，共同制定活动方案。\n2.活动项目符合老年人特点及需求选择的活动项目应符合老年人的身体状况、活动能力、个人喜好和需求。因此，对老年人身心状况、个人喜好和活动意愿的评估非常关键。\n3.注意场地及设施的安全性在组织活动前，应评估活动场地及设施是否安全，包括桌椅的稳定性、健身器材的完好性、活动所需物品的安全性等\n4.活动中密切观察老年人的变化活动过程中，密切观察老年人的情绪变化。对于情绪激动或有攻击性行为的老年人，应立即将其带离活动现场\n', '1 工作准备\n(1)护理员准备。\n1)与其他人员沟通好。在组织老年人休闲娱乐活动之前，要与其他专业人员沟通好，例如，社会工作者、职业治疗师、康复护士等，把握活动过程中的要点及');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33ecf93-ef1c-11ed-a8b9-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33e0bf7-ef1c-11ed-869b-8c554aa628fd', '2', '心理疏导', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33f1db1-ef1c-11ed-8dac-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33ecf93-ef1c-11ed-a8b9-8c554aa628fd', '3', '用语言和肢体语言疏导老年人不良情结', 1, 1, 1, '\n1.做好与其他专业人员的沟通在制定心理疏导方案时，应与社会工作者、护士等专业人员进行沟通，在共同商讨的条件下，制定心理疏导方案。必要时，求助于社会工作者的协助。\n2.尊重老年人自己的意愿\n心理疏导方案应尽量与老年人达成一致意见。每次心理疏导前，应征得老年人的同意；在心理疏导过程中，如果老年人出现情绪过于激动的情况，应适时终止。\n3.营造无拘束的环境氛围\n进行心理疏导时，应注意环境的舒适、安静、温馨和私密性，尽量营造无拘束的环境氛围，让老年人感到安心和舒适。\n4.语言和非语言疏导技巧的运用\n在进行心理疏导的过程中，应结合老年人的个性特征及特定情境，恰当地运用各种语言和非语言疏导技巧，尤其是使用肢体触摸等沟通技巧，但要注意慎重使用。\n5.注意保护老年人的隐私\n有些老年人认为抑郁是一件不光彩的事情，因此，要注意保护老年人的隐私。每次进行心理疏导时，注意环境的私密性，并对相关资料做好保密工作。\n\n', '1 工作准备\n(1)护理员准备\n1)与其他人员的商讨，进行心理疏导前，护理员应与社工人员，护士等专业人员进行构通，制订心理疏导的计划\n2)安排出充足的时间。护理员要将自已的工作安排好，留出1小时左右的时间，避免因临时性工作而被打断。\n(2)老年人准备\n1)知晓心理疏导的目的和时间，提前与老年人就心理疏导的目标、时间和频次等达成一致意见，每次心理疏导前，告知老年人时间、地点\n2)做好疏导前的准备。避开吃板、午睡、其他治疗和护理操作的时间，提翻老年人上厕所，以免心理疏导过程因上厕所而被打断\n(3)环境准备\n选择一间光线明亮、安静、整洁、气氛轻松的独立房间，房间最好不要太大。房间内至少有一张桌子、两把椅子或沙发，最好是比较舒适的沙发，高度最好为40~50厘米，两侧应有扶手。房间内可摆放一些绿色植物或色彩鲜艳的花。测评前，关闭房间内的音响设备，调节好房间的温度，最好在22~24℃。测评过程中不要有人员走动。如果老年人住单间，也可在老年人自己的房间内进行。\n(4)物品准备，记录单、笔，一盒纸巾。\n\n步骤2 评估\n结合老年人抑郁评估量表、与老年人交谈、平日对老年人情绪和行为的观察等，全面收集老年人的资料，与老年人一起，共同找出目前最关心、最困扰、最需要解决的问题，从而确定优先解决的问题，并进一步了解问题的来龙去脉，包括问题的起因、过程、已经采取的措施等。另外，应评估老年人的视力、听力、语言表达和理解能力。\n\n步骤3 制订计划\n针对评估结果，与社会工作者、护士等专业人员共同制定心理硫导计划，包括心理或导的目标、形式与方法、时间与频次、内容提纲、效果评价时间和方法等。\n(1)目标。应与老年人共同制定目标，最好双方达成一致，目标应具体、可行、积极，双方可以接受、可以评估。同时，注意近期目标与远期目标的结合。\n(2)形式与方法。可采用一对一心理疏导、团体心理疏导、心理健康知识宣教、安排参加休闲娱乐活动等形式进行心理疏导。\n(3)时间与频次，通常每周1~2次，每次60分钟。进行4次心理疏导后，进行效果评估。\n(4)内容提纲。列出每次心理疏导时的内容提纲，以使心理疏导能够紧扣主题，提高效率。尽量设置开放式问题，以引导老年人充分表达内心的感受。\n(5)效果评价时间和方法。分别在每次心理疏导结束后和完成4次心理疏导后进行效果评价。评价的方法包括量表评估、观察情绪和生理症状的变化、老年人的自我评估。\n\n步骤4 实施计划\n按照制订好的心理疏导计划实施。\n(1)开始阶段。在心理疏导的开始阶段，要通过各种方式努力与老年人建立信任的关系。在制定心理疏导计划时，让老年人共同参与，并达成一致意见；在每次心理疏导之前，提前告知时间和地点；准备舒适、整洁、温馨、私密、熟悉的房间，以营造舒适、安全、轻松的氛围。\n(2)进行阶段。充分利用各种语言和非语言疏导技巧，如恰当的空间距离，明确的表达，全神贯注的倾听，适时的反应，开放式提问，恰当的身体姿势，目光和面部表情等，调动老年人的积极性，启发和引导老年人表达内心的感受，发现自身的问题及解决问题的方法，鼓励和支持老年人，增强解决问题的信心与力量。\n1)启发、引导。启发和引导老年人自已去探索解决自身问题的方式，做到授之以“渔”，而非授之以“鱼”，包括启发和引导老年人认识目前存在的问题、纠正错误认识、学会接纳现实、构建新的行为模式和人际关系等。在心理硫导过程中，通过恰当的空间距离、目光接触、适时的反应等方式，对老年人表达关心和尊重；通过开放式提问、重复和澄清等技巧的运用，调动老年人的积极性，引导老年人把内心的真实感受表达出来，发现自身的问题，并发掘自身解决问题的潜力。\n2)支持、鼓励。老年人已经体验到了自身问题带来的困扰和痛苦，内心可能有改变自我的愿望，但往往缺乏改变自我的信心和改变自我的力量。通过支持和鼓励，可提升老年人解决自身问题的信心，激发老年人改变自我的内在力量。在心理疏导过程中，通过全神贯注的倾听、适时的反应、恰当的身体姿势、适时的身体触摸、鼓励的目光和面部表情等，理解老年人的感受，提供情感上的支持和鼓励。同时，指导老年人学会放松训练的技巧。\n(3)结束阶段。在每次心理疏导结束时，提前告诉老年人，让老年人有心理准备，不要突然中止谈话；在谈话快要结束时，尽量不要再提出新问题；简要总结会谈的重点内容，提出下次会谈的时间和重点；对老年人的合作表示感谢，并安排老年人休息。\n\n步骤5 效果评价\n(1)每次心理疏导后的总结。在每次心理疏导后，注意观察老年人心理和情绪的变化，反思自己在心理疏导过程中的言行是否恰当，对老年人的问题把握是否准确。同时，请老年人自己总结在这次心理疏导之后，发生了什么变化，引起变化的原因是什么。通过双方的反思与总结，及时发现新的问题，以确定下一次心理疏导的重点。\n(2)最终效果评价。在完成4次心理疏导之后，进行最终的效果评价。评价方法包括：①采用量表进行评估；②观察情绪症状、生理症状的改善情况，③老年人自身对效果的评估。如果未见改善，应与社会工作者、护士等讨论该个案的情况，调整心理疏导计划。对于心理问题严重的老年人，应及时进行转介，求助于社会工作者或精神科专业人员的帮助和治疗。\n\n步骤6 记录存档\n记录每次进行心理疏导的时间、地点、参加者、内容、疏导过程中的异常情况等，并将心理评估结果、心理疏导计划和实施记录进行存档。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33f44bf-ef1c-11ed-834d-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33ecf93-ef1c-11ed-a8b9-8c554aa628fd', '3', '识别老年人的抑郁情绪及其原因', 1, 1, 1, '\n1.避免使用刺激性语言\n有些抑郁的老年人对外界的刺激性语言比较敏感，反而会加重抑郁情绪。因此，在测评过程中，避免直接使用“抑郁”等刺激性的语言。\n2.不要强迫老年人回答问题\n在测评过程中，如果老年人出现强烈的情绪反应，出现哽咽、哭泣等情况，此时不要机械地继续询问问题，可暂停一会，递给老年人纸巾，通过抚摸等动作给予安慰，待其情绪稍微平复后，再接着进行测评，或者另外安排其他的时间进行测评。\n3.尽可能引导老年人倾诉自已的感受\n在询问抑郁的原因时，尽可能采用开放式的问题，鼓扇和引导老年人倾诉自己的内心感受。在倾听过程中，要表现出专心的态度，并适时通过目光、点头、握老人的手、递纸巾等动作，表达对老人的关心。', '1 工作准备\n(1)护理员准备。\n1)安排出充足的时间。护理员要将自己的工作安排好，留出30分钟的时间，避免因临时性工作而被打断。\n2)熟悉评估方法。测评前，护理员应熟悉量表的使用方法和评分方法，了解抑郁的表现和常见原因。\n(2)老年人准备。\n1)知晓要做评估。测评前，护理员应事先跟老年人沟通好，讲清楚一会儿要花20一30分钟的时间，问一些简单的问题，征得老年人同意。\n2)做好测评准备。选择老年人情绪稍微稳定的时候，避开吃饭、午睡、其他治疗和护理操作的时间。在测评前，提醒老年人上厕所，以避免测评过程中因上厕所而中断。\n(3)环境准备。选择一间光线明亮、安静、整洁、气氛轻松的独立房间，房间最好不要太大。房间内至少有一张桌子、两把椅子或沙发，最好是比较舒适的沙发，高度最好为40~50厘米，两侧应有扶手。房间内可摆放一些绿色植物或色彩鲜艳的花。测评前，关闭房间内的音响设备，调节好房间的温度，最好在22~24℃。测评过程中不要有人员走动。如果老年人住单间，也可在老年人自己的房间内进行测评。\n(4)物品准备。\n1)评估表。准备一份空白的老年抑郁量表及评分标准，在表格的最上方，写上老年人的姓名、测评者的姓名、测评日期(年月日)。\n2)其他物品。准备一支记录用的笔、一盒纸巾（抑郁的老年人容易流泪，准备纸巾备用)。\n\n步骤2 开场白\n请老年人坐在沙发上，护理员可与老年人坐在桌子的两侧，或面对面坐好)。开场白可以是：“老人家，您好，听说您最近心情不太好。我能跟您聊一聊吗？一会儿我问您一些简单的问题，您直接告诉我是或不是就行，也可以跟我说一下您最近为什么觉得心情不好。如果您准备好了，我们就开始。”\n\n步骤3 实施评估 \n测评者通过口头询问的方式，逐项询间以下问题，根据老年人的口头回答，在相应的选项序号上画勾（见下表）。\n\n老年抑郁测评量表\n项目                                           是   否\n1.对生活基本上满意\n2.已放弃了许多活动与兴趣\n3.觉得生活空虚\n4.感到厌倦\n5.觉得未来有希望\n6.因为脑子里一些想法摆脱不掉而烦恼\n7.大部分时间精力充沛\n8.害怕会有不幸的事落到自已头上\n9.大部分时间感到幸福\n10.常感到孤立无援\n11.经常坐立不安，心烦意乱\n12.希望待在家里而不愿去做些新鲜事\n13.常常担心将来\n14.觉得记忆力比以前差\n15.觉得现在活着很惬意\n16.常感到心情沉重、郁闷\n17.觉得像现在这样活着毫无意义\n18.总为过去的事忧愁\n19.觉得生活很令人兴奋\n20.开始一件新的工作很困难\n21.堂得生活充满活力\n22.觉得自己的处境已毫无希望\n23.觉得大多数人比自已强得多\n24.常为一些小事伤心\n25.常常觉得想哭\n26.集中精力有困难\n27.早晨起来觉得很快活\n28.希望开聚会\n29.做决定很容易\n30.头脑像往常一样清晰\n\n步骤4 结果判定\n由于条目1、5、7、9、15、19、21、27、29、30是反序计分，因此，先将这十个条目的记分转换过来，将1转换为0,0转换为1。然后，将30个项目相加，得到总分。按照“0~10分为正常范围，11~20分为轻度抑郁，21~30分为中重度抑郁”的评分标准测评，如果处于抑郁状态，应请医生做进一步的检查，确诊是否患有抑郁症。\n\n步骤5 探寻原因\n量表测评结束后，通过进一步的交谈，探寻抑郁的可能原因。针对抑郁的可能原因，加以开导，并采取相应的安全防范措施，以预防自杀等意外事件的发生。如果进一步确诊为抑郁症，应根据医嘱服用相应的药物。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33f9297-ef1c-11ed-b769-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33ecf93-ef1c-11ed-a8b9-8c554aa628fd', '3', '识别老年痴呆症', 1, 1, 1, '】测评者在说这三样东西的名称时，必须发音清晰，每样东西说秒钟左右，只说一遍。\n\n(3)进行第12个项目的测评。\n测评者：“请您从100减去7，然后用所得的数再减去7，这样一直计算下去，把每一个答案都告诉我，直到我说停为止\"。\n\n第12个项目测评\n项目           正确错误/不做\n13.用100连续减7\n100-7  93\n    __-7   85\n    __-7  78\n    __-7  71\n    __-7  算不了了\n停！\n\n【评分说明】在本例中，第1次减7计算正确，记1分；第2次减7正确得数是86，而如果给出错误的答案，该项记为0分，第3次、第4次的计算结果均正确，均记为1分；最后一次减7未做，记为0分。\n【注意事项】这项测评除了检查老年人的计算能力以外，还检查老年人的注意力。所以，在测评时，不要重复老年人的答案，也不能让老人用笔算。将老年人每一次计算出的数值写在横线上。\n\n(4)进行第13个项目的测评。\n测评者：“现在请您告诉我，刚才我让您记住的三样东西是什么？”\n老年人：“国旗…想不起来了。”\n\n【评分说明】进行回忆时，不需要按照次序回答，只要说出了某样东西的名称，就在该项上记1分。该例中，老年人只回忆出了“国旗”这个词，因此，只在“国旗”这个项目上记1分，在“皮球”和“树木”这两个项目上各记0分。\n\n(5)进行第14个项目的测评\n测评者：拿出手表和钢笔，“请您说出它们的多称“，\n老年人：“手表，写字用的”\n\n【评分说明】该老年人正确说出了“手表”的名称，记为1分。而对于铅笔，只说出“写字用的”，没有说出“铅笔”的名称，记为0分。\n\n(6)进行第15个项目的测评\n测评者：“现在我要说一句话，请您清楚地重复一遍：四十四只石狮子”\n老年人：“四十四只石狮子”\n\n【评分说明】老年人回答时，只有每个字都正确，而且咬字清楚，才记1分，该例中，老年人回答正确，记为1分。\n【注意事项】测评者只允许对老年人说一遍，注意咬字清晰。\n\n(7)进行第16个项目的测评\n测评者：把写有“请闭上您的眼”字样的卡片交给老年人，“请您照着卡片上所写的去做”\n老年人：做出闭上眼晴的动作。\n【评分说明】该例中，老年人按着卡片上所写的闭上眼睛，记1分；如果动作做错了，或没有反应，则记为0分。\n\n(8)进行第17个项目的测评。\n测评者：“接下来我说三个动作，等我说完后，您按照我说的顺序做这三个动作。‘请用右手拿这张纸，用双手把纸对折，再把纸放在您的大腿上？。”\n老年人：（动作）用左手拿纸、用双手把纸对折。\n\n【评分说明】只有动作和顺序都正确，才记为正确。该例中，老人第一个动作没有按照测评者说的“用右手拿纸”，而用的是左手，该项记为0分；第二个动作正确，记1分；第三个动作没有做，记为0分。\n【注意事项】测评者把事先准备好的白纸交给老年人，把让老年人要做的三个动作同时说完，再让老年人做，不要重复说明，也不要给予示范。\n\n(9)进行第18个项目的测评。\n测评者：“请您说一句完整的，有意义的句子。”\n老年人：“今天我感到很高兴。”\n\n【评分说明】句子必须有主语和动词，才算合乎标准。该例中老人所说的句子合乎标准，记1分。\n\n(10)进行第19个项目的测评。\n测评者：把画着两个五边形图案的卡片交给老年人，“请您照着这张图的样子，在纸上照原样把这个图形画出来”。\n老年人：画出图形。\n\n【评分说明】需按图画出两个五边形，而且两个五边形的交叉处形成一个四边形，才记为1分。如果错误，记为0分\n\n步骤4 结果判定\n根据该例老年人的测评结果，计算总分。按照“中学或以上的老年人（教育年限大于或等于6年）来说，如果总分小于24分，判定为可疑痴呆”的标准。如果判定为可疑痴呆，应将老人的症状以及简易智能状态速检表的结果告诉医生，请医生对老人做进一步的检查，以确诊其是否患有老年痴呆症。\n\n注意事项\n1.选择安静、气氛轻松的环境\n测评时，应选择安静、气氛轻松的环境，注意在老年人熟悉的地方进行测评，以缓解老年人的紧张情绪。同时，避免环境中有声音、光线、人员等方面的干扰，使老年人能安心完成测评。\n2.测评过程中注意给予鼓励\n大多数老年人对自已的记忆力减退非常在意，因此，进行记忆力测查时，老年人容易紧张，而且容易灰心或放弃。所以，在测评过程中，应注意给予适当的鼓励。\n\n', '1 工作准备\n(1)护理员准备。\n1)安排出充足的时间。护理员要将自己的工作安排好，留出30分钟的时间，避免因临时性工作而被打断。\n2)熟悉评估方法。测评前，护理员应熟悉量表的使用方法和评分方法，了解抑郁的表现和常见原因。\n(2)老年人准备。\n1)知晓要做评估。测评前，护理员应事先跟老年人沟通好，讲清楚一会儿要花20一30分钟的时间，问一些简单的问题，征得老年人同意。\n2)做好测评准备。选择老年人情绪稍微稳定的时候，避开吃饭、午睡、其他治疗和护理操作的时间。在测评前，提醒老年人上厕所，以避免测评过程中因上厕所而中断。\n(3)环境准备。选择一间光线明亮、安静、整洁、气氛轻松的独立房间，房间最好不要太大。房间内至少有一张桌子、两把椅子或沙发，最好是比较舒适的沙发，高度最好为40~50厘米，两侧应有扶手。房间内可摆放一些绿色植物或色彩鲜艳的花。测评前，关闭房间内的音响设备，调节好房间的温度，最好在22~24℃。测评过程中不要有人员走动。如果老年人住单间，也可在老年人自己的房间内进行测评。\n(4)物品准备。\n1)评估表。准备一份空白的简易智能状态速检表及评分标准，在表格的最上方，写上老年人的姓名、测评者的姓名、测评日期(年月日)。\n2)其他物品。一支记录用的笔，一张可折叠的白纸，一支铅笔，一块手表，\n一张用初号字号写着“请闭上您的眼睛”的卡片，一张画着两个五边形图案的卡片。\n\n步骤2 开场白 \n请老年人坐在桌子一侧的椅子上，护理员与老年人面对面坐好，或坐在桌子的两侧。开场白可以是：“老人家，待会儿我会问您一些问题，请您口头回答我，或者按照我说的做一些简单的动作，大概花10~20分钟的时间。如果您准备好了，我们就开始。”\n\n步骤3 实施评估\n(1)进行前10个项目的测评。\n测评者：把简易智能状态速检表放在桌上，用口头询问的方式，逐项询问第1~10项问题，并将老年人口头回答的内容记录在表格中相应项目的横线上。\n\n简易智能状态速检表\n测评项目											正确   错误\n1.今年的年份是什么？     1976年（实际是2012年）\n2.现在是什么季节？冬天\n3.今天是几号？5号（实际是20号）\n4.今天是星期几？星期五（实际是星期四）\n5.现在是几月份？正月（实际是2月，阴历正月）\n6.现在我们在哪个省（市）？北京市\n7.现在您住在什么县（区）？海淀区\n8.现在您住在什么街道？说不清楚\n9.现在您在几楼？2楼\n10.这儿是什么地方？北京市第一社会福利院\n\n【评分说明】第1项回答错误，记为0分；第2项回答正确，记为1分；第3项（今天是几号）和第4项（今天是星期几），只要相差在3天及以内，均记为正确。因此，在该例中，第3项判定为错误，记为0分，第4项判定为正确，记为1分；第5项回答阴历正确，也判定为正确，记1分；第6项和第7项正确，各记1分；第8项未说出具体的街道，记为0分；第9项和第10项回答正确，各记1分。\n\n(2)进行第11个项目的测评。\n测评者：“现在我说三样东西的名称，在我说完之后，请您重复一遍，并记住这三样东西，因为过一会儿我还会再问您这三样东西的名称。”\n“皮球、国旗、树木。”\n\n“请您把这三样东西的名称重复一遍。”\n老年人：“皮球、树木、国旗。”\n\n【评分说明】在重复时，不要求按次序回答。该老年人回答的是“皮球，树木，国旗”，虽然顺序不对，但三个词均正确，因此在三个词的后面均记为正确。如果第一道复述错误，先记分（在回答错误的词后面记为错误），然后再告诉老年人错在哪里，并请他重复说一遍，直至正确。但最多只能重复5次。\n【');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a33fb9ef-ef1c-11ed-844a-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', '', '1', '生活照料', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34007ed-ef1c-11ed-a816-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33fb9ef-ef1c-11ed-844a-8c554aa628fd', '2', '排泄照料', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3402ed6-ef1c-11ed-92ea-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '为有肠造瘘的老年人更换粪袋', 1, 1, 0, '\n1.餐后2~3小时内不要更换粪袋，此时肠蠕动较活跃，更换时老年人有可能出现排便情况。\n2.操作过程中应注意保暖，并注意保护老年人的隐私。\n', '1 准备工作\n(1)环境温度18~22℃，湿度50%~60%为宜，根据季节因地制宜调节室温。\n(2)护理员衣着整齐，洗净双手，戴好口罩。\n(3)物品有清洁、干燥粪袋1个，检查是否在有效期内并无破损。温水、脸盆、毛巾、卫生纸、便盆。\n\n步骤2 评估交流\n(1)向老年人说明操作的目的，以取得合作。\n(2)评估粪袋内容物超过1/3时应将粪袋取下更换。\n\n步骤3 更换粪袋\n(1)护理员先协助老年人暴露造瘘口的部位，将纸巾垫于人工肛门处的身下。\n(2)打开粪袋与造瘘口连接处的底盘扣环，取下粪袋放于便盆上，查看人工肛门周围的皮肤，如无异常可用柔软的卫生纸擦拭干净，再用温热毛巾清洗净局部皮肤并擦干。\n(3)将清洁的粪袋与腹部造瘘口底盘扣环连接，扣紧扣环后用手向下牵拉粪袋，确认粪袋固定牢固，然后将粪袋下口封闭。\n\n特别提示 操作时护理员应注意遮挡老年人，并为老年人保暖\n\n步骤4 整理用物\n(1)将粪便倾倒于厕所内，用清水清洗粪袋。\n(2)根据需要记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34055e7-ef1c-11ed-87db-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '为留置导尿的老年人更换尿袋', 1, 1, 0, '为留置导尿的老年人更换尿袋\n\n步骤1 准备工作\n护理员更换尿袋时应做好准备工作。\n(1)护理员应着装整齐，并洗净双手，戴好口罩。\n(2)准备好尿袋、碘伏、棉签、别针，一次性手套、止血钳、笔、记录单。\n(3)检查尿袋有效期是否到期，有无破损。所使用的消毒液和棉签是否在有效期内。\n\n步骤2 评估交流\n(1)护理员应向老年人解释操作目的，以取得老年人的配合。\n(2)护理员应评估留置导尿管无滑脱，并保持管路通畅。\n\n步骤3 更换尿袋\n(1)护理员应仔细观察尿液颜色、性状、尿量\n\n特别提示 观察尿量时护理员视线应与刻度保持水平。\n\n(2)打开尿袋放尿端口，排空尿袋内余尿，关闭放尿端口。夹闭尿袋引流管上的开关。\n(3)撕开备好的尿袋外包袋，内面朝上平铺在留置尿管和尿袋连接处下面。\n(4)戴手套，用止血钳夹住留置尿管开口上端3~5厘米处，分离留置尿管与尿袋。\n取下尿袋，将连接尿管口端置于尿袋上卷起放置一旁。\n(5)用碘伏消毒尿管端口及外周。检查并旋紧待更换尿袋的放尿端口。取下新尿袋引流管端口盖帽，将引流管端口插人导尿管内。\n\n特别提示 护理员将引流管端口插入导尿管内时手不可触及两端口及周围。\n\n(6)松开止血钳，观察尿液引流情况。引流通畅，夹闭尿袋引流管上的开关，每2小时放尿一次。用别针将尿袋固定在床旁\n\n步骤4 整理用物\n(1)棉签，手套、更换下来的尿袋及可能被尿液污染的用物置于医用黄色垃圾袋中，按医用垃圾处理，脱去手套。\n\n特别提示 脱手套时不要污染其他物品\n\n(2)洗手，整理老年人床单位。\n\n步骤5 记录报告\n做好更换记录，发现异常情况时及时报告医护人员。', '1 准备工作\n护理员更换尿袋时应做好准备工作。\n(1)护理员应着装整齐，并洗净双手，戴好口罩。\n(2)准备好尿袋、碘伏、棉签、别针，一次性手套、止血钳、笔、记录单。\n(3)检查尿袋有效期是否到期，有无破损。所使用的消毒液和棉签是否在有效期内。\n\n步骤2 评估交流\n(1)护理员应向老年人解释操作目的，以取得老年人的配合。\n(2)护理员应评估留置导尿管无滑脱，并保持管路通畅。\n\n步骤3 更换尿袋\n(1)护理员应仔细观察尿液颜色、性状、尿量\n\n特别提示 观察尿量时护理员视线应与刻度保持水平。\n\n(2)打开尿袋放尿端口，排空尿袋内余尿，关闭放尿端口。夹闭尿袋引流管上的开关。\n(3)撕开备好的尿袋外包袋，内面朝上平铺在留置尿管和尿袋连接处下面。\n(4)戴手套，用止血钳夹住留置尿管开口上端3~5厘米处，分离留置尿管与尿袋。\n取下尿袋，将连接尿管口端置于尿袋上卷起放置一旁。\n(5)用碘伏消毒尿管端口及外周。检查并旋紧待更换尿袋的放尿端口。取下新尿袋引流管端口盖帽，将引流管端口插人导尿管内。\n\n特别提示 护理员将引流管端口插入导尿管内时手不可触及两端口及周围。\n\n(6)松开止血钳，观察尿液引流情况。引流通畅，夹闭尿袋引流管上的开关，每2小时放尿一次。用别针将尿袋固定在床旁\n\n步骤4 整理用物\n(1)棉签，手套、更换下来的尿袋及可能被尿液污染的用物置于医用黄色垃圾袋中，按医用垃圾处理，脱去手套。\n\n特别提示 脱手套时不要污染其他物品\n\n(2)洗手，整理老年人床单位。\n\n步骤5 记录报告\n做好更换记录，发现异常情况时及时报告医护人员');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a340a407-ef1c-11ed-af06-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '使用人工取便的方法辅助老年人排便', 1, 1, 0, '\n1.人工取便时勿使用器械，避免误伤肠黏膜\n2.人工取便操作过程中，护理员应注意观察老年人的情况，如出现面色苍白，呼吸急促，全身大汗等症状时应立即停止操作，必要时应及时就医。\n', '1 准备工作\n人工取便前应做好以下准备工作。\n(1)环境温度18~22℃、湿度50%~60%为宜，根据季节因地制宜调节室温。\n(2)护理员着装整齐，戴好口罩。\n(3)准备一次性手套，橡胶布（或一次性尿垫），润滑液（肥皂液或开塞露），卫生纸和便盆。必要时备屏风等遮挡老年人。\n\n步骤2 评估沟通\n(1)护理员向老年人说明操作的目的，告诉老年人在进行取便时会有异物感，消除老年人的紧张、恐惧心理，以取得老年人的配合。\n(2)护理员与老年人及家属进行交谈，评估老年人的身体状况，了解老年人的排便习惯等。\n\n步骤3 摆放体位\n护理员协助老年人取左侧卧位。脱下裤子至大腿部，暴露臀部，臀下垫橡胶单或一次性尿垫，在进行该操作时应注意应使老年人舒适\n\n特别提示 注意为老年人保暖，必要时遮挡老年人，保护其隐私。\n\n步骤4 人工取便\n护理员右手戴手套，左手分开老年人臀部，右手食指涂肥皂液润滑后，嘱咐老年人深呼吸以放松腹肌，待肛门松弛时，食指沿直肠一侧轻轻插人老年人直肠内，慢慢地由浅人深地将粪便一块一块地掏出，并放于便盆内。\n\n步骤5 清洁整理\n(1)护理员帮助老年人取便完毕后，用温水清洁其肛门，并用卫生纸为其擦净肛门。\n(2)护理员整理好用物，洗手，按要求做好记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a340cb1a-ef1c-11ed-8dee-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '观察并记录留置导尿老年人的尿量及颜色', 1, 1, 0, '观察并记录留置导尿老年人的尿量及颜色\n\n步骤1 准备工作\n(1)护理员着装整齐，洗净双手，戴好口罩。\n(2)准备好笔和记录单。必要时备好量杯和便盆。\n\n步骤2 沟通\n(1)护理员向老年人说明操作的目的，以取得合作。\n(2)护理员询问老年人有无腹胀等不适，并观察老年人的精神状态是否有异常情况。\n\n步骤3 观察尿量\n护理员视线与尿袋中尿液液面平齐，读取液面所对刻度即为尿量。\n\n步骤4 观察尿液颜色\n将尿袋平对白色或无色背景，观察尿液颜色。\n\n步骤5 记录报告\n将尿量、尿液颜色登记于登记表上，若有异常，应及时报告相关人员，必要时及时就医。', '1 准备工作\n(1)护理员着装整齐，洗净双手，戴好口罩。\n(2)准备好笔和记录单。必要时备好量杯和便盆。\n\n步骤2 沟通\n(1)护理员向老年人说明操作的目的，以取得合作。\n(2)护理员询问老年人有无腹胀等不适，并观察老年人的精神状态是否有异常情况。\n\n步骤3 观察尿量\n护理员视线与尿袋中尿液液面平齐，读取液面所对刻度即为尿量。\n\n步骤4 观察尿液颜色\n将尿袋平对白色或无色背景，观察尿液颜色。\n\n步骤5 记录报告\n将尿量、尿液颜色登记于登记表上，若有异常，应及时报告相关人员，必要时及时就医');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a340f23b-ef1c-11ed-a117-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '为老人更换尿垫（尿布）', 0, 1, 0, '\n1.定时查滑尿垫浸湿情况，根据尿垫吸收锁水的能力进行更换，防止发生尿布疹及压疮。\n2.更换一次性尿垫（尿布）时，动作轻稳，避免老年人受凉。\n3.为老年人更换一次性尿垫（尿布）时应使用温热毛巾擦拭或清洗会阴部，减轻异味，保持局部清洁干燥。\n4.当老年人患有传染性疾病时，一次性尿垫应放人医用黄色垃圾袋，作为医用垃圾集中回收处理', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净并温暖双手，必要时戴口罩\n(3)物品准备。一次性尿垫（尿布）、屏风、水盆、温热毛巾。\n\n步骤2 沟通\n查看并向老年人解释需要更换一次性尿垫（尿布），以取得合作。\n\n步骤3 更换尿垫\n护理员将水盆、毛巾放在床旁座椅上。掀开老年人下身盖被，双手分别扶住老年人的肩部、葡部翻转其身体呈侧卧位，将身下污染的一次性尿垫（尿布）向侧面方向折叠，取温湿毛巾擦拭会阴部；观察老年人会阴部及臀部皮肤情况。将清洁的一次性尿垫（尿布）一半平铺，一半卷折，翻转老年人身体呈平卧位，撤下污染的次性尿垫（尿布）放入专用污物桶。整理拉平清洁一次性尿垫（尿布）。盖好盖被。\n\n步骤4 整理\n护理员整理老人床位，开窗通风。清洗毛巾，刷洗水盆。尿布需要集中清洗消毒隙干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3414046-ef1c-11ed-bac4-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '为老年人更换纸尿裤', 0, 1, 0, '\n1.更换纸尿裤时，将纸尿裤大腿内、外侧边缘展平，防止侧漏\n2.根据老年人胖瘦情况选择适宜尺寸的纸尿裤\n3.老年人使用纸尿裤，每次更换或排便后应使用温热毛巾擦拭或清洗会阴部，或轻异味，保持局部清洁干燥。\n4.当老年人患有传染性疾病时，纸尿裤应放人医用黄色垃圾袋，作为医用垃圾集中回收处理', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净并温暖双手。必要时戴口罩。\n(3)物品准备。纸尿裤、卫生纸、屏风、水盆、温热毛巾。\n\n步骤2 沟通\n查看并向老年人解释需要更换纸尿裤，以取得合作。\n\n步骤3 更换纸尿裤\n护理员将水盆、毛巾放在床旁座椅上。掀开老年人下身盖被，协助老年人取平卧位，解开纸尿裤粘扣，将前片从两腿间后撤。双手分别扶住老年人的肩部、髋部翻转老年人身体呈侧卧位，将污染纸尿裤内面对折于臀下，取温湿毛巾擦拭会阴部；观察老年人会阴部及臀部皮肤情况。将清洁纸尿裤前后对折的两片（紧贴皮肤面朝内)平铺于老年人臀下，向下展开上片。协助老年人翻转身体至平卧位，从一侧撤下污染纸尿裤放人污物桶，并拉平身下清洁纸尿裤，从两腿间向上兜起纸尿裤前片，整理纸尿裤大腿内侧边缘至服帖，将前片两翼向两侧位紧，后片粘扣粘贴于纸尿裤前片粘贴区。盖好盖被。\n\n步骤4 整理\n护理员整理老年人床单位，开窗通风。清洗毛巾，刷洗水盆。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3416756-ef1c-11ed-ba08-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '使用开塞露辅助老年人排便', 0, 1, 0, '\n1.使用开塞露前，检查开塞露前端是否圆润光滑，以免损伤肛门周围组织。\n2.患有痔疮的老年人使用开塞露时，操作应轻缓并充分润滑。\n3.对本品过敏者禁用，过敏体质者慎用。\n4.开塞露不可长期使用，以免耐受而失去作用。\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净并温暖双手。必要时戴口罩。\n(3)物品准备。开塞露、剪刀、卫生纸、便盆、一次性尿垫。必要时备屏风。\n\n步骤2 沟通\n护理员向老年人说明操作方法、目的，消除其紧张、恐惧心理，以取得合作。\n\n步骤3 摆放体位\n护理员协助老年人将裤子脱至膝部，取左侧卧位，臀部靠近床边，臀下垫一次性尿垫。\n\n步骤4 注入药液\n护理员拧开开塞露的盖帽，左手分开老年人臀部，右手持开塞露塑料壳球部，挤出少量药液润滑开塞露前端及肛门口，再将开塞露细管部分沿直肠壁插人肛门内，叮嘱老年人深吸气，用力挤压开塞露塑料壳球部，将药液全部挤入肛门内。退出开塞露塑料壳，同时左手取用卫生纸按压肛门5分钟。叮刚老年人保持体位10分钟后再行排便，老年人主诉有便意时，指导其深呼吸，提肛（收紧肛门）。10分钟后护理员协助老年人排便。\n\n步骤5 整理与记录\n整理床单位，洗手。记录使用开塞露的量及排便情况（量及次数）。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3418eaf-ef1c-11ed-8f9d-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '协助老年人呕吐时变换体位', 0, 1, 0, '\n1.发现呕吐物颜色呈红色、黄绿色、咖啡色等，应保留呕吐物，通知医护人员查看。\n2.老年人呕吐协助变换体位时应避免动作过大，造成老年人身体伤害。\n3.呕吐后及时协助老年人漱口，消除口腔异味。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净双手。必要时戴口罩。\n(3)物品准备。水杯及漱口水、毛巾、痰盂、一次性护理垫。必要时备吸管屏风\n\n步骤2 沟通\n得知老年人发生呕吐，护理员立即来到老年人身边，用关怀的语气安慰老年不要紧张。将痰孟置于老年人面前地上盛接呕吐物，同时协助不能自理的老年人变换体位。\n\n步骤3 摆放体位\n既往身体状况良好、能自理的老年人：叮嘱其取坐位，身体稍前倾，双手扶税椅背或桌子、床沿等支撑物。护理员在旁边看护。\n呕吐症状轻者：护理员协助老年人取半卧位，头偏向一侧，口角边垫一次性打理垫，体弱、病重者：护理员协助老年人取侧卧位或仰卧位，头偏向一侧。口角边垫一次性护理垫。\n\n步骤4 漱口\n呕吐停止后，护理员立即取水杯协助老年人漱口。用毛巾擦净口角水痕。不能自己漱口的老年人应对其进行口腔擦拭。\n\n步骤5 清理\n护理员撤去一次性护理垫，整理老年人床单位。及时清理老年人呕吐物，必要时遵医嘱留取（呕吐物）标本。如有被服污染及时更换。开窗通风。\n\n步骤6 记录\n护理员洗净双手，对老年人呕吐情况进行记录。记录内容包括呕吐时间、呕吐物的性质、量及颜色等。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a341dc86-ef1c-11ed-8d39-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '帮助卧床老年人使用便盆', 0, 1, 0, '\n1.使用便盆前检查便盆是否洁净完好。\n2.协助老年人排便，避免长时间暴露老年人身体，导致老年人受凉。\n3.便盆及时倾倒并清洗消毒，避免污渍附着。\n4.为老年人放置便盆时不可硬塞，以免损伤其皮肤。\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风\n(2)护理员准备，服装整洁，洗净并温暖双手。必要时戴口罩\n(3)物品准备。便盆、一次性护理垫、卫生纸、屏风。必要时备温水、水盆毛巾。\n\n步骤2 沟通\n询问老年人是否有便意，提醒老年人定时排便\n\n步骤3 放置便盆\n(1)仰卧位放置便盆法。护理员协助老年人取仰卧位，掀开下身盖被折向远侧，协助其脱下裤子至膝部。叮嘱老年人配合屈膝拾高臀部，同时一手托起老年人的臀部，另一手将一次性护理垫垫于老年人臀下。再次要求老年人配合屈膝拾高暑部，同时一手托起老年人的臀部，另一手将便盆放置于老年人的臀下（便盆窄口朝向足部)。为防止老年人排尿溅湿盖被，可在会阴上部覆盖一张一次性护理垫。为老年人盖好盖被。\n(2)侧卧位放置便盆法。护理员将老年人裤子脱至膝部，双手扶住老年人的肩部及髋部翻转身体，使老年人面向自己呈侧卧位，掀开下身盖被折向自己一侧，暴露臀部，将一次性护理垫垫于老年人腰及臀下，再将便盆扣于老年人臀部（便盆窄口朝向足部) ，协助老年人恢复平卧位。在会阴上部覆盖一张一次性护理垫。为老年人盖好盖被。\n\n步骤4 撤去便盆\n老年人排便后，护理员一手扶稳便盆一侧，另一手协助老年人侧卧，取出便盆放于地上。取卫生纸为老年人擦净肛门。必要时用温水清洗肛门及会阴部并擦干。\n撤去一次性护理垫。\n\n步骤5 整理\n协助老年人卧位舒适，穿好裤子，整理床单位。必要时协助老年人洗手。开窗通风。观察、倾倒粪便。冲洗消毒便盆，晾干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3420396-ef1c-11ed-bbd8-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '帮助卧床老年人使用尿壶', 0, 1, 0, '\n1.老年女性使用尿壶时，应注意确定贴紧会阴部，以免漏尿打湿床单位。\n2.接尿时避免长时间暴露老年人身体，导致受凉。\n3.尿壶及时倾倒并清洗消毒，减少异味及尿渍附着。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风\n(2)护理员准备。服装整洁，洗净并温暖双手。\n(3)物品准备。便壶（男、女）、一次性护理垫、卫生纸。必要时备温水、水盆、毛巾。\n\n步骤2 沟通\n询问老年人是否有尿意。\n\n步骤3 放置尿壶\n护理员协助老年女性取仰卧位，掀开下身盖被折向远侧，协助其脱下裤子至膝部。订嘱老年人配合，屈膝拾高臀部，同时一手托起老年人的臀部，另一手将一次性护理垫垫于老年人臀下。叮嘱老年人屈膝，双腿呈八字分开，护理员手持尿壶将开口边缘贴紧阴部，盖好盖被。\n\n协助老年男性面向护理员取侧卧位，双膝并拢，将阴茎插人尿壶接尿口，用手握住尿壶把手固定，盖好被子\n\n步骤4 整理\n老年人排尿后，护理员撒下尿壶。用卫生纸擦干老年人会阴部，必要时，护理员为老年人清洗或擦拭会阴部。撒去一次性护理垫，协助老年人穿好裤子，整理床单位，必要时协曲老年人洗手。开窗通风，观繁，俪倒尿液，冲洗尿壶，隙干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3422aa8-ef1c-11ed-8fed-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '帮助老年人正常如厕', 0, 1, 0, '\n1.房间靠近卫生间，方便老年人如厕。\n2.卫生间设有坐便器并安装扶手，方便老年人坐下和站起。\n3.卫生用品放在老人伸手可以拿取的位置。\n4.保持卫生间地面整洁，无水渍，以免老年人滑倒。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。服装整洁，洗净双手。\n(3)物品准备。卫生间有坐便器及扶手设施、卫生纸，必要时床旁备坐便椅。\n\n步骤2 沟通\n询问老年人是否需要排便，根据老年人自理程度采取轮椅推行或搀扶方式。\n\n步骤3 协助如厕\n护理员使用轮椅推行或搀扶老年人进入卫生间，协助其转身面对护理员，双手扶住坐便器旁的扶手。护理员一手搂抱老年人腋下（或腰部），另一手协助老年人(或老年人自已)脱下裤子。双手环抱老年人腋下，协助老年人缓慢坐于坐便器上，双手扶稳扶手进行排便。老年人便后自己擦净肛门或身体前倾由护理员协助用手纸擦净肛门。老年人自己借助卫生间扶手支撑身体（或护理员协助老年人）起身，老年人自己（或护理员协助）穿好裤子。按压坐便器开关冲水。\n能采取坐位但行走不便的老年人，护理员可协助其在床旁使用坐便椅排便，方法同上。\n\n步骤4 整理\n护理员使用轮椅推行或搀扶老年人回房间休息，卫生间开窗通风或开启抽风设备清除异味，之后将其关闭。协助老年人使用坐便椅排便后，倾倒污物，清洗消毒便盆，晾干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3425200-ef1c-11ed-898e-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '采集老年人便标本', 0, 1, 0, '\n1.老年人发生腹泻时，应留取带有黏液或脓血部分的粪便；如为水样便，应使用大口径玻璃容器盛装送检。\n2.如检查项目为寄生虫卵，应取粪便不同部分适量，送检。\n3.如检查项目为阿米巴原虫，在采集前先用热水将便器加温后，再叮嘱老年人排便于盆内，便后立即送检。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净并温暖双手。必要时戴手套。\n(3)物品准备。清洁、干燥粘贴标签的便标本盒（见图1一16），化验单，便盆。\n\n步骤2 沟通\n护理员向老年人解释采集标本的内容、目的、要求，以取得配合。\n\n步骤3 采集便标本\n对能自理的老年人，可将标本盒交给老年人，向其讲解留取便标本的方法。即在排便后，用棉签取少量（量约蚕豆大小）感觉异常（如稀水样、黏液样、柏油样等)的粪便放入标本盒，盖上盒盖；对不能自理的老年人，由护理员协助老年人使用便盆排便，留取便标本方法同上。\n\n步骤4 整理\n护理员为老年人整理床单位，倾倒便盆，刷洗、消毒、晾干备用。\n\n步骤5 送检标本\n将便标本连同化验单一起送至化验室\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3429fd6-ef1c-11ed-b3dd-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '采集老年人尿标本', 0, 1, 0, '\n1.采集标本的容器应清洁干燥，一次性使用。\n2.不可将粪便或其他物质混人尿标本中。\n3.尿液标本收集后要立即送检，以避免发生细菌污染、化学物质及有形成分改变。\n4.自尿管留取尿标本注意应无菌操作，避免污染管路衔接处。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员淮备。服装整洁，洗净并温暖双手。必要时戴口罩。\n(3)物品准备。清洁、干燥尿杯（容量约30毫升）和粘贴标签的尿标本瓶，化验单、便盆、碘酊、棉签\n\n步骤2 沟通\n护理员拿到化验单后，及时告知老年人第二天晨起需要采集尿标本以及采集尿标本目的、要求，以便取得老年人的配合。\n\n步骤3 采集尿标本\n次日晨起协助老年人留取尿标本。\n(1)对于能自理的老年人。可将尿杯及标本瓶交给老年人，要求排尿前先清洁会阴部，见尿后使用尿杯接取尿液约30毫升放置一旁，排尿完毕整理衣裤。将尿杯中的尿液倒进标本瓶中，交予护理员。\n(2)对于不能自理的老年人。由护理员使用棉签蕾取碘酊为老年人消毒尿道口。\n1) 老年女性臀下垫便盆，见尿液流出，迅速使用尿杯接取尿液。将尿杯中尿液倒进标本瓶中放置妥当，排尿后协助撒下便盆，整理床单位\n2) 老年男性使用尿壶接取尿液，尿道口与尿壶之间保持约3~5厘米的距房见尿液流出，使用尿杯接取尿液约30毫升放置一旁。至老年人排尿完毕，协助理衣裤，再将尿杯中的尿液倒进标本瓶中。\n3) 对于留置尿管的老年人。反折导尿管，关闭尿袋上的放尿开关，分离导，管与尿袋的衔接处，使用碘酊消毒导尿管末端，便盆放于床上，打开导尿管放出高分尿液至便盆内。再次反折导尿管，将尿标本瓶或尿杯放置在导尿管末端接取尿物至足够量后反折导尿管。标本放置妥当。碘酊消毒导尿管末端及尿袋衔接端，再格尿袋衔接端插入导尿管内。打开尿袋上开关检查导尿管路是否通畅。整理床单位\n\n步骤4 整理\n护理员为老年人整理床单位，倾倒便器，刷洗、消毒、晾干备用。\n步骤5 送检标本\n将尿标本连同化验单一起送至化验室。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a342c6e5-ef1c-11ed-ac05-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '分析老年人排泄异常的表现和原因', 1, 1, 1, '\n1.态度认真，语言亲切。\n2.安慰老年人，勿让其担心、焦虑。\n', '1 工作准备\n(1)护理员准备。护理员着装整洁，洗净双手。\n(2)安置老年人。协助老年人平卧床上，盖好被子。\n(3)物品准备。准备记录单、笔\n(4)环境准备。谈话环境安静、整洁。\n\n步骤2 评估\n(1)护理员与老年人沟通，解释谈话目的。\n(2)评估老年人精神、心理状况，了解老年人排便或排尿异常的表现\n(3)详细询问进水、进食、用药情况以及既往病史，了解有无受凉、饮食不洁。\n\n步骤3 分析判断\n根据老年人的表现及疾病情况，初步判断老年人排泄异常的原因。\n\n步骤4 记录并报告\n(1)详细记录下老年人的精神状态、排泄异常的表现、可能的原因等。\n(2)及时报告家属或医护人员。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a342ee3f-ef1c-11ed-a4aa-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34007ed-ef1c-11ed-a816-8c554aa628fd', '3', '识别和记录老年人呕吐物异常并及时应对', 1, 1, 1, '识别和记录老年人呕吐物异常并及时应对\n\n步骤1 工作准备\n(1)护理员准备。护理员熟练掌握观察、记录呕吐物的方法，及应对老年人呕吐的措施。\n(2)物品准备。准备盛装呕吐物的容器、标本盒，以及记录单、笔。\n\n步骤2 评估观察\n(1)护理员立即为老年人摆体位。\n(2)保持呼吸道通畅。\n(3)评估呕吐物的性质、颜色、气味、量。\n(4)安慰老年人。\n\n步骤3 安置老年人\n(1)老年人停止呕吐后，及时擦净口角痕迹并漱口清洗口腔。\n(2)取舒适体位。\n\n步骤4 留取标本\n护理员戴手套取适量呕吐物放入标本盒内，以便送检。\n\n步骤5 记录报告\n(1)记录老年人呕吐的时间，呕吐物的性质、颜色、气味。\n(2)询问老年人进食、进水、用药治疗情况等并记录。\n(3)将记录内容报告给家属或医护人员。\n\n步骤6 整理用物\n及时处理污物，必要时更换衣物、床单，擦洗地面等，并开窗通风。\n', '1 工作准备\n(1)护理员准备。护理员熟练掌握观察、记录呕吐物的方法，及应对老年人呕吐的措施。\n(2)物品准备。准备盛装呕吐物的容器、标本盒，以及记录单、笔。\n\n步骤2 评估观察\n(1)护理员立即为老年人摆体位。\n(2)保持呼吸道通畅。\n(3)评估呕吐物的性质、颜色、气味、量。\n(4)安慰老年人。\n\n步骤3 安置老年人\n(1)老年人停止呕吐后，及时擦净口角痕迹并漱口清洗口腔。\n(2)取舒适体位。\n\n步骤4 留取标本\n护理员戴手套取适量呕吐物放入标本盒内，以便送检。\n\n步骤5 记录报告\n(1)记录老年人呕吐的时间，呕吐物的性质、颜色、气味。\n(2)询问老年人进食、进水、用药治疗情况等并记录。\n(3)将记录内容报告给家属或医护人员。\n\n步骤6 整理用物\n及时处理污物，必要时更换衣物、床单，擦洗地面等，并开窗通风。');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3433c62-ef1c-11ed-822a-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33fb9ef-ef1c-11ed-844a-8c554aa628fd', '2', '清洁照料', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a343632d-ef1c-11ed-bf5e-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人进行口腔护理', 1, 1, 0, '\n1.擦洗时动作要轻柔，防止碰伤老年人黏膜及牙龈。\n2.植物人或昏迷的老年人禁忌漱口，必要时使用张口器，张口器应从白齿处放人，牙关紧闭者不可暴力助其张口。\n3.擦洗时须用镊子（或止血钳）夹紧棉球，每次一个，防止棉球遗留在口腔内。\n4.棉球不可过湿，以防老年人将溶液吸入呼吸道。\n5.每次张口擦拭时间不可过长，应以20~25秒为限；擦拭上腭及舌面时，置不可以太靠近咽部，以免引起老年人恶心与不适。', '1 准备工作\n(1)护理员服装整洁，并洗净双手。\n(2)协助老年人平卧于床上。\n(3)物品准备。一次性使用口护包（弯盘2个、镊子2把、棉球数个、压舌板1根、垫巾1条)、手电筒1把，必要时备润唇油1支，选择合适的溶液。\n\n步骤2 沟通\n(1)护理员向老年人解释操作的目的，以取得老年人的合作。\n(2)护理员与老年人交谈，了解其身体状况能否配合口腔护理，用手电筒检查口腔黏膜有无损伤、义齿等。\n\n步骤3 摆放体位\n(1)护理员将物品置于便于使用的位置。协助老年人取侧卧位或平卧位，头偏向一侧，面朝向护理员，将毛巾铺在老年人颌下及胸前。\n(2)护理员将棉球点数后放于治疗碗中用溶液浸湿，把弯盘置于老人口角边。\n\n步骤4 观察口腔\n护理员嘱老年人张口，左手持压舌板，伸人老年人口腔并向下压舌体，右手持手电筒照射老年人口腔。观察老年人口腔内有无牙龈出血、感染等情况。\n\n步骤5 擦拭口腔\n(1)护理员双手各执一把镊子，左手镊子夹取浸湿的干净棉球，两只镊子绞棉球至不滴水为宜，首先擦拭并湿润口唇。\n\n特别提示 接触口腔的镊子不可与夹取干净棉球的镊子相接触，以避免污染\n\n(2)护理员用右手镊子夹紧棉球进行擦拭。每个棉球擦拭口腔一个部位。擦拭顺序为：叮嘱老年人牙齿咬合，擦拭牙齿外侧面（由内而外纵向擦拭至门齿）；叮嘱老年人张开口腔，分别擦拭牙齿各内侧面及咬合面；轻轻按压牙龈；分别擦拭两侧颊部；最后逐步擦拭上颚、舌面、舌下。\n(3)叮嘱老年人再次张开口腔，检查口腔是否擦拭干净。\n\n步骤6 整理用物\n用毛巾擦净老年人口角水痕，清点棉球数量。撤去用物。必要时向口唇涂擦润唇油。使老年人躺卧舒适。洗手。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3438a7f-ef1c-11ed-a4d5-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '对老年人房间进行终末消毒', 1, 1, 0, '\n1.操作过程中注意做好个人防护。穿工作服，戴好口罩、手套，必要时穿隔离衣。\n2.根据消毒剂的说明按要求配比、使用消毒剂。\n3.房间内的所用物品须经过终末消毒后方可进行清洁、处理。\n\n', '1 准备工作\n(1)护理员穿着工作服，衣帽整齐，戴口罩、手套，必要时穿隔离衣。\n(2)物品准备：紫外线灯、消毒液、抹布、水桶、污物袋。\n\n步骤2 消毒前准备\n护理员撤去被服，打开各种柜门、抽屉，翻转床垫，关闭门窗。\n\n步骤3 消毒房间\n护理员选用如熏蒸、紫外线照射等不同的方法首先对房间空气、物体表面消毒，然后用消毒液擦拭家具、床具、地面等。\n\n步骤4 消毒后处理\n打开门窗通风，铺好床单位，整理用物备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a343d2bc-ef1c-11ed-b62b-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '对老年人进行床旁隔离', 1, 1, 0, '\n1.每天对使用的物品按要求进行消毒。\n2.教育探视家属在探望老年人前后应洗手。\n3.要尊重被隔离的老年人。', '1 准备工作\n(1)护理员服装整洁，戴好帽子、口罩。\n(2)环境准备：使老年人独居一室，或者将其床单位安置在整个房间的一角。\n(3)使用物品包括：隔离标志、警示标牌、体温计、血压计、听诊器、便器、快速手消毒剂、医用垃圾桶、医用垃圾袋、屏风，必要时准备隔离衣。\n\n步骤2 沟通\n护理员应与老年人交谈，告知老年人床边隔离的目的，消除老年人的恐惧心理，取得老年人配合。\n\n步骤3 调整环境\n若有条件可使老年人独居一室；无条件的情况下可将老年人的床单位安置在整个房间的一角。\n\n步骤4 做好标识\n(1)在房门和老年人床头卡粘贴隔离标识，提醒无关人员勿人。\n(2)将准备好的用物放在指定地点，专人专用。如体温计、血压计、听诊器、清洁物品及便器等，所用物品上要做好标识。\n\n步骤5 实施隔离\n(1)护理员为老年人进行照护时应戴手套，必要时穿隔离衣。\n(2)先为其他老年人提供照料，被隔离者安排在最后。\n(3)照料完毕后，护理员应脱去手套后消毒双手。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34409bd-ef1c-11ed-af80-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为卧床老年人更换被服', 0, 1, 0, '\n1.协助老年人翻身侧卧时，应注意老年人安全，防止发生坠床。必要时使用床挡。\n2.扫床时，每扫一刷要重叠上一刷的1/3，避免遗漏渣屑。\n3.一床一刷套，不可重复交叉使用。\n4.更换被罩时，避免遮住老年人口鼻。\n5.将棉胎装人被罩内，被头部分应充实，不可有虚沿。\n6.套好的枕头应四角充实，枕套开口背门。\n7.操作动作轻稳，不要过多暴露老年人身体，以免受凉。\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，戴帽子、口罩。\n(3)老年人准备。老年人平卧于床上，盖好被子。\n(4)物品准备。扫床车1辆、床刷1把、刷套数个、脸盆2个（分别盛装洁净、污染的刷套)，清洁床单、被罩、枕套数个。\n\n步骤2 沟通\n备齐用物，推车进人老年人居室。向老年人解释，取得老年人配合。关闭门窗。\n\n步骤3 更换床单\n(1)将物品按使用顺序码放在床尾椅子上（上层床单，中层被罩，下层校套）。\n(2)护理员站在床的右侧，一手托起老年人头部，一手将枕头平移向床的侧，协助老年人翻身侧卧至床的左侧（背向护理员）盖好被子，必要时对侧安挡。从床头至床尾，松开近侧床单，将床单向上卷起直至人老年人身下。\n(3)从脸盆中取刷套套在床刷上，靠近床中线清扫褥垫上的渣屑，从床头扫床尾，每扫一刷要重叠上一刷的1/3，避免遗漏。\n(4)取清洁床单，床单的纵向中线对齐床中线，展开近侧床单平整铺于床上，余下的一半塞于老年人身下，分别将近侧床单的床头床尾部分反折于床褥下绷紧床单，在将近侧下垂部分的床单平整塞于床褥下。\n(5)将枕头移至近侧，协助老年人翻转身体侧卧于清洁大单上（面向护理员），盖好被子。必要时近侧安装床挡。\n(6)护理员转至床对侧，从床头至床尾松开床单，将床单向上卷起，再将污单从床头、床尾向中间卷起放在污衣袋内。清扫褥垫上的渣屑(方法同前)，撒下刷套，放在另一脸盆中\n(7)拉平老年人身下的清洁床单，平整铺于床褥上，方法同前。协助老年人平卧于床中线上。盖好被子\n\n步骤4 更换被罩\n(1)护理员站在床右侧，将盖于老年人身上的棉被两侧及被尾展开。打开被罩被尾开口端，一手揪住被罩边缘，一手伸入被罩中分别将两侧被胎向中间对折；一手抓住被罩被头部分，一手抓住被胎被头部分，将被胎呈S形从被罩中撤出，折叠置于床尾。被罩仍覆盖在老年人身体上。\n(2)取清洁被罩平铺于污被罩上，被罩中线对准床中线。床罩的被头部分置于老年人颈部。打开清洁被罩被尾开口端，一手抓住棉胎被头部分将棉胎装人清洁被罩内，在被罩内将棉胎向两侧展开。从床头向床尾方向翻卷撤出污被罩，放在污衣袋内\n(3)棉被纵向两侧分别内折（成被筒），被尾向内反折至整齐。\n\n步骤5 更换枕套\n(1)护理员一手托起老年人头部，另一手撤出枕头。\n(2)将枕芯从枕套中撤出，将污枕套放在污衣袋内。\n(3)在床尾部，取清洁枕套反转内面朝外，双手伸进枕套内撑开揪住两内角。\n(4)抓住枕芯两角，反转枕套套好。\n(5)将枕头从老年人胸前放至左侧头部旁边，护理员右手托起老年人头部，左手将枕头拉至老年人头下适宜位置。枕套开口应背门（必要时，为老年人更换衣裤)。\n\n步骤6 整理用物\n护理员开窗通风，洗净双手。将更换下的被服统一洗涤、消毒。使用过的床套集中使用含氯消毒剂浸泡30分钟，清洗晾干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3443132-ef1c-11ed-8884-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为卧床老年人翻身预防压疮', 0, 1, 0, '\n1.翻身时应将老年人拾起，避免拖、拉、推等动作，以免挫伤皮肤。\n2.卧床老年人，一般情况下2小时翻身1次，必要时1小时翻身1次\n3.记录准确、全面。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。护理员衣着整齐，洗净并温暖双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。软枕数个、脸盆（盛装50℃温水）1个、毛巾1条、记录单、融流超蓝是选笔，必要时备床挡。\n\n步骤2 沟通\n评估老年人营养状态，以及身体受压部位皮肤情况。向老年人解释操作方法，以取得配合。\n\n步骤3 协助侧卧\n(1)护理员将手伸进盖被内轻握老年人近侧手臂放于近侧枕边，远侧手臂放于胸前。在盖被内将远侧下肢搭在近侧下肢上。护理员双手分别扶住老年人的肩和髋部向近侧翻转，使老年人呈侧卧位。双手环抱住老年人的臀部移至床中线位置。\n(2)在老年人胸前放置软枕，上侧手臂搭于软枕上。上侧小腿中部垫软枕。保持体位稳定舒适。\n(3)掀开老年人背部盖被，检查背臀部皮肤情况。\n(4)护理员用温热毛巾擦拭老年人背臀，拉平上衣。用软枕支撑背部，盖好盖被。\n\n步骤4 整理床铺\n整理床铺。必要时加装床挡。\n\n步骤5 记录\n护理员洗净双手，进行记录。记录内容包括翻身时间、体位、皮肤情况（潮湿、压红，压红消退时间，水泡、破溃、感染等)。发现异常及时报告。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3446c86-ef1c-11ed-b8ef-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人修剪指（趾）甲', 0, 1, 0, '\n1.老年人沐浴后指（趾）甲较软，便于修剪。\n2.日常修剪指（趾）甲，遇老年人指（趾）甲较硬时，可用温热毛巾包裹片刻，再进行修剪。\n3.修剪指（趾）甲时，要避免损伤皮肤。\n4.修剪完毕的指（趾）甲边缘要光滑，不可有毛刺。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。护理员衣着整齐，洗净双手。女。\n(3)老年人准备。老年人取坐位或卧位。\n(4)物品准备。指甲刀、纸巾。\n\n步骤2 沟通\n向老年人解释，以取得合作。\n\n步骤3 修剪指（趾）甲\n护理员在老年人手（或足）下铺垫纸巾。左手握住老年人一只手（或足）的手指（脚趾），右手持指甲刀（弧形），修剪指甲长度与指端平齐或稍短一些为宜。逐一修剪。手指圆剪，脚趾平剪。\n\n步骤4 锉平指（趾）甲边缘\n用指甲锉逐一修理锉平指（趾）甲边缘毛刺。\n\n步骤5 整理用物\n用纸巾包裹指（趾）甲碎屑并丢入垃圾桶内，整理床铺。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34493fd-ef1c-11ed-8c36-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人修饰仪容仪表', 0, 1, 0, '营\n1.要根据老年人的健康状况、文化素养等协助整理仪容仪表。\n2.仪容仪表要求做到干净、整洁。\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。护理员衣着整齐，洗净双手。\n(3)老年人准备。老年人取坐位或卧位。\n(4)物品准备。镜子1块、毛巾1条、梳子1把、适宜服装（自备）。\n\n步骤2 检查修饰仪容\n检查老年人仪容是否干净。修饰仪容，可用毛巾擦拭去除眼角、口角及鼻孔的分泌物。修剪指甲，头发梳理整齐。\n\n步骤3 检查修饰仪表\n检查老年人仪表是否整洁。修饰仪表，可根据时间、地点、场合选择适宜着装，掸去衣服上的头屑、脱落头发。衣着整洁，无污渍。\n\n步骤4 进一步修饰\n协助老年人照镜子，根据老年人要求做进一步修饰，满足老年人精神需求，使老年人满意。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a344c4c8-ef1c-11ed-a9e9-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人坐位洗发', 0, 1, 0, '\n1.洗发过程中，观察并询问老年人有无不适，以便及时调整操作方法\n2.注意室温、水温变化，及时擦干头发，防止老年人着凉。\n3.洗发操作轻快，减少老年人的不适和疲劳。\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。老年人坐在椅子上。\n(4)物品准备。毛巾1条、洗发液1瓶、梳子1把、脸盆1个、暖瓶1只、水壶1个（盛装40~45℃温水）、方凳1个。必要时备吹风机1个。\n\n步骤2 沟通\n向老年人解释，以取得合作。备齐用物，携用物至床旁。\n\n步骤3 摆放体位\n协助老年人取坐位，颈肩围上毛巾，面前方凳上放置脸盆，叮嘱并协助老年人双手扶稳盆沿，低头闭眼，头部位于脸盆上方。\n\n步骤4 协助洗发\n护理员一手持水壶缓慢倾倒，另一手揉搓头发至全部淋湿。头发上涂擦洗发液，双手指腹揉搓头发，按摩头皮（力量适中，揉搓方向由发际向头顶部）。同时观察并询问老年人有无不适。一手持水壶缓慢倾倒，另一手揉枕头发至洗发液全液，双冲洗干净观察并询\n\n步骤5 擦干头发\n取颈肩部毛巾擦干面部及头发，必要时用吹风机吹干头发，将头发梳理整齐\n\n步骤6 整理用物\n协助老年人上床休息，清理用物。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a344f2f6-ef1c-11ed-b6f6-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人床上擦浴', 0, 1, 0, '\n1.擦浴过程中，动作要轻稳，老年人身体暴露部位要及时遮盖，以防着凉。\n2.随时更换温水，注意调整水温。\n3.擦洗过程中，观察老年人反应，如出现寒战、面色苍白等情况，要立即停止擦浴，进行保暖，通知专业医护人员。\n4.清洗会阴部、足部的水盆和毛巾要分开单独使用。\n', '1 工作准备\n(1)环境准备。环境整洁，将浴室温度调节为24~26℃，将水温调节为40℃左右（手伸进水中，温热不烫手）。关闭门窗，屏风遮挡。\n(2)护理员准备。护理员衣着整齐，洗净双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。脸盆3个（身体、臀部、脚）、毛巾3条（身体、臀部、脚）、方毛巾1条、浴巾1条、浴液1瓶、椽胶单1块、清洁衣裤1套、暖瓶1个、污水桶1个、橡胶手套1副。必要时备屏风等。\n\n步骤2 沟通\n评估老年人身体状况、疾病情况，判断是否适宜床上擦浴。对神志清楚的老年人征得其同意，以取得配合。备齐用物携至床旁（多人同住一室时，用屏风遮挡）。在脸盆内盛装40~45℃温水，协助老年人脱去衣裤，盖好被子。\n\n步骤3 顺序擦浴\n(1)擦洗面部。将浴巾覆盖在枕巾及胸前被子上。\n1)擦洗眼睛。护理员将方毛巾浸湿后拧干，横向对折再纵向对折。用对折后的小毛巾的四个角分别擦洗双眼的内眼角和外眼角。\n2)方毛巾包裹在手上。包裹方法：方毛巾的左右两边绕开拇指折向手心，前端下垂部分对齐折向手掌，并校于掌根毛巾边缘内。涂上浴液进行擦拭。\n①④额部。额部由额中间分别向左再向右擦洗。\n②鼻部。由鼻根擦向鼻尖。\n③面類。由鼻翼一侧向下至鼻后部横向镶，沿一侧后角向下，再横向擦轼下颏，顺向斜上方擦拭颊部，用同样方法擦拭另一侧。\n④颈部。由中间分别向左再向右擦洗。\n洗净方毛巾，用同样手法擦净脸上浴液，再用浴巾擦干脸上的水气。\n\n(2)擦拭手臂。暴露老年人近侧手臂，护理员将浴巾半铺半盖于手臂，方毛巾包手，涂上浴液，打开浴巾，由前臂向上臂擦拭。擦拭后浴巾遮盖，洗净方毛巾。同样手法手法擦净上臀浴液，再用浴巾包裹沾干手臂上的湿气，盏被盖严手臂。用同样手法擦拭另一侧手臂。\n\n(3)擦拭胸部。护理员将老年人盖被向下折叠暴解胸部，用浴巾遮盖胸部。洗净方毛巾包寒在手上，涂上浴液，打开浴巾由上向下擦拭胸部及两侧，注意擦降肤皱褶处（如腋窝、女性乳房下垂部位），擦拭后浴巾遮盖，洗净方毛巾，用同手法蒸净胸部浴液，再用浴巾沾干肉部湿气。\n(4)擦拭腹部。护理员将老年人盖被向下折至大腿上部，用浴巾遮盖胸腹部。洗净方毛巾包裹在手上，涂上浴液，打开浴巾下角暴露腹部，顺时针螺旋形擦拭腹部及两侧腰部，擦拭后浴巾遮盖，洗净方毛巾，用同样手法擦净腹部浴液，再用浴巾沾干腹部湿气。盖好盖被。\n(5)擦拭背臀部。护理员协助老年人翻身侧卧，背部朝向护理员，将被子向上折起暴露背臀部。浴巾铺于背臀下，向上反折遮盖背臀部。洗净方毛巾包裹在手上，涂上浴液，打开浴巾暴露背臀部，由腰骶部沿脊柱向上至肩颈部，再螺旋向虾擦洗背部一侧，用同样方法擦洗另一侧。分别环形擦洗两侧臀部，擦拭后浴巾道盖，洗净方毛巾，用同样手法擦净背臀部浴液，再用浴巾沾干背臀部湿气。撤去浴巾，协助老年人取平卧位，盖好盖被。\n(6)擦洗下肢。暴露一侧下肢，浴巾半铺半盖。洗净方毛巾并包裹在手上，涂上浴液，打开浴巾暴露下肢，一手扶住老年人下肢的躁部呈屈膝状，另一手由小腿向大腿方向进行擦洗，擦拭后用浴巾遮盖，洗净方毛巾，用同样手法擦净下肢浴液，再用浴巾擦干下肢上的水气。用同样手法擦洗另一侧下肢。\n在上述操作过程中，边擦拭边观察老年人有无不适，并随时添加热水保持水温和更换清水。\n(7)清洗足部。护理员更换水盆（脚盆），将40一45℃温水盛装至水盆的1/2处。将老年人被子的被尾向左侧打开暴露双足，取软枕垫在老年人膝下支撑。足下铺橡胶单，水盆放在橡胶单上，将老年人左足在水中浸湿，抬起涂擦浴液并揉搓，再放入水盆中浸泡，擦洗干净（注意洗净趾缝），用专用毛巾擦干足部，放入盖被内。用同样手法清洗右侧足部，如图1一50所示。撤去水盆、橡胶单，盖好盖被。\n(8)擦洗会阴。护理员更换水盆（专用盆），盛装40~45℃温水。一手托起老年人臀部，一手铺垫橡胶单和浴巾（也可以协助老年人侧卧，铺垫橡胶单和浴巾，在协助老年人平卧)，戴好橡胶手套，将专用毛巾浸湿拧于。老年女性：擦洗由阴阜向下至尿道口、阴道口、肛门，边擦洗边转动毛巾，投洗毛巾分别擦洗两侧腹股沟部位。老年男性：擦洗由尿道外口、阴茎、阴囊、腹股沟和肛门。随时投洗毛巾。直至清洁无异味。撒去橡胶单和浴巾，协助老年人更换清洁衣裤。为老年人盖好盖被，开窗通风。\n\n步骤4 整理用物\n护理员将用物放回原处，刷洗水盆，擦干地面水渍，清洗浴巾、毛巾及老年人换下的衣裤\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3452783-ef1c-11ed-96c7-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人床上洗发', 0, 1, 0, '\n1.洗发过程中，观察并询问老年人有无不适，以便及时调整操作方法。\n2.注意室温、水温变化，及时擦干头发，防止老年人着凉。\n3.洗发操作轻快，减少老年人的不适和疲劳。\n4.防止水流入服、耳内或打湿被服。如果打湿，及时更换。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净双手2.\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。洗头器1个、毛巾1条、洗发液1瓶、梳子1把、暖瓶1个，水壶或水杯1个（盛装40一45℃温水）、污水桶1个。必要时备吹风机1个。\n\n步骤2 沟通\n评估老年人身体状况、疾病情祝，是否适宜床上洗头。向老年人解释，询问老年人是否需要便器，以取得合作。备齐用物，携带用物至床旁。\n\n步骤3 放置洗头器\n护理员一只手托起老年人头部，另一只手撤去枕头，放置简易洗头器，使老年人脖颈枕于简易洗头器凹槽上，洗头器排水管下接污水桶。在颈肩部围上毛巾。\n\n步骤4 床上洗发\n护理员一只手持水壶缓慢倾倒，另一手揉搓头发至全部淋湿。头发上涂擦洗发液，双手指腹揉搓头发、按摩头皮（力量适中，揉搓方向由发际向头顶部）。同时观察并询问老年人有无不适。揉搓完毕，一手持水壶缓慢倾倒温水，另一手揉搓头发至洗发液全部冲洗干净。\n\n步骤5 擦干头发\n护理员取老年人颈肩部毛巾擦干老年人面部水痕，再用毛巾包裹头部，撤去简易洗头器，充分擦干头发，垫好枕头。必要时用吹风机吹干头发。将头发梳理整齐。\n\n步骤6 整理用物\n整理床铺，倾倒污水，用物放回原处备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3454f0e-ef1c-11ed-9f17-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人摘戴清洁义齿', 0, 1, 0, '\n1.对意识不清的老年人应将义齿取下，刷洗干净，放于清洁冷水杯内保存。\n2.义齿不可浸泡在热水、酒精中保存。\n3.佩戴义齿的老年人不宜咀嚼过硬或过黏的食物。\n4.摘戴义齿，不可用力过大，以免损伤牙龈。摘取不下来时可轻推卡环。\n5.佩戴义齿时叮嘱老年人不要用力咬合，以防卡环变形或义齿折断。\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。\n服装整洁，洗净并温暖双手。必要时戴口罩。\n(3)老年人准备。老年人取坐位或卧位。\n(4)物品准备。水杯1个、纱布数块。\n\n步骤2 沟通\n向老年人解释，以取得合作。\n\n步骤3 摘取义齿\n护理员叮嘱老年人张口，一手垫纱布轻轻拉动义齿基托将义齿取下。上牙轻轻向外下方拉动，下牙轻轻向外上方拉动。上下均为义齿，先摘取上方，再摘取下方。清洗义齿后将其放于清洁冷水杯中存放。\n\n步骤4 佩戴义齿\n护理员将盛装义齿的水杯在流动自来水下冲洗后，放于老年人床头桌上。叮嘱老年人张口，一手垫纱布取义齿，轻轻上推义齿基托将义齿戴上。町嘱老年人上下齿轻轻咬合数次，使义齿与牙组织完全吻合。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34575c0-ef1c-11ed-b86b-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人整理床单位', 0, 1, 0, '\n1.护理员扫床需要藏口罩。\n2.床套在使用时每床一只，不可重复使用\n3.床套使用后可用含氯消毒剂浸泡30分钟，清洗晾干备用。', '1 工作准备\n(1)环境准备。环境整洁，\n(2)护理员准备。服装整洁，戴口罩、帽子\n(3)物品准备。扫床车1辆、床刷1把、刷套数个，脸盆2个（分别盛装洁净、污染的刷套)\n\n步骤2 折叠棉被\n将用物放置于扫床车上，推车进人老年人居室。将棉被折叠成方块状，放置在床旁椅子上。将枕头放在棉被上。\n\n步骤3 整理床单\n将一侧床头部位床单反折于床褥下压紧，再将床尾部床单抻平反折于床褥下同样方法铺好床单另一侧，使床单平整紧细于床褥上。\n\n步骤4 清扫床铺\n取床刷套好刷套。从床头纵向扫床至床尾，每扫一刷要重叠上一刷的1/3，避免遗漏。\n\n步骤5 整理\n撒下刷套，放在另一脸盆中。整理枕头至蓬松放置在床头枕套开口应背门，棉被放置于床尾。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a345c98a-ef1c-11ed-95e5-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人晨间梳洗', 0, 1, 0, '\n1.水温不可过热，以防烫伤。\n2.脸盆摆放平稳，避免打湿被褥和衣物\n3.梳理动作要轻稳，不可以强拉硬拽。\n4.头发缠绕成团不易梳通时，可蘸水或涂抹少量白酒湿润后再小心梳理。\n5.头发较长者可分段梳理，先梳理靠近发梢的一段，梳通后，再由发根部分梳理至发梢。\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。脸盆（内盛1/2盆的40℃的温水）1个、毛巾1条、香皂1块、润肤油1盒、梳子1把、床旁椅子1把。\n\n步骤2 沟通\n护理员携带用物至老年人床旁，将脸盆放在床旁椅子上。向老年人解释准备洗漱，以取得合作。老年人取坐位或卧位。\n\n步骤3 协助洗脸\n护理员将毛巾围在老年人胸前，一手扶住老年人肩部，一手用清水将老年人面部打湿并涂擦香皂，反复多次用清水将老年人面部皂液洗净，取毛巾擦干面部。\n\n步骤4 协助洗手\n护理员牵拉老年人一只手臂于水盆上，用水打湿，涂擦香皂，反复多次用水装老年人手臂皂液洗净并擦干。在面部及双手涂擦润肤油。\n\n步骤5 清洁口腔\n操作方法详见本节学习单元2。\n\n步骤6 协助梳头\n(1)坐位梳头发。护理员将毛巾围于老年人肩上将头发散开，左手压住发根右手梳理头发至整齐。头发较长不易梳通时，可分段梳理，先梳理靠近发梢的段。梳理通顺后，再从发根梳理至发梢。毛巾卷起撤下。\n(2)卧位梳头发。护理员一手托起老年人头部，一手将毛巾铺在枕巾上。叮嘱双手并协助老年人头偏向一侧，梳理方法同前。梳完一侧，将头部转向另一侧，用同样方法梳理另一侧至整齐。护理员一手托起老年人头部，一手将毛巾卷起撤下。\n\n步骤7 整理用物\n整理床单位。倾倒污水，抖落毛巾上的头屑及脱落的头发，清洗毛巾，晾干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a346063a-ef1c-11ed-be42-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年人清洁义齿', 0, 1, 0, '\n1.刷洗义齿的牙刷的刷毛不可太过坚硬，以免损伤义齿表面。\n2.义齿的各个面均应刷洗干净。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。关闭门窗，必要时遮挡屏风。\n(2)护理员准备。服装整洁，洗净双手\n(3)物品准备。义齿、水杯1个、软毛牙刷1把、自来水设备、义齿清洗剂或假牙清洁片、纱布数块。\n\n步骤2 刷洗义齿\n护理员在晚间或老年人除前协助其取下义齿，放置于水杯中，打开水龙头，左手热炒布粗住义传，右手用牙副侧去义齿上的食物残屑并冲洗干净\n\n步骤3 漫泡义齿\n护理员制洗水杯，取义齿清洗液5一10毫升倒人杯中，加人温水至液面浸没文齿。未使用义齿清洗液可直接在水杯中感装清洁冷水，将义街浸泡其中。\n\n步骤4 刷洗义齿\n次日，用流动水冲洗，同时用牙刷刷去义齿上浮垢至清洁，再协助老年人戴上义齿。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a346349b-ef1c-11ed-9144-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年女性冲洗会阴', 0, 1, 0, '\n1.操作前护理员要洗净双手。\n2.便盆不可硬塞于老年人臀下，以免挫伤骶尾部皮肤。\n3.冲洗时缓慢倒水，避免打湿被褥。\n4.擦拭的毛巾应专用。\n\n', '1 工作准备\n(1)环境准备。环境整洁，将室内温度调节为24~26℃，将冲洗壶内水温调节为40℃左右（手伸进水中，温热不烫手）。关闭门窗，屏风遮挡。\n(2)护理员准备。护理员衣着整齐，洗净双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。冲洗壶1个（内盛40~45℃温水）、专用毛巾1条、一次性橡胶手套1副、一次性尿垫1张、浴巾1条、便盆1个、屏风1个。\n\n步骤2 沟通\n护理员向老年人解释会阴冲洗目的及方法，以取得配合。\n\n步骤3 摆放体位\n护理员掀开老年人近侧盖被下端，在臀下放置便盆（按照仰卧位或侧卧位放置便盆方法)，戴好橡胶手套，协助老年人取仰卧屈膝位，盖被盖严远侧下肢，浴巾遮盖近侧肢体。\n\n步骤4 冲洗、擦干会阴\n护理员戴好橡胶手套，一手持冲洗壶，一手拿毛巾，边冲边擦洗会阴部，从阴单向下冲洗擦拭至肛门部以及大腿两侧腹股沟。撤去便盆，用毛巾擦干会阴部并检查会阴部皮肤状况。更换一次性尿垫，摘下一次性橡胶手套，撤下浴巾，为老年人盖好盖被。\n\n步骤5 整理用物\n整理床单位，倾倒便盆，刷洗消毒备用。用物放回原处。洗净毛巾，晾干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3466072-ef1c-11ed-8409-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '为老年男性剃须', 0, 1, 0, '\n1.剃须时，要绷紧皮肤，以免刮伤皮肤\n2.胡须较为坚硬时，可用温热毛巾热敷5~10分钟。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。护理员衣着整齐，洗净双手 \n(3)老年人准备。老年人取坐位或卧位\n(4)物品准备。电动剃须刀、毛巾、润肤油。\n\n步骤2 沟通\n向老年人解释，以取得合作。\n\n步骤3 剃须\n护理员在老年男性晨起清浩面部后进行削须。一手绷紧皮肤，一手打开电动须刀开关，以从左至右、从上到下的顺序荆须，荆须完毕，用毛巾擦拭剃须都位检查是否刮净，有无遗漏部位。涂擦润肤油。\n\n步骤4 整理用物\n用物放回原处，清洗毛巾，晾干备用。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3469e07-ef1c-11ed-85af-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '使用棉棒擦拭清洁口腔', 0, 1, 0, '\n1.棉棒蘸水后在杯壁上轻轻挤压，以免与牙齿接触后，漱口水挤出流入气管引起呛咳。\n2.一个棉棒只可使用一次，不可反复蘸取漱口水使用。\n3擦拭上聘及舌面时，位置不可以太靠近咽部，以免引起恶心、不适。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。服装整洁，洗净并温暖双手。必要时戴口罩。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。漱口杯1个、大棉棒1包、毛巾1条、污物碗1个，必要时备润唇油1支。\n\n步骤2 沟通\n向老年人解释，以取得合作。\n\n步骤3 摆放体位 \n备齐用物，携至床旁。协助老年人取侧卧位或平卧位，头偏向一侧（朝向护理员)。毛巾铺在老年人颌下及胸前，污物碗置于枕边\n\n步骤4 擦拭口腔\n每次取一根棉棒蘸适量漱口水擦拭口腔一个部位。首先擦拭湿润口唇；叮嘱老年人牙齿咬合，擦拭牙齿外侧面（由内而外纵向擦拭至门齿）；叮嘱老年人张开口腔，分别擦拭牙齿各内侧面、咬合面；轻轻按压牙龈；分别擦拭两侧颊部；最后逐步擦拭上颚、舌面、舌下。可嘱老年人再次张口，检查口腔是否擦拭干净。用毛巾擦净老年人口角水痕。\n\n步骤5 整理用物\n撒去用物，整理床单位。必要时口唇涂擦润唇油。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a346c74f-ef1c-11ed-939a-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '协助老人盆浴', 0, 1, 0, '协助老人盆浴\n\n步骤1 工作准备\n(1)环境雅备。环境整洁，调节浴室温度为24~26℃。浴盆中放水至1/3一12满，水温约40℃左右（手伸进水中，温热不烫手），浴盆内放置防滑垫。关闭门窗，地面放置防滑垫。\n(2)护理员准备。护理员更换短袖上衣、短裤，洗净双手。\n(3)物品准备。浴盆设施、毛巾两条、浴巾1条、浴液1瓶、洗发液1瓶、清洁衣裤1套、梳子1把、座椅1把。必要时备吹风机1个。\n\n步骤2 沟通\n评估老年人身体状况、疾病情况，是否适宜盆浴。征得老年人同意后，备齐用物。搀扶（或用轮椅运送）老年人穿着防滑拖鞋进入浴室。\n\n步骤3 脱衣洗浴\n(1)护理员协助老年人脱去衣裤（一侧肢体活动障碍时，应先脱健侧，再脱患侧)，搀扶老年人进入浴盆坐稳，叮嘱老年人双手握住扶手或盆沿。\n(2)洗发，护理员叮嘱老年人头稍后仰，手持花洒淋湿头发，为老年人涂擦洗发液，双手指腹揉搓头发、按摩头皮（力量适中，揉搓方向由发际向头顶部）。同时观察并询问老年人有无不适，再用花洒将洗发液全部冲洗干净。\n(3)洗浴身体，浸泡身体后放掉浴盆中的水，由上至下涂抹浴液，涂擦颈部、耳后、胸腹部、双上肢、背部、双下肢，然后擦洗、会阴及臀下、双足。轻轻揉搓肌肤。最后护理员冲净双手，取少量浴液为老年人清洁面部，再用花洒将面部及全身浴液冲洗干净，关闭开关。\n\n步骤4 擦千更衣\n护理员用毛巾迅速擦干老年人面部及头发，用浴巾包裹身体，协助老年人出浴盆。擦干身体坐在浴室座椅上，协助老年人更换清洁衣裤（一侧肢体活动障碍时，应先穿患侧后穿健侧)，搀扶（或用轮椅运送）老年人回床休息。\n\n步骤5 整理用物\n护理员将用物放回原处，开窗通风。刷洗浴盆，擦干浴室地面，清洗浴巾、毛巾及老年人换下的衣裤。\n\n注意事顶\n1.浴盆内应放置防滑垫，以防老年人滑倒。\n2.老年人盆浴时间不可过长，水温不可过高，水量不可过多，以免引起不适。\n3.协助老年人盆浴时，随时询问和观察老年人的反应，如有不适，应迅速结束操作。告知专业医护人员。', '1 工作准备\n(1)环境雅备。环境整洁，调节浴室温度为24~26℃。浴盆中放水至1/3一12满，水温约40℃左右（手伸进水中，温热不烫手），浴盆内放置防滑垫。关闭门窗，地面放置防滑垫。\n(2)护理员准备。护理员更换短袖上衣、短裤，洗净双手。\n(3)物品准备。浴盆设施、毛巾两条、浴巾1条、浴液1瓶、洗发液1瓶、清洁衣裤1套、梳子1把、座椅1把。必要时备吹风机1个。\n\n步骤2 沟通\n评估老年人身体状况、疾病情况，是否适宜盆浴。征得老年人同意后，备齐用物。搀扶（或用轮椅运送）老年人穿着防滑拖鞋进入浴室。\n\n步骤3 脱衣洗浴\n(1)护理员协助老年人脱去衣裤（一侧肢体活动障碍时，应先脱健侧，再脱患侧)，搀扶老年人进入浴盆坐稳，叮嘱老年人双手握住扶手或盆沿。\n(2)洗发，护理员叮嘱老年人头稍后仰，手持花洒淋湿头发，为老年人涂擦洗发液，双手指腹揉搓头发、按摩头皮（力量适中，揉搓方向由发际向头顶部）。同时观察并询问老年人有无不适，再用花洒将洗发液全部冲洗干净。\n(3)洗浴身体，浸泡身体后放掉浴盆中的水，由上至下涂抹浴液，涂擦颈部、耳后、胸腹部、双上肢、背部、双下肢，然后擦洗、会阴及臀下、双足。轻轻揉搓肌肤。最后护理员冲净双手，取少量浴液为老年人清洁面部，再用花洒将面部及全身浴液冲洗干净，关闭开关。\n\n步骤4 擦千更衣\n护理员用毛巾迅速擦干老年人面部及头发，用浴巾包裹身体，协助老年人出浴盆。擦干身体坐在浴室座椅上，协助老年人更换清洁衣裤（一侧肢体活动障碍时，应先穿患侧后穿健侧)，搀扶（或用轮椅运送）老年人回床休息。\n\n步骤5 整理用物\n护理员将用物放回原处，开窗通风。刷洗浴盆，擦干浴室地面，清洗浴巾、毛巾及老年人换下的衣裤。\n\n注意事顶\n1.浴盆内应放置防滑垫，以防老年人滑倒。\n2.老年人盆浴时间不可过长，水温不可过高，水量不可过多，以免引起不适。\n3.协助老年人盆浴时，随时询问和观察老年人的反应，如有不适，应迅速结束操作。告知专业医护人员');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34706a0-ef1c-11ed-a58f-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '协助老年人更换开襟衣服', 0, 1, 0, '\n1.操作轻柔快捷，避免老年人受凉。\n2.协助老年人翻身时，注意安全，必要时安装床挡。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。护理员衣着整齐，洗净双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。老年人清洁开襟上衣。\n\n步骤2 沟通\n护理员为老年人选择合适的开襟上衣，向老年人解释，以取得配合。\n\n步骤3 更换开襟衣服\n(1)掀开盖被，解开上衣纽扣，一手扶住老年人肩部，另一手扶住髋部，协助老年人翻身侧卧，脱去一侧衣袖（遇老年人一侧肢体不灵活时，应卧于健侧，患侧在上，先脱患侧)。\n(2)取清洁开襟上衣穿好一侧（或患侧）的衣袖，其余部分（清洁的和被更换的上衣)平整地掖于老年人身下。\n(3)协助老年人取平卧位，从老年人身下拉出清洁的和被更换的上衣。脱下被更换的上衣。穿好清洁上衣另一侧衣袖（或健侧）。扣好纽扣。\n\n步骤4 整理上衣\n护理员拉平老年人上衣的衣身、衣袖，确保身下衣服无皱褶。整理衣领。\n\n步骤5 整理床铺\n护理员为老年人盖好盖被，整理床铺。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3473254-ef1c-11ed-9add-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '协助老年人更换裤子', 0, 1, 0, '\n1.操作轻柔快捷，避免老年人受凉。\n2.穿脱裤子不可硬拽，以免损伤老年人皮肤。\n\n', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。护理员衣着整齐，洗净双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。老年人的清洁裤子。\n\n步骤2 沟通\n护理员向老年人解释，取得合作。\n\n步骤3 脱下裤子\n(1)护理员为老年人松开裤带、裤扣。协助老年人身体左倾，将裤子右侧部分向下拉至臀下，再协助老年人身体右倾，将裤子左侧部分向下拉至臀下。\n(2)护理员叮嘱能够配合的老年人屈膝，两手分别拉住老年人两侧裤腰部分向下褪至膝部，抬起一侧下肢，褪去一侧裤腿。用同样方法褪去另一侧裤腿。\n\n步骤4 更换裤子\n(1)护理员取清洁裤子辨别正反面。左手从裤管口套入至裤腰开口，轻握老年人脚躁，右手将裤管向老年人大腿方向提拉。用同样方法穿上另一条裤管。\n(2)护理员两手分别拉住两侧裤腰部分向上提拉至老年人臀部。\n(3)协助老年人身体左倾，将右侧裤腰部分向上拉至腰部，再协助老年人身体右倾，将裤子左侧部分向上拉至腰部。系好裤带、裤扣。\n\n步骤5 整理床铺\n协助老年人盖好盖被，整理床铺\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3475ff0-ef1c-11ed-9480-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '协助老年人淋浴', 0, 1, 0, '\n1.老年人身体状况较好，要求单独洗浴时，浴室不要锁门，可在门外把手上悬挂示意标牌。护理员应经常询问是否需要帮助。\n2.浴室地面应放置好防滑垫，叮嘱老年人穿着防滑拖鞋，以防老年人滑倒。\n3.先调节水温再协助老年人洗浴。调节水温时，先开冷水后开热水。\n4.老年人淋浴时间不可过长，水温不可过高，以免发生头晕等不适。\n5.淋浴应安排在老年人进食1小时之后，以免影响消化吸收。\n6.淋浴过程中，随时询问和观察老年人的反应，如有不适，应迅速结束操作。告知专业医护人员。\n\n', '1 工作准备\n(1)环境准备。环境整洁，调节浴室温度为24一26℃。关闭门窗，放好洗澡椅，地面放置防滑垫。\n(2)护理员准备。护理员更换短袖上衣、短裤，洗净双手。\n(3)物品准备。淋浴设施、毛巾1条、浴巾1条、浴液1瓶、洗发液1瓶、清洁衣裤1套、梳子1把、洗澡椅1把、防滑拖鞋1双或防滑垫1块。必要时备吹风机1个。\n\n步骤2 沟通\n评估老年人身体状况、疾病情况，是否适宜淋浴。征得老年人同意后，备齐用系物。搀扶（或用轮椅运送）老年人穿着防滑拖鞋进入浴室。\n\n步骤3 坐稳洗浴\n(1)调节水温，先开冷水开关，再开热水开关（单把手开关由冷水向热水一侧调节)，调节水温约40℃左右为宜（伸手触水，温热不烫手）。\n(2)护理员协助老年人脱去衣裤（一侧肢体活动障碍时，应先脱健侧，再脱患侧)，搀扶老年人在洗澡椅上坐稳，叮嘱老年人双手握住洗澡椅扶手。\n(3)洗发。叮嘱老年人身体靠紧椅背，头稍后仰，手持花洒淋湿头发，为老年八涂擦洗发液，双手指腹揉搓头发、按摩头皮（力量适中，揉搓方向由发际向头顺部)。同时观察并询问老年人有无不适。再用花洒将洗发液全部冲洗干净。关闭开关，并用毛巾擦干面部及头发。\n(4)清洗身体。手持花酒淋湿老年人身体，由上至下涂抹浴液，涂擦颈部、耳关，并用毛巾擦干面部及头发。\n后、胸酸部。双版背部、双下成，然后擦洗会阴及臀下双足，轻经揉关肤。曼后护理员冲净夏手，敦少量合液为老年人清洁面部，再用花西将面部及全身浴液冲洗干净。关闭开关。\n\n步骤4 擦干更衣\n(1)护理员用毛巾迅速擦干老年人面部及头发，用浴巾包裹老年人身体。\n(2)协助老年人更换清洁衣裤（一侧肢体活动障碍时，应先穿患侧，再穿健侧)，搀扶（或用轮椅运送）老年人回屋休息。\n\n步骤5 整理用物\n护理员将用物放回原处，开窗通风。擦干浴室地面，清洗浴巾、毛巾及老年人换下的衣裤。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3478ee4-ef1c-11ed-a28a-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '协助老年人漱口', 0, 1, 0, '\n1.每次含漱口水的量不可过多，避免发生呛咳或误吸。\n2.卧床老年人漱口时，口角边垫好毛巾避免打湿被服。\n', '1 工作准备\n(1)环境淮备。室内环境清洁，温湿度适宜。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。水杯1个、吸管1根、弯盘或小碗1个、毛巾1条，必要时备润唇油1支。\n\n步骤2 沟通\n向老年人解释，以取得配合。\n\n步骤3 摆放体位\n协助老年人取侧卧位，抬高头胸部；或半坐卧位，头面部侧向护理员。将毛巾铺在老年人颌下及胸前部位，避免水渍打湿枕巾、被褥。将弯盘或小碗置于口角旁。\n\n步骤4 协助漱口\n水杯内盛接清水2/3满，递到老年人口角旁，直接含饮或用吸管吸引漱口水至口腔后闭紧双唇，用一定力量鼓动颊部，使漱口水在牙缝内外来回流动冲刷。倾吐漱口水至口角边的弯盘或小碗中，反复多次直至口腔清爽。用毛巾擦干口角水痕，必要时涂擦润唇油。\n\n步骤5 整理用物\n整理床单位，清理用物，放回原处\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a347bfff-ef1c-11ed-9c95-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a3433c62-ef1c-11ed-822a-8c554aa628fd', '3', '协助老年人穿脱套头上衣', 0, 1, 0, '\n1.操作轻柔快捷，避免老年人受凉。\n2.协助老年人取坐位时注意安全。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜。\n(2)护理员准备。护理员衣着整齐，洗净双手。\n(3)老年人准备。老年人平卧于床上。\n(4)物品准备。老年人清洁套头上衣。\n\n步骤2 沟通\n护理员为老年人选择合适的套头上衣，向老年人解释，以取得配合。协助老年人取坐位。\n\n步骤3 脱下套头衫\n护理员将老年人套头上衣的下端向上拉至胸部，一手托起老年人头部，一手从背后向前脱下衣身部分。一手扶住老年人肩部，一手拉住近侧袖口，脱下一侧衣袖，同法脱下另一侧衣袖。\n\n步骤4 穿上套头衫\n(1)辨别套头衫前后面，护理员一手从衣袖口处伸入至衣身开口处，握住老年人手腕，将衣袖套人老年人手臂，同法穿好另一侧。\n(2)一手托起老年人头部，一手握住衣身背部的下开口至领口部分，套入老年人头部\n(3)护理员将老年人套头衫衣身向下拉平，整理衣服至平整。\n\n步骤5 整理床铺\n协助老年人取舒适卧位，确保身下衣服平整无皱褶，盖好盖被，整理床铺。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a347e727-ef1c-11ed-b910-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33fb9ef-ef1c-11ed-844a-8c554aa628fd', '2', '睡眠照料', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3480e35-ef1c-11ed-98f2-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a347e727-ef1c-11ed-b910-8c554aa628fd', '3', '指导老年人改变不良睡眠习惯', 1, 1, 0, '\n1.护理员与老年人沟通时要主动耐心，认真听取老年人的诉说。\n2.护理员要调动老年人的积极性，使老年人能主动配合，共同参与。\n3.护理员应随时了解老年人不良睡眠习惯改变的情况，循序渐进，不能急于求成。', '1 准备工作\n护理员应认真与老年人沟通，详细了解老年人睡眠的习惯、老年人性格特点等。\n\n步骤2 确定问题\n根据沟通的情况和老年人的表现确定存在的睡眠不良习惯。\n\n步骤3 帮助指导\n护理员先和老年人确认存在的睡眠不良习惯，得到老年人的认同和配合后，共同讨论采用适合方法帮助指导老年人改变不良习惯。\n\n步骤4 改进\n护理员及时与老年人沟通，了解老年人不良睡眠习惯的改善情况及方法是否得当。要鼓励有改进的老年人坚持不懈，对效果不明显的老年人要再次讨论其改进方法。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3485c5f-ef1c-11ed-aaa4-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a347e727-ef1c-11ed-b910-8c554aa628fd', '3', '照料有睡眠障碍的老年人入睡', 1, 1, 0, '\n1.护理员与老年人沟通时要主动、耐心，认真听取老年人的诉说\n2.采取的措施应适合老年人的特点，切实可行。\n', '1 准备工作\n护理员应认真与老年人沟通，详细了解老年人睡眠障碍的表现、睡眠习惯等。\n\n步骤2 确定问题\n根据老年人的表现确定其睡眠障碍的类型，并明确引起睡眠障碍的原因。\n\n步骤3 采取措施\n根据原因和老年人的身体状况选用不同的适合老年人睡眠障碍的照料方法。\n\n步骤4 改进\n护理员与老年人沟通，了解老年人睡眠的改善情况及采取措施的有效性，提出改进措施。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a3488367-ef1c-11ed-afa2-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a347e727-ef1c-11ed-b910-8c554aa628fd', '3', '识别影响老年人睡眠的环境因素及改善睡眠环境建议', 1, 1, 0, '\n1.护理员与老年人沟通时态度诚恳、认真，多使用开放式的询问方式。\n2.认真倾听主诉，观察老年人居室环境是否存在影响睡眠因素。\n3.护理员提出的改进建议应尊重老年人的生活习惯，并结合老年人的特点，切实可行。\n', '1 准备工作\n(1)护理员服装整洁，并事先查阅照料记录，了解老年人近期睡眠状况。\n(2)物品准备包括：笔1支，记录单。\n\n步骤2 沟通\n护理员应与老年人沟通，详细了解老年人以往睡眠习惯、睡眠环境。\n\n步骤3 评估居室环境\n护理员认真观察现在老年人的居室环境，与老年人习惯的环境此较分析，找出影响老年人睡眠的环境因素。\n\n步骤4 提出改进建议\n护理员根据收集到的影响老年人睡眠的因素为老年人提出改进建议，注意观察改进措施的有效性。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a348c2aa-ef1c-11ed-8fb1-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a347e727-ef1c-11ed-b910-8c554aa628fd', '3', '为老年人布置睡眠环境', 0, 1, 0, '\n1.老年人睡前，卧室适当通风换气，避免空气浑浊或异味影响睡眠。\n2.被褥薄厚随季节调整\n3.枕头不宜太高或太低，软硬度适中。', '1 工作准备\n(1)环境准备。室内安静整洁。\n(2)护理员准备。服装整洁。\n(3)老年人准备。排便、洗漱完毕。\n(4)物品准备。根据气候备棉被、床褥、毛毯等。\n\n步骤2 沟通\n护理员轻敲房门后进入房间，告知老年人准备熄灯休息。询问老年人房间温湿度是否合适，有无需要帮助的地方。\n\n步骤3 布置睡眠环境\n(1)护理员协助关闭窗户，闭合窗帘。\n(2)调节室内空调或暖气开关，调整温湿度。\n(3)检查老年人床铺有无渣屑，按压床铺硬度。展开被褥平整铺床，被褥松软适中。整理枕头至蓬松，高度随老年人习惯适当调整。\n(4)协助老年人上床就寝，盖好盖被。询问是否还有需求，及时满足。\n(5)调节光线，开启地灯，关闭大灯。\n\n步骤4 关门退出\n护理员轻步退出房间，轻手关门。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a348f2c3-ef1c-11ed-b6f2-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a347e727-ef1c-11ed-b910-8c554aa628fd', '3', '观察并记录老年人异常睡眠', 0, 1, 0, '\n1.夜间查房注意走路轻，关门轻，避免惊醒老年人。\n2.记录内容详细，字迹清楚。', '1 工作准备\n(1)护理员准备。服装整洁，查阅既往照料记录，了解老年人近期状况\n(2)环境准备。居室整洁。\n(3)老年人准备。老年人平卧床上。\n(4)物品准备。记录单、笔。必要时备被子、褥子、毛毯等。\n\n步骤2 协助入睡\n护理员为老年人布置舒适的睡眠环境，协助老年人入睡。\n\n步骤3 观察睡眠\n护理员夜间2小时查房一次。做到走路轻，关门轻。观察老年人睡眠状况，夜间11时查房老人仍未进人睡眠状态，整夜觉醒4次。夜间温度下降，老年人觉醒时，为老年人增盖薄被。\n\n步骤4 沟通。\n晨起巡视并询问睡眠情况。\n\n步骤5 记录\n交班本上记录主诉内容。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34921c1-ef1c-11ed-94c0-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a33fb9ef-ef1c-11ed-844a-8c554aa628fd', '2', '饮食照料', 0, 0, 0, NULL, NULL);
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a349492f-ef1c-11ed-aaff-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '对噎食、误吸的老年人采取应急救助措施，报告并记录', 1, 1, 0, '\n1.老年人发生噎食、误吸时应及时采用正确的急救方法就地抢救，分秒必争。\n2.进行抢救时动作用力适度，以免造成肋骨骨折或内脏损伤。\n\n', '1 判断\n根据老年人的表现，护理员快速判断老年人可能出现的问题，同时大声呼喊他人帮助。\n\n步骤2 取出口腔中异物\n护理员用手或汤匙迅速抠出口咽中的积聚食物团（或异物）。如老年人牙关紧闭或抽搐，可用筷子等撬开口腔掏取食物团（或异物），并解开老年人领口。\n\n步骤3 实施救助\n护理员协助意识清醒的老年人采取立位或坐位，对意识不清或卧床的老年人采取平卧位，头偏向一侧。迅速采取腹部手拳冲击法进行救助。救助时应严密观察老年人的情沉，必要时及时协助老年人就医。\n\n特别提示 噎食、误吸急救方法的选择应因人而异、因地制宜。但都应以抢救老年人生命为准则\n\n步骤4 记录\n护理员应及时记录老年人噎食、误吸发生的时间、表现和采取的急救措施，报告主管部门。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a349703e-ef1c-11ed-bd54-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '带鼻饲管老年人的进食照料', 1, 1, 0, '\n1.对长期鼻饲的老年人，每日晨、晚间应做口腔护理，保持口腔清洁。\n2.对需要吸痰的老年人，应在鼻饲前30分钟给予吸痰；鼻饲前、后30分钟内禁止吸痰，避免引起老年人胃液或食物返流及误吸。\n3.鼻饲老年人遵医嘱需要服用口服药物时，为防止鼻饲管堵塞，如片剂应研碎、溶解后再灌注。\n4.随时观察老年人鼻饲管固定处皮肤的情况，发现异常时应及时通知医护人员处理。\n5.老年人鼻饲过程中，若出现恶心、呕吐等情况，应立即停止鼻饲，并立即通知医护人员。\n6.在鼻饲喂食前，护理员抽吸胃液，若发现胃液呈深棕色或感觉异常，应立即通知医护人员。', '1 准备工作管\n进行鼻饲前，应进行以下准备工作。\n(1)保证室内环境清洁。\n(2)护理员衣着整洁，并洗净双手。\n(3)检查灌注器的完好情况，并准备毛巾、水杯盛装100毫升温水和记录单。\n(4)护理员根据饮食单准备鼻饲饮食，核对床号、姓名、鼻饲饮食种类及量。\n\n步骤2 沟通\n(1)护理员带着准备好的鼻饲饮食用物到老年人房间。\n(2)对于能够有效沟通的老年人，护理员应询问老年人床号、姓名，并向老人讲解即将进食鼻饲的饮食种类和量，以取得老年人的配合；对于不能进行有效沟通的老年人，护理员应核对老年人的房间号、床号、床头卡姓名、鼻饲饮食种类和量。\n\n步骤3 摆放体位\n根据老年人身体情况，协助其摆放舒适的体位。\n(1)对于上半身功能较好的老年人，护理员应协助老年人采用坐位或半坐位：对于平卧的老年人，护理员应将床头摇高或使用软枕垫起，使之与床水平线呈30度角。\n(2)在老年人的颌下垫毛巾或治疗巾。\n\n特别提示为老年人进行鼻饲饮食时不能采用站立位。\n\n步骤4 检查鼻饲管 \n为确保老年人鼻饲饮食安全，每次鼻饲饮食前必须进行以下检查。\n(1)检查鼻饲管。护理员首先应检查鼻饲管固定是否完好，插入的长度是否与鼻饲管标记的长度一致，如发现有管路滑脱，应立即通知医护人员处理。\n(2)检查鼻饲管是否在胃内。护理员打开胃管末端盖帽，将灌注器的乳头与胃管末端连接并进行抽吸，有胃液或胃内容物被抽出，表明鼻饲管在胃内（常用方法)。推回胃液或胃内容物，盖好鼻饲管末端盖帽。\n\n步骤5 进行鼻饲\n(1)测试鼻饲饮食的温度(38~40℃)，护理员应将鼻饲饮食少量滴在自己的掌侧腕部，以感觉温热、不烫手为宜。\n(2)护理员用灌注器从水杯中抽取20毫升温开水，连接鼻饲管向老年人胃内缓慢灌注，再盖好鼻饲管末端盖帽，以确定鼻饲管通畅，并同时可以使鼻饲管管腔润滑、刺激胃液分泌。\n(3)护理员抽吸鼻饲饮食（每次50毫升/管），在水杯中轻沾灌注器乳头部分，涮下外壁鼻饲饮食残渣滓，打开鼻饲管盖帽并连接，缓慢推注，速度为10一13毫升/分钟。灌注后立即盖好胃管盖帽，再次抽吸鼻饲饮食，同法至鼻饲饮食全部推注完毕。\n(4)每次鼻饲量不应超过200毫升，推注时间以15一20分钟为宜，两次鼻饲之间间隔不少于2小时。\n(5)鼻饲饮食灌注完毕，护理员用灌注器抽取30~50毫升温开水缓慢注人，冲净鼻饲管内壁食物残渣。防止食物残渣堵塞鼻饲管。盖好鼻饲管盖帽。保持鼻饲时体位30分钟后，恢复平卧位。\n(6)叮嘱并协助老年人进食后保持体位30分钟再卧床休息。这样有利于食物的消化与吸收，以防喂食后食物反流引发误吸。\n\n特别提示对意识不清、躁动的老年人可适当使用约束用具，避免其因不适，自己将胃管拔出。\n\n步骤6 整理用物并记录\n(1)撤下毛巾，整理床单位。\n(2)清洗用物。将灌注器在流动水下清洗干净，用开水浸泡消毒后放人碗内，上面覆盖纱布备用。灌注器更换频率为1次/周。预防消化道疾病发生。\n(3)准确记录鼻饲时间和鼻饲量。重点双察老年人鼻饲后有无酸胀、腹泻等适症状并记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a349b61e-ef1c-11ed-8961-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '协助老年人进食', 0, 1, 0, '\n1.食物温度适宜。食物温度太高，则会发生烫伤；温度太低，则会引起胃部不适。\n2.老年人进餐后不宜立即平卧，以防止食物反流。\n3.对于咀嚼或吞咽困难的老年人，可将食物打碎成糊状，再协助进食。\n4.老年人进食中如发生呛咳、噎食等现象，立即急救处理并通知医护人员或家属。', '1 工作准备\n(1) 环境准各，环境整清，温湿度适宜，无异味\n(2) 护理员准备。服装整洁，洗净双手。\n(3) 老年人准备\n  1)询间老年人进食前是否需要大小便，根据需要协助排便。\n  2)协助老年人洗净双手。\n  3)协助老年人戴上义齿。\n  4)协助老年人服用餐前口服药。\n(4) 物品准备。餐具（碗、筷、汤匙）、食物、围裙或毛巾，手帕或纸巾，桌、清洁口腔用物。\n步骤2 沟通\n向老年人说明进食时间和本次进餐食物，询问有无特殊要求。\n\n步骤3 摆放体位\n护理员根据老年人自理程度及病情采取适宜的进食体位（如轮椅坐位、床上坐位、半坐位、侧卧位等，参见本节学习单元1的相关内容)。为老年人带上围裙或将毛巾垫在老年人颌下及胸前部位。\n\n步骤4 协助进餐\n护理员将已准备好的食物盛入老年人的餐具中并摆放在餐桌上。\n(1)鼓励能够自己进餐的老年人自行进餐。指导老年人上身坐直并稍向前倾头稍向下垂，叮嘱老年人进餐时细嚼慢咽，不要边进食边讲话，以免发生呛咳。\n(2)对于不能自行进餐的老年人，由护理员喂饭。护理员用手触及碗壁感受并估计食物温热程度，以汤匙喂食时，每喂食一口，食物量为汤匙的1/3为宜，等看到老年人完全咽下后再喂食下一口。\n(3)对于视力障碍能自己进食的老年人，护理员将盛装温热食物餐碗放入老年人的手中（确认食物的位置），再将汤匙递到老年人手中，告知食物的种类，叮嘱老年人缓慢进食。进食带有骨头的食物，护理员要特别告知小心进食，进食鱼类要先协助剔除鱼刺。\n\n步骤5 整理\n护理员协助老年人进餐后漱口，并用毛巾擦干口角水痕。叮嘱老年人进餐后不能立即平卧，保持进餐体位30分钟后再卧床休息。清扫整理床单位。\n使用流动水清洁餐具并放回原处备用，必要时进行消毒。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a349e947-ef1c-11ed-9679-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '协助老年人饮水', 0, 1, 0, '\n1.开水晾温后再递交到老年人手中或进行喂水，防止发生烫伤。\n2.老年人饮水后不能立即平卧，防止反流发生呛咳、误吸\n3.对不能自理的老年人每日分次定时喂水。', '1 工作准备\n(1)环境淮备。环境整洁，温湿度适宜，无异味。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。协助老年人取坐位或半卧位，洗净双手。\n(4)物品准备。茶杯或小水壶盛装1/2一2/3满的温开水（触及杯壁时温热不烫手)，准备吸管、汤匙及小毛巾。\n\n步骤2 沟通\n提醒老年人饮水并询问有无特殊要求。\n\n步骤3 协助饮水\n(1)鼓励能够自己饮水的老年人手持水杯或借助吸管饮水，叮嘱老年人饮水时身体坐直或稍前倾，小口饮用，以免呛咳。出现呛咳，应稍事休息再饮用。\n(2)护理员给不能自理的老年人喂水时可借助吸管饮水；使用汤匙喂水时，水盛装汤匙的1/2~23为宜，见老年人下咽后再喂下一口。\n\n步骤4 整理用物\n将水杯或水壶放回原处。护理员用小毛巾擦干老年人口角水痕。整理床单位。叮嘱老年人保持体位30分钟后再躺下休息。必要时，根据老年人病情需要，记果饮水次数和饮水量。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34a1d1d-ef1c-11ed-93f5-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '发放治疗饮食', 0, 1, 0, '\n1.认真核对饮食单及转抄的发饭单。\n2.发饭时，认真核对床号、姓名及治疗饮食种类及量，避免发放错误，影响饮食治疗效果。\n3.流质治疗饮食可使用瓶装或袋装塑封，防止污染。\n4.奶类食物宜温热食人，注意保温。\n', '1 工作准备\n(1)环境准备，环境整情，温湿度适宜，无异珠\n(2)护理员准备，服装整洁，洗净双手，戴口罩，帽子\n(3)物品准备，糖尿病食物、饮食单、发饭单，笔。\n\n步骤2 转抄核对\n接到老年人发饭治疗饮食单，转抄饮食单内容至发饭单上，并认真核对无误。饮食单内容包括科区、房间及床号、姓名，饮食种类及量，将饮食单递交到营养料(膳食科)准备食物。\n\n步骤3 领取食物\n按照发饭时间，从膳食科领取老年人的治疗饮食，并认真核对科区姓名、饮食种类及量。\n\n步骤4 核对发放饮食\n护理员推餐车到老年人房间门前，报床号、姓名，并再次核对。得到肯定应答后，拿取老人餐具盛接食物，摆放在老年人餐桌上。\n\n步骤5 记录\n在发放饮食单上打钩记录。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34a4491-ef1c-11ed-826d-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '摆放进食体位', 0, 1, 0, '\n1.护理员协助老年人摆放体位前应做好评估。\n2.摆放体位时动作轻稳，保障安全。\n3.辅助器具使用前，检查其是否处于安全完好的备用状态。', '1 工作准备\n(1)环境准备。环境整洁，温湿度适宜，无异味。\n(2)护理员准备。服装整洁，洗净双手。\n(3)老年人准备。询问老年人进食前是否需要大小便，根据需要协助排便，协助老年人洗净双手。\n(4)物品准备。根据需要准备轮椅或床上支具（靠垫、枕头、床具支架等）。\n步骤2 沟通\n向老年人说明进食时间和本次进餐食物，询问有无特殊要求。\n步骤3 摆放体位\n护理员根据老年人自理程度及病情采取适宜的进食体位。\n(1)轮椅坐位（适用于下肢功能障碍或行走无力的老年人）\n轮椅与床呈30度夹角，固定轮子，拾起脚踏板。护理员叮嘱老年人将双手环抱护理员脖颈，双手环抱老年人的腰部或腋下，协助老年人坐起，双腿垂于床下，双脚踏稳地面，再用膝部抵住老年人的膝部，挺身带动老年人站立并旋转身体，使老年人坐在轮椅中间，后背贴紧椅背，将轮椅上的安全带系在老年人腰间。\n\n(2)床上坐位（适用于下肢功能障得或行走无力的老年人）\n护理员按上述环抱方法协助老年人在床上坐起，将靠垫或软枕垫于老年人后背及膝下，保证坐位稳定舒适。床上放置餐桌。\n(3)半卧位（适用于完全不能自理的老年人）\n使用可摇式床具时，护理员将老年人床头摇起，抬高至与床具水平面星30~45度角。使用普通床具时，可使用棉被或靠垫支撑老年人背部使其上身拾起。采用半卧位时，应在身体两侧及膝下垫软枕以保证体位稳定。\n(4)侧卧位（适用于完全不能自理的老年人）\n使用可摇式床具时，护理员将老年人床头摇起，抬高至与床具水平面呈30度角。护理员双手分别扶住老年人的肩部和髋部，使老年人面向护理员侧卧，肩背部垫软枕或楔形垫。一般宜采用右侧卧位。\n\n步骤4 准备进餐\n护理员为老年人穿戴好围裙或在颌下及胸前垫好毛巾准备进餐。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34a85e3-ef1c-11ed-b596-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '老年人进食、水种类和量的观察与记录', 0, 1, 0, '\n1.预先了解老年人饮食习惯，便于对比，发现异常情况。\n2.记录应详细、准确，有利于准确判断老年人身体状况。\n', '1 工作准备\n护理员服装整洁，携带记录单、笔。\n步骤2 沟通\n询问了解老年人以往进食、饮水的习惯、种类及量，本次进食、饮水情况。对于听力有障碍的老年人，护理员可采用提示性语言或使用写字进行交流。\n步骤3 观察进食、饮水情况\n(1)老年人进食、饮水体位，需要辅助程度。\n(2)老年人进食、饮水的种类、进食速度、进食量以及近期有无明显饮食量、饮食习惯改变等。\n(3)进食、饮水过程中有无吞咽困难、噎食、误吸、呛咳、呕吐等现象。\n步骤4 记录\n记录所观察内容，并标明日期、时间，签全名。每月小结，从中发现问题及时告知医护人员或家属。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34ab5f1-ef1c-11ed-9f8c-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '识别老年人进食、进水困难的基本原因', 1, 1, 1, ' 识别老年人进食、进水困难的基本原因\n\n步骤1 工作准备\n(1)护理员准备。护理员着装整洁，收集资料，了解老人的基本状况\n(2)环境准备。使谈话环境整洁、无异味。\n(3)物品准备。准备1支笔、1份记录单。\n(4)安置老年人。协助老年人在居室内取舒适体位。\n\n步骤2 观察表现\n观察老人进食、饮水时速度慢，无呛咳，吞咽有停顿。\n\n步骤3 询问主诉\n\n步骤4 判断原因\n根据老人的表现，无咀嚼困难及饮水呛咳，有吞咽苦难，吞咽不顺畅，主诉咽东西时胸口部有异物感，可知老人有食道疾病的可能。\n\n步骤5 记录并报告\n(1)记录下老人的表现及主诉\n(2)报告家属或医护人员。\n(3)及时就医，以做进一步检查、确诊。\n', '1 工作准备\n(1)护理员准备。护理员着装整洁，收集资料，了解老人的基本状况\n(2)环境准备。使谈话环境整洁、无异味。\n(3)物品准备。准备1支笔、1份记录单。\n(4)安置老年人。协助老年人在居室内取舒适体位。\n\n步骤2 观察表现\n观察老人进食、饮水时速度慢，无呛咳，吞咽有停顿。\n\n步骤3 询问主诉\n\n步骤4 判断原因\n根据老人的表现，无咀嚼困难及饮水呛咳，有吞咽苦难，吞咽不顺畅，主诉咽东西时胸口部有异物感，可知老人有食道疾病的可能。\n\n步骤5 记录并报告\n(1)记录下老人的表现及主诉\n(2)报告家属或医护人员。\n(3)及时就医，以做进一步检查、确诊。');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34ae88d-ef1c-11ed-971b-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '对老年人不良饮食习惯进行健康指导并提出饮食改善建议', 1, 1, 1, '\n1.尊重老年人,仅作为建议,不可强迫;\n2.语言要温和,多用询问语句.尽量多讲改善饮食习惯的好处,让老年人觉得是为自己的健康着想,以便取得老年人的理解和配合.', '1 工作准备\n(1)护理员准备。护理员着装整洁，收集资料，了解老人的基本状况。\n(2)环境准备。谈话环境整洁，通风良好，光线充足。\n(3)物品准备。准备1支笔、1份记录单。\n\n步骤2 评估\n护理员与老人面对面坐好，进行沟通交流，了解不良习惯的状况。\n\n步骤3 分析判断\n运用知识分析判断老年人不良饮食习惯的原因及会带来哪些不良后果，对老年人不良饮食习惯进行健康指导。\n\n步骤4 提出建议\n对老年人不良饮食习惯提出改善建议\n\n步骤5 记录并报告\n(1)记录老年人不良饮食习惯 原因,改善建议及老年人是否接受建议\n(2)报告家属或医护人员\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a34b1caf-ef1c-11ed-a89d-8c554aa628fd', NULL, 20, NULL, '2023-05-10 18:22:45', NULL, '2023-05-10 18:22:45', 'a34921c1-ef1c-11ed-94c0-8c554aa628fd', '3', '检查老年人治疗饮食的的落实情况', 1, 1, 1, '\n1.语言亲切，态度和葛。使用开放式的问询方式。\n2.建议老年人严格执行治疗饮食规范，同时建议改善治疗饮食的口味。\n3.做好相关记录并及时报告。\n', '1 工作准备\n(1)护理员准备。护理员着装整洁，收集资料，了解老人的基本状况。\n(2)物品准备。准备1支笔、1份记录单\n(3)环境准备。居室环境整洁，光线充足、柔和。\n\n步骤2 沟通交流\n护理员与老人面对面坐好，进行沟通、交流，了解身体、心理情况。\n\n步骤3 询问饮食情况\n询问老年人是否按时、按要求食用治疗饮食。\n\n步骤4 分析判断\n分析老年人食用治疗饮食的时间和内容，与医嘱规定有何差异。分析老人情况\n\n步骤5 询问效果\n询问老年人，分析老年人主诉，检查老年人食用治疗饮食后的效果。\n\n步骤6 记录并报告\n(1)记录下老年人治疗饮食的食用时间、内容，以及老年人的身体状况。\n(2)报告家属或医护人员。\n\n');
INSERT INTO `service_scou_info`(`id`, `remark`, `status`, `creater`, `create_time`, `updater`, `update_time`, `pid`, `level`, `scou_name`, `caregiver_intermediate`, `caregiver_junior`, `caregiver_senior`, `attention`, `operation`) VALUES ('a910be69-f476-11ee-b618-5405db7a49bd', NULL, 20, NULL, '2024-04-07 08:28:18', NULL, '2024-04-07 08:28:18', 'a32ec9ba-ef1c-11ed-a13e-8c554aa628fd', '3', '发广告会发光', 0, 1, 0, NULL, NULL);
